import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const Employees = () => {
  const [employees, setEmployees] = useState([]);
  const [divisions, setDivisions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    birth_date: '',
    address: '',
    join_date: '',
    education_level: 'S1',
    major: '',
    last_education_place: '',
    designation: '',
    role: '',
    division_id: ''
  });
  const [submitting, setSubmitting] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  
  const { user } = useAuth();

  useEffect(() => {
    fetchEmployees();
    fetchDivisions();
  }, []);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/employees.php');
      
      if (response.data.success) {
        setEmployees(response.data.data);
      } else {
        setError('Gagal memuat data karyawan');
      }
    } catch (error) {
      setError('Terjadi kesalahan saat memuat data');
      console.error('Employees error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchDivisions = async () => {
    try {
      // This would need a divisions API endpoint
      setDivisions([
        { division_id: 1, division_name: 'IT' },
        { division_id: 2, division_name: 'HR' },
        { division_id: 3, division_name: 'Finance' },
        { division_id: 4, division_name: 'Marketing' },
        { division_id: 5, division_name: 'Operations' }
      ]);
    } catch (error) {
      console.error('Divisions error:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const response = await axios.post('/employees.php', {
        action: 'add',
        ...formData
      });

      if (response.data.success) {
        setShowModal(false);
        setFormData({
          name: '',
          email: '',
          phone: '',
          birth_date: '',
          address: '',
          join_date: '',
          education_level: 'S1',
          major: '',
          last_education_place: '',
          designation: '',
          role: '',
          division_id: ''
        });
        fetchEmployees();
        alert('Karyawan berhasil ditambahkan');
      } else {
        alert(response.data.error || 'Gagal menambahkan karyawan');
      }
    } catch (error) {
      alert('Terjadi kesalahan saat menambahkan karyawan');
      console.error('Add employee error:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const getContractBadge = (contractType) => {
    if (!contractType) return <span className="badge bg-secondary">Tidak ada</span>;
    
    const badges = {
      'probation': <span className="badge bg-warning">Probation</span>,
      '1': <span className="badge bg-primary">Kontrak 1</span>,
      '2': <span className="badge bg-success">Kontrak 2</span>,
      '3': <span className="badge bg-info">Kontrak 3</span>,
      'permanent': <span className="badge bg-dark">Permanent</span>
    };
    
    return badges[contractType] || <span className="badge bg-secondary">{contractType}</span>;
  };

  const getStatusBadge = (status) => {
    const badges = {
      'active': <span className="badge bg-success">Aktif</span>,
      'resigned': <span className="badge bg-danger">Resign</span>,
      'terminated': <span className="badge bg-dark">Terminasi</span>
    };
    
    return badges[status] || <span className="badge bg-secondary">{status}</span>;
  };

  const filteredEmployees = employees.filter(employee => {
    const matchesSearch = employee.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         employee.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         employee.role.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesStatus = filterStatus === 'all' || employee.status === filterStatus;
    
    return matchesSearch && matchesStatus;
  });

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
          <div className="text-center">
            <div className="spinner-border text-primary mb-3" style={{ width: '3rem', height: '3rem' }} role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="text-muted">Memuat data karyawan...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Header */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <h2 className="mb-1">
                <i className="fas fa-users me-2 text-primary"></i>
                Karyawan
              </h2>
              <p className="text-muted mb-0">Kelola data karyawan dan lihat rekomendasi kontrak</p>
            </div>
            {(user.role === 'hr' || user.role === 'admin') && (
              <button
                className="btn btn-primary"
                onClick={() => setShowModal(true)}
              >
                <i className="fas fa-plus me-2"></i>
                Tambah Karyawan
              </button>
            )}
          </div>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          <i className="fas fa-exclamation-triangle me-2"></i>
          {error}
        </div>
      )}

      {/* Filters */}
      <div className="row mb-4">
        <div className="col-md-6">
          <div className="input-group">
            <span className="input-group-text">
              <i className="fas fa-search"></i>
            </span>
            <input
              type="text"
              className="form-control"
              placeholder="Cari karyawan..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
        <div className="col-md-3">
          <select
            className="form-select"
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
          >
            <option value="all">Semua Status</option>
            <option value="active">Aktif</option>
            <option value="resigned">Resign</option>
            <option value="terminated">Terminasi</option>
          </select>
        </div>
        <div className="col-md-3">
          <div className="text-muted">
            Total: {filteredEmployees.length} karyawan
          </div>
        </div>
      </div>

      {/* Employee Cards */}
      <div className="row">
        {filteredEmployees.length === 0 ? (
          <div className="col-12">
            <div className="text-center py-5">
              <i className="fas fa-users fa-3x text-muted mb-3"></i>
              <h5 className="text-muted">Tidak ada data karyawan</h5>
              <p className="text-muted">
                {searchTerm || filterStatus !== 'all' 
                  ? 'Tidak ada karyawan yang sesuai dengan filter'
                  : 'Belum ada karyawan yang terdaftar'
                }
              </p>
            </div>
          </div>
        ) : (
          filteredEmployees.map((employee) => (
            <div key={employee.eid} className="col-lg-6 col-xl-4 mb-4">
              <div className="employee-card">
                <div className="d-flex align-items-start">
                  <div className="employee-avatar">
                    {employee.name.charAt(0).toUpperCase()}
                  </div>
                  <div className="employee-info flex-grow-1">
                    <h5>{employee.name}</h5>
                    <p className="mb-1">
                      <i className="fas fa-briefcase me-1"></i>
                      {employee.role}
                    </p>
                    <p className="mb-1">
                      <i className="fas fa-building me-1"></i>
                      {employee.division_name || 'Tidak ada divisi'}
                    </p>
                    <p className="mb-2">
                      <i className="fas fa-envelope me-1"></i>
                      {employee.email}
                    </p>
                    
                    <div className="employee-meta">
                      <div className="meta-item">
                        <strong>Status:</strong> {getStatusBadge(employee.status)}
                      </div>
                      <div className="meta-item">
                        <strong>Kontrak:</strong> {getContractBadge(employee.current_contract_type)}
                      </div>
                    </div>
                    
                    <div className="employee-meta mt-2">
                      <div className="meta-item">
                        <strong>Pendidikan:</strong> {employee.education_level}
                      </div>
                      <div className="meta-item">
                        <strong>Jurusan:</strong> {employee.major}
                      </div>
                    </div>

                    <div className="mt-3 d-flex gap-2">
                      <Link
                        to={`/employees/${employee.eid}`}
                        className="btn btn-outline-primary btn-sm"
                      >
                        <i className="fas fa-eye me-1"></i>
                        Detail
                      </Link>
                      
                      {user.role === 'manager' && (
                        <button className="btn btn-outline-success btn-sm">
                          <i className="fas fa-thumbs-up me-1"></i>
                          Rekomendasikan
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Add Employee Modal */}
      {showModal && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  <i className="fas fa-user-plus me-2"></i>
                  Tambah Karyawan Baru
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Nama Lengkap *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Email *</label>
                      <input
                        type="email"
                        className="form-control"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>

                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">No. HP</label>
                      <input
                        type="text"
                        className="form-control"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Tanggal Lahir</label>
                      <input
                        type="date"
                        className="form-control"
                        name="birth_date"
                        value={formData.birth_date}
                        onChange={handleChange}
                      />
                    </div>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Alamat</label>
                    <textarea
                      className="form-control"
                      name="address"
                      value={formData.address}
                      onChange={handleChange}
                      rows="2"
                    ></textarea>
                  </div>

                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Tanggal Bergabung *</label>
                      <input
                        type="date"
                        className="form-control"
                        name="join_date"
                        value={formData.join_date}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Tingkat Pendidikan *</label>
                      <select
                        className="form-select"
                        name="education_level"
                        value={formData.education_level}
                        onChange={handleChange}
                        required
                      >
                        <option value="D3">D3</option>
                        <option value="S1">S1</option>
                        <option value="S2">S2</option>
                        <option value="S3">S3</option>
                      </select>
                    </div>
                  </div>

                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Jurusan *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="major"
                        value={formData.major}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Institusi Pendidikan</label>
                      <input
                        type="text"
                        className="form-control"
                        name="last_education_place"
                        value={formData.last_education_place}
                        onChange={handleChange}
                      />
                    </div>
                  </div>

                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Role/Posisi *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="role"
                        value={formData.role}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Divisi *</label>
                      <select
                        className="form-select"
                        name="division_id"
                        value={formData.division_id}
                        onChange={handleChange}
                        required
                      >
                        <option value="">Pilih Divisi</option>
                        {divisions.map(division => (
                          <option key={division.division_id} value={division.division_id}>
                            {division.division_name}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Designation</label>
                    <input
                      type="text"
                      className="form-control"
                      name="designation"
                      value={formData.designation}
                      onChange={handleChange}
                      placeholder="e.g., Senior Developer, Manager, etc."
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowModal(false)}
                    disabled={submitting}
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={submitting}
                  >
                    {submitting ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Menyimpan...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-save me-2"></i>
                        Simpan
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Employees;
