import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const EmployeeDetail = () => {
    const { eid } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [employee, setEmployee] = useState(null);
    const [contracts, setContracts] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [activePanel, setActivePanel] = useState('recommendation');
    const [resignReason, setResignReason] = useState('');
    const [resignDate, setResignDate] = useState('');
    const [showResignForm, setShowResignForm] = useState(false);

    useEffect(() => {
        fetchEmployeeData();
    }, [eid]);

    const fetchEmployeeData = async () => {
        try {
            setLoading(true);
            const response = await axios.get(`/employees.php?action=detail&eid=${eid}`, {
                withCredentials: true
            });

            if (response.data.success) {
                setEmployee(response.data.employee);
                setContracts(response.data.contracts || []);
                setRecommendations(response.data.recommendations || []);
            } else {
                console.error('Error fetching employee data:', response.data.error);
            }
        } catch (error) {
            console.error('Error fetching employee data:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleProbation = async () => {
        try {
            const response = await axios.put('/employees.php', {
                eid: employee.eid,
                status: 'Probation'
            }, {
                withCredentials: true
            });

            if (response.data.success) {
                alert('Status karyawan berhasil diubah ke Probation');
                fetchEmployeeData();
            } else {
                alert(response.data.message || 'Gagal mengubah status karyawan');
            }
        } catch (error) {
            console.error('Error updating employee status:', error);
            alert('Terjadi kesalahan saat mengubah status karyawan');
        }
    };

    const handleAktif = () => {
        // Navigate to contract recommendation page
        navigate(`/contract-recommendation/${employee.eid}`);
    };

    const handleResign = async () => {
        if (!resignReason.trim()) {
            alert('Mohon isi alasan resign');
            return;
        }

        if (!resignDate.trim()) {
            alert('Mohon pilih tanggal resign');
            return;
        }

        try {
            const response = await axios.put('/employees.php', {
                eid: employee.eid,
                status: 'Inactive',
                resign_reason: resignReason,
                resign_date: resignDate
            }, {
                withCredentials: true
            });

            if (response.data.success) {
                alert('Karyawan berhasil resign');
                fetchEmployeeData();
                setShowResignForm(false);
                setResignReason('');
                setResignDate('');
            } else {
                alert(response.data.message || 'Gagal mengubah status karyawan');
            }
        } catch (error) {
            console.error('Error updating employee status:', error);
            alert('Terjadi kesalahan saat mengubah status karyawan');
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'N/A';
        return new Date(dateString).toLocaleDateString('id-ID');
    };

    const getContractTypeColor = (type) => {
        switch (type) {
            case 'Probation': return 'warning';
            case 'Kontrak 1': return 'primary';
            case 'Kontrak 2': return 'info';
            case 'Kontrak 3': return 'secondary';
            case 'Permanent': return 'success';
            default: return 'secondary';
        }
    };

    const getStatusBadge = (status) => {
        switch (status) {
            case 'active':
                return 'status-active';
            case 'probation':
                return 'status-probation';
            case 'resigned':
            case 'terminated':
                return 'status-inactive';
            default:
                return 'status-inactive';
        }
    };

    const getStatusText = (status) => {
        switch (status) {
            case 'active':
                return 'Aktif';
            case 'probation':
                return 'Probation';
            case 'resigned':
                return 'Resign';
            case 'terminated':
                return 'Terminated';
            default:
                return 'Tidak Aktif';
        }
    };

    const getActualContractStatus = (contract) => {
        if (!contract.end_date) {
            return 'active'; // Permanent contract
        }

        const today = new Date();
        const endDate = new Date(contract.end_date);

        if (endDate < today) {
            return 'expired'; // Contract has ended
        } else if (contract.status === 'active') {
            return 'active'; // Still active
        } else {
            return contract.status; // Use database status
        }
    };

    const getContractStatusText = (actualStatus) => {
        switch (actualStatus) {
            case 'active':
                return 'Aktif';
            case 'expired':
                return 'Berakhir';
            case 'completed':
                return 'Selesai';
            case 'terminated':
                return 'Dibatalkan';
            default:
                return actualStatus;
        }
    };

    const getContractStatusColor = (actualStatus) => {
        switch (actualStatus) {
            case 'active':
                return 'success';
            case 'expired':
                return 'danger';
            case 'completed':
                return 'secondary';
            case 'terminated':
                return 'warning';
            default:
                return 'secondary';
        }
    };

    if (loading) {
        return (
            <div className="loading-spinner">
                <div className="spinner"></div>
            </div>
        );
    }

    if (!employee) {
        return (
            <div className="main-container">
                <div className="alert alert-warning">
                    Karyawan tidak ditemukan
                </div>
            </div>
        );
    }

    // Debug log untuk melihat nilai education_job_match
    console.log('Employee data:', employee);
    console.log('Education job match:', employee.education_job_match);

    return (
        <div className="main-container">
            {/* Header dengan tombol aksi */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <button 
                        className="btn btn-outline-secondary me-3" 
                        onClick={() => navigate('/employees')}
                    >
                        ‚Üê Kembali
                    </button>
                    <h1 className="d-inline">Karyawan</h1>
                </div>
                {employee.status !== 'Inactive' && employee.status !== 'resigned' && employee.status !== 'terminated' && employee.education_job_match !== 'Unmatch' && (
                    <div className="action-buttons">
                        <button 
                            className="btn-action btn-aktif"
                            onClick={handleAktif}
                            disabled={employee.status === 'active'}
                        >
                            Perpanjang
                        </button>
                        <button 
                            className="btn-action btn-probation"
                            onClick={handleProbation}
                            disabled={employee.status === 'Inactive' || employee.status === 'resigned' || employee.status === 'terminated'}
                        >
                            Probation
                        </button>
                        <button 
                            className="btn-action btn-resign"
                            onClick={() => setShowResignForm(true)}
                            disabled={employee.status === 'Inactive' || employee.status === 'resigned' || employee.status === 'terminated'}
                        >
                            Resign
                        </button>
                    </div>
                )}
                {employee.status !== 'Inactive' && employee.status !== 'resigned' && employee.status !== 'terminated' && employee.education_job_match === 'Unmatch' && (
                    <div className="action-buttons">
                        <div className="alert alert-warning mb-0">
                            <small><strong>Karyawan tidak dapat direkomendasikan</strong><br/>
                            Status Match: Unmatch - Pendidikan dan role tidak sesuai</small>
                        </div>
                    </div>
                )}
            </div>

            {/* Three Column Layout */}
            <div className="three-column-layout">

                {/* Left Column - Employee Profile */}
                <div className="employee-profile-card">
                    <div className="employee-info">
                        <img 
                            src={`https://via.placeholder.com/80x80/007bff/white?text=${employee.name?.charAt(0) || 'N'}`}
                            alt={employee.name}
                            className="employee-avatar"
                        />

                        <div className="info-item">
                            <strong>Nama Lengkap</strong>
                            <div className="info-value">{employee.name || 'N/A'}</div>
                        </div>

                        <div className="info-item">
                            <strong>Tanggal Lahir</strong>
                            <div className="info-value">{formatDate(employee.birth_date)}</div>
                        </div>

                        <div className="info-item">
                            <strong>EID</strong>
                            <div className="info-value">{employee.eid || 'N/A'}</div>
                        </div>

                        <div className="info-item">
                            <strong>No HP</strong>
                            <div className="info-value">{employee.phone || 'N/A'}</div>
                        </div>
                    </div>

                    <div className="cv-section">
                        <div className="cv-placeholder">
                            CV
                        </div>
                    </div>
                </div>

                {/* Center Column - Data Karyawan */}
                <div className="employee-data-card">
                    <div className="card-header">
                        <h2>Data Karyawan</h2>
                    </div>

                    <div className="data-field">
                        <strong>Nama Department:</strong>
                        <div className="field-value">{employee.division_name || 'N/A'}</div>
                    </div>

                    <div className="data-field">
                        <strong>Alamat:</strong>
                        <div className="field-value">{employee.address || 'N/A'}</div>
                    </div>

                    <div className="data-field">
                        <strong>Tingkat Pendidikan:</strong>
                        <div className="field-value">{employee.education_level || 'N/A'}</div>
                    </div>

                    <div className="data-field">
                        <strong>Tanggal Join:</strong>
                        <div className="field-value">{formatDate(employee.join_date)}</div>
                    </div>

                    <div className="data-field">
                        <strong>Role:</strong>
                        <div className="field-value">{employee.role || 'N/A'}</div>
                    </div>

                    <div className="data-field">
                        <strong>Status Match:</strong>
                        <div className="field-value">
                            <span className={`badge ${employee.education_job_match === 'Match' ? 'bg-success' : 'bg-danger'}`}>
                                {employee.education_job_match || 'Unmatch'}
                            </span>
                            {(employee.education_job_match === 'Unmatch' || !employee.education_job_match) && (
                                <div className="text-muted mt-1">
                                    <small>Pendidikan dan role tidak sesuai</small>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Right Column - Detail Probation */}
                <div className="recommendation-card">
                    <div className="card-header">
                        <h2>Detail Probation</h2>
                    </div>

                    <div className="probation-info">
                        <div className="info-item">
                            <strong>Tanggal Masuk Probation:</strong>
                            <div className="info-value">{formatDate(employee.join_date) || 'N/A'}</div>
                        </div>

                        <div className="info-item">
                            <strong>Tanggal Selesai Probation:</strong>
                            <div className="info-value">
                                {employee.join_date ? (() => {
                                    const joinDate = new Date(employee.join_date);
                                    const probationEndDate = new Date(joinDate);
                                    probationEndDate.setMonth(probationEndDate.getMonth() + 3);
                                    return probationEndDate.toLocaleDateString('id-ID');
                                })() : 'N/A'}
                            </div>
                        </div>

                        <div className="info-item">
                            <strong>Status:</strong>
                            <div className="info-value">
                                <span className={`status-badge-modern ${getStatusBadge(employee.status)}`}>
                                    {getStatusText(employee.status)}
                                </span>
                            </div>
                        </div>

                        <div className="info-item">
                            <strong>Pendidikan:</strong>
                            <div className="info-value">
                                {employee.education_level ? `${employee.education_level}${employee.major ? ` - ${employee.major}` : ''}` : 'N/A'}
                            </div>
                        </div>
                    </div>

                    <div className="probation-actions">
                        {(employee.status === 'Inactive' || employee.status === 'resigned' || employee.status === 'terminated') ? (
                            // Tidak ada tombol untuk karyawan yang sudah resign
                            <div className="text-center text-muted">
                                <small>Karyawan sudah tidak aktif</small>
                            </div>
                        ) : (employee.education_job_match === 'Unmatch' || employee.education_job_match === null || employee.education_job_match === undefined) ? (
                            <div className="alert alert-warning text-center mb-0" style={{
                                padding: '12px',
                                borderRadius: '8px',
                                border: '1px solid #ffc107',
                                backgroundColor: '#fff3cd'
                            }}>
                                <div className="mb-2">
                                    <span className="badge bg-danger">‚ùå Unmatch</span>
                                </div>
                                <small className="text-muted">
                                    Tidak dapat direkomendasikan<br/>
                                    <em>Pendidikan dan role tidak sesuai</em>
                                </small>
                            </div>
                        ) : employee.education_job_match === 'Match' ? (
                            <button 
                                className="btn-send-green"
                                onClick={handleAktif}
                                disabled={employee.status === 'Inactive' || employee.status === 'resigned' || employee.status === 'terminated'}
                            >
                                Perpanjang
                            </button>
                        ) : (
                            // Fallback untuk status yang tidak dikenali
                            <div className="alert alert-secondary text-center mb-0" style={{
                                padding: '12px',
                                borderRadius: '8px',
                                border: '1px solid #6c757d',
                                backgroundColor: '#f8f9fa'
                            }}>
                                <div className="mb-2">
                                    <span className="badge bg-secondary">‚ùì Status Tidak Diketahui</span>
                                </div>
                                <small className="text-muted">
                                    Status match tidak dapat ditentukan
                                </small>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Detail Kontrak Section */}
            {contracts && contracts.length > 0 && (
                <div className="contract-details-section mt-4">
                    <div className="card">
                        <div className="card-header" style={{ backgroundColor: '#007bff', color: 'white' }}>
                            <h3 className="mb-0">üìÑ Detail Kontrak</h3>
                        </div>
                        <div className="card-body">
                            <div className="row">
                                {contracts.map((contract, index) => {
                                    const actualStatus = getActualContractStatus(contract);
                                    return (
                                    <div key={contract.contract_id} className="col-md-6 mb-3">
                                        <div className={`contract-card ${actualStatus === 'active' ? 'active-contract' : ''}`} style={{
                                            border: actualStatus === 'active' ? '2px solid #28a745' : actualStatus === 'expired' ? '2px solid #dc3545' : '1px solid #dee2e6',
                                            borderRadius: '8px',
                                            padding: '15px',
                                            backgroundColor: actualStatus === 'active' ? '#f8fff9' : actualStatus === 'expired' ? '#fff5f5' : '#f8f9fa',
                                            marginBottom: '10px'
                                        }}>
                                            <div className="contract-header">
                                                <h5 className="contract-title">
                                                    {contract.type}
                                                    <span className={`badge ms-2 bg-${getContractStatusColor(actualStatus)}`}>
                                                        {getContractStatusText(actualStatus)}
                                                    </span>
                                                </h5>
                                            </div>
                                            <div className="contract-details">
                                                <div className="detail-row" style={{ 
                                                    display: 'flex', 
                                                    justifyContent: 'space-between', 
                                                    marginBottom: '8px',
                                                    paddingBottom: '5px',
                                                    borderBottom: '1px solid #e9ecef'
                                                }}>
                                                    <strong>Periode:</strong>
                                                    <span>{formatDate(contract.start_date)} - {formatDate(contract.end_date)}</span>
                                                </div>
                                                <div className="detail-row" style={{ 
                                                    display: 'flex', 
                                                    justifyContent: 'space-between', 
                                                    marginBottom: '8px',
                                                    paddingBottom: '5px',
                                                    borderBottom: '1px solid #e9ecef'
                                                }}>
                                                    <strong>Durasi:</strong>
                                                    <span>
                                                        {(() => {
                                                            const start = new Date(contract.start_date);
                                                            const end = new Date(contract.end_date);
                                                            const months = Math.round((end - start) / (1000 * 60 * 60 * 24 * 30));
                                                            return `${months} bulan`;
                                                        })()}
                                                    </span>
                                                </div>
                                                {contract.notes && (
                                                    <div className="detail-row" style={{ 
                                                        display: 'flex', 
                                                        justifyContent: 'space-between', 
                                                        marginBottom: '8px',
                                                        paddingBottom: '5px',
                                                        borderBottom: '1px solid #e9ecef'
                                                    }}>
                                                        <strong>Catatan:</strong>
                                                        <span className="text-muted">{contract.notes}</span>
                                                    </div>
                                                )}
                                                {actualStatus === 'active' && (
                                                    <div className="detail-row" style={{ 
                                                        display: 'flex', 
                                                        justifyContent: 'space-between', 
                                                        marginBottom: '8px',
                                                        paddingBottom: '5px',
                                                        borderBottom: '1px solid #e9ecef'
                                                    }}>
                                                        <strong>Sisa Waktu:</strong>
                                                        <span className="text-warning">
                                                            {(() => {
                                                                const today = new Date();
                                                                const endDate = new Date(contract.end_date);
                                                                const diffTime = endDate - today;
                                                                const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

                                                                if (diffDays > 0) {
                                                                    return `${diffDays} hari lagi`;
                                                                } else if (diffDays === 0) {
                                                                    return 'Berakhir hari ini';
                                                                } else {
                                                                    return 'Sudah berakhir';
                                                                }
                                                            })()}
                                                        </span>
                                                    </div>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                    );
                                })}
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Riwayat Rekomendasi Section */}
            {recommendations && recommendations.length > 0 && (
                <div className="recommendations-section mt-4">
                    <div className="card">
                        <div className="card-header" style={{ backgroundColor: '#28a745', color: 'white' }}>
                            <h3 className="mb-0">üìã Riwayat Rekomendasi Manager</h3>
                        </div>
                        <div className="card-body">
                            <div className="table-responsive">
                                <table className="table table-striped">
                                    <thead>
                                        <tr>
                                            <th>Tanggal</th>
                                            <th>Jenis</th>
                                            <th>Durasi</th>
                                            <th>Status</th>
                                            <th>Manager</th>
                                            <th>Diproses</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {recommendations.map((rec, index) => (
                                            <tr key={rec.recommendation_id}>
                                                <td>{formatDate(rec.created_at)}</td>
                                                <td>
                                                    <span className="badge bg-info">
                                                        {rec.recommendation_type === 'extend' ? 'Perpanjang' : 
                                                         rec.recommendation_type === 'permanent' ? 'Permanent' : 
                                                         rec.recommendation_type}
                                                    </span>
                                                </td>
                                                <td>{rec.recommended_duration} bulan</td>
                                                <td>
                                                    <span className={`badge ${
                                                        rec.status === 'approved' ? 'bg-success' : 
                                                        rec.status === 'pending' ? 'bg-warning' : 'bg-danger'
                                                    }`}>
                                                        {rec.status === 'approved' ? '‚úÖ Disetujui' : 
                                                         rec.status === 'pending' ? '‚è≥ Pending' : '‚ùå Ditolak'}
                                                    </span>
                                                </td>
                                                <td>{rec.recommended_by_name || 'N/A'}</td>
                                                <td>{rec.updated_at ? formatDate(rec.updated_at) : '-'}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Resign Modal */}
            {showResignForm && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Resign Karyawan</h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => {
                                        setShowResignForm(false);
                                        setResignReason('');
                                        setResignDate('');
                                    }}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <div className="form-group">
                                    <label>Alasan Resign</label>
                                    <textarea 
                                        className="form-control"
                                        rows="4"
                                        placeholder="Masukkan alasan resign..."
                                        value={resignReason}
                                        onChange={(e) => setResignReason(e.target.value)}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Tanggal Resign</label>
                                    <input 
                                        type="date"
                                        className="form-control"
                                        value={resignDate}
                                        onChange={(e) => setResignDate(e.target.value)}
                                    />
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button 
                                    type="button" 
                                    className="btn btn-secondary"
                                    onClick={() => {
                                        setShowResignForm(false);
                                        setResignReason('');
                                        setResignDate('');
                                    }}
                                >
                                    Batal
                                </button>
                                <button 
                                    type="button" 
                                    className="btn btn-danger"
                                    onClick={handleResign}
                                >
                                    Resign
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
            {showResignForm && <div className="modal-backdrop fade show"></div>}

            {/* Footer */}
            <div className="footer">
                <div className="footer-links">
                    <a href="#">Tentang Kami</a>
                    <a href="#">Kontak</a>
                    <a href="#">Kebijakan Privasi</a>
                    <a href="#">Ketentuan Layanan</a>
                </div>
                <p>¬©2025 CareerMatch. Semua hak cipta dilindungi.</p>
            </div>
        </div>
    );
};

export default EmployeeDetail;
