      'probation': { variant: 'warning', text: 'Probation' },
      '1': { variant: 'info', text: 'Kontrak 1 Tahun' },
      '2': { variant: 'primary', text: 'Kontrak 2 Tahun' },
      '3': { variant: 'secondary', text: 'Kontrak 3 Tahun' },
      'permanent': { variant: 'success', text: 'Permanent' },
      null: { variant: 'light', text: 'N/A' }
    };

    const badge = badges[contractType] || badges[null];

    if (contractType === 'probation' && tenure >= 3) {
      return <Badge bg="warning" className="border border-danger">Probation (Ready for Review)</Badge>;
    }

    return <Badge bg={badge.variant}>{badge.text}</Badge>;
  };

