                                          const tenure = selectedEmployee.tenure_months || 0;
                                          const contractType = selectedEmployee.current_contract_type;

                                          // Base resign rate (inverse of success rate)
                                          let resignRate = educationScore >= 3 ? 15 : educationScore >= 2 ? 30 : 55;

                                          // Adjust based on tenure
                                          if (tenure > 24) resignRate -= 10;
                                          else if (tenure > 12) resignRate -= 5;
                                          else if (tenure < 6) resignRate += 10;

                                          // Adjust based on contract progression
                                          if (contractType === 'permanent') resignRate = 5;
