                                          const contractType = selectedEmployee.current_contract_type;

                                          // Base early resign rate
                                          let earlyResignRate = educationScore >= 3 ? 8 : educationScore >= 2 ? 15 : 35;

                                          // Probation phase has higher early resign risk
                                          if (contractType === 'probation') earlyResignRate += 10;
                                          else if (contractType === 'permanent') earlyResignRate = 3;

                                          // Recent hires have higher risk
                                          if (tenure < 3) earlyResignRate += 15;
