import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const Employees = () => {
    const { user } = useAuth();
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showAddModal, setShowAddModal] = useState(false);
    const [newEmployee, setNewEmployee] = useState({
        name: '',
        email: '',
        phone: '',
        birth_date: '',
        address: '',
        education_level: '',
        major: '',
        role: '',
        division_id: '',
        join_date: ''
    });
    const [divisions, setDivisions] = useState([]);

    useEffect(() => {
        fetchEmployees();
        fetchDivisions();
    }, []);

    const fetchEmployees = async () => {
        try {
            const response = await axios.get('/employees.php');
            if (response.data.success) {
                setEmployees(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching employees:', error);
            setError('Terjadi kesalahan saat memuat data');
        } finally {
            setLoading(false);
        }
    };

    const fetchDivisions = async () => {
        try {
            const response = await axios.get('/divisions.php');
            console.log('Divisions response:', response.data); // Debug log
            if (response.data.success) {
                setDivisions(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching divisions:', error);
        }
    };

    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            console.log('Adding employee with data:', newEmployee);

            const formData = new FormData();
            formData.append('action', 'create');
            Object.keys(newEmployee).forEach(key => {
                formData.append(key, newEmployee[key]);
                console.log(`FormData: ${key} = ${newEmployee[key]}`);
            });

            console.log('Sending request to /employees.php');
            const response = await axios.post('/employees.php', formData);
            console.log('Response received:', response.data);

            if (response.data.success) {
                alert('Karyawan berhasil ditambahkan!');
                setShowAddModal(false);
                setNewEmployee({
                    name: '',
                    email: '',
                    phone: '',
                    birth_date: '',
                    address: '',
                    education_level: '',
                    major: '',
                    role: '',
                    division_id: '',
                    join_date: ''
                });
                fetchEmployees();
            } else {
                console.error('Server error:', response.data);
                alert('Error: ' + (response.data.error || 'Unknown error'));
            }
        } catch (error) {
            console.error('Error adding employee:', error);
            console.error('Error response:', error.response?.data);
            alert('Terjadi kesalahan saat menambah karyawan: ' + (error.response?.data?.error || error.message));
        }
    };

    const getStatusBadge = (status) => {
        switch (status) {
            case 'Active':
                return 'status-active';
            case 'Probation':
                return 'status-probation';
            default:
                return 'status-inactive';
        }
    };

    const getStatusText = (status) => {
        switch (status) {
            case 'Active':
                return 'Aktif';
            case 'Probation':
                return 'Probation';
            case 'Inactive':
                return 'Resign';
            default:
                return 'Tidak Aktif';
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'active':
                return 'status-active-color';
            case 'probation':
                return 'status-probation-color';
            case 'resigned':
            case 'terminated':
                return 'status-inactive-color';
            default:
                return 'status-inactive-color';
        }
    };

    if (loading) {
        return (
            <div className="loading-spinner">
                <div className="spinner"></div>
            </div>
        );
    }

    return (
        <div className="main-container">
            {/* Header */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <h1 className="page-title">Total Karyawan: {employees.length}</h1>
                </div>
                {(user.role === 'admin' || user.role === 'hr') && (
                    <button 
                        className="btn btn-primary btn-add-employee"
                        onClick={() => setShowAddModal(true)}
                    >
                        <i className="fas fa-plus"></i> Tambah Karyawan
                    </button>
                )}
            </div>

            {/* Karyawan Profile Section */}
            <div className="employee-section">
                <h2 className="section-title-main">Karyawan Profile</h2>
                <p className="section-subtitle-main">Total Talents {employees.filter(emp => emp.status === 'active' || emp.status === 'probation').length}</p>

                <div className="employee-list-horizontal">
                    {/* Add Employee Card */}
                    {(user.role === 'admin' || user.role === 'hr') && (
                        <div className="add-employee-card-section" onClick={() => setShowAddModal(true)}>
                            <div className="add-icon">
                                <i className="fas fa-plus"></i>
                            </div>
                        </div>
                    )}

                    {/* Active & Probation Employees */}
                    {employees.filter(emp => emp.status === 'active' || emp.status === 'probation').map(employee => (
                        <div key={employee.eid} className="employee-card-horizontal">
                            <div className="employee-content-wrapper">
                                <div className="employee-top-section">
                                    <div className="employee-avatar-container">
                                        <img 
                                            src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=60`}
                                            alt={employee.name}
                                            className="employee-avatar-horizontal"
                                        />
                                    </div>
                                    <div className="employee-info-horizontal">
                                        <h4 className="employee-name-horizontal">{employee.name}</h4>
                                        <p className="employee-subtitle-horizontal">{employee.age ? `${employee.age} Tahun` : 'N/A Tahun'}</p>
                                        <p className="employee-email-horizontal">{employee.email}</p>
                                    </div>
                                </div>
                                <div className="employee-status-indicator">
                                    <div className="status-dots-container">
                                        <span className={`status-dot ${employee.status === 'active' ? 'status-active-color' : ''}`}></span>
                                        <span className={`status-dot-small ${employee.status === 'probation' ? 'status-probation-color' : ''}`}></span>
                                        <span className="status-dot-small"></span>
                                    </div>
                                    <Link 
                                        to={`/employees/${employee.eid}`}
                                        className="btn-details-horizontal"
                                    >
                                        Details
                                    </Link>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Karyawan Resign Section */}
            {employees.filter(emp => emp.status === 'resigned' || emp.status === 'terminated').length > 0 && (
                <div className="employee-section">
                    <h2 className="section-title-main">Karyawan Resign</h2>
                    <p className="section-subtitle-main">Total Karyawan {employees.filter(emp => emp.status === 'resigned' || emp.status === 'terminated').length}</p>

                    <div className="employee-list-horizontal">
                        {employees.filter(emp => emp.status === 'resigned' || emp.status === 'terminated').map(employee => (
                            <div key={employee.eid} className="employee-card-horizontal resigned">
                                <div className="employee-content-wrapper">
                                    <div className="employee-top-section">
                                        <div className="employee-avatar-container">
                                            <img 
                                                src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=60`}
                                                alt={employee.name}
                                                className="employee-avatar-horizontal"
                                            />
                                        </div>
                                        <div className="employee-info-horizontal">
                                            <h4 className="employee-name-horizontal">{employee.name}</h4>
                                            <p className="employee-subtitle-horizontal">{employee.age ? `${employee.age} Tahun` : 'N/A Tahun'}</p>
                                            <p className="employee-email-horizontal">{employee.email}</p>
                                        </div>
                                    </div>
                                    <div className="employee-status-indicator">
                                        <div className="status-dots-container">
                                            <span className="status-dot-small"></span>
                                            <span className="status-dot-small"></span>
                                            <span className="status-dot status-inactive-color"></span>
                                        </div>
                                        <Link 
                                            to={`/employees/${employee.eid}`}
                                            className="btn-details-horizontal"
                                        >
                                            Update
                                        </Link>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {/* Empty State - jika tidak ada karyawan sama sekali */}
            {employees.length === 0 && (
                <div className="empty-state">
                    <div className="empty-icon">
                        <i className="fas fa-users fa-3x"></i>
                    </div>
                    <h3>Belum Ada Data Karyawan</h3>
                    <p className="text-muted">Mulai dengan menambahkan karyawan baru</p>
                    {(user.role === 'admin' || user.role === 'hr') && (
                        <button 
                            className="btn btn-primary"
                            onClick={() => setShowAddModal(true)}
                        >
                            <i className="fas fa-plus"></i> Tambah Karyawan Pertama
                        </button>
                    )}
                </div>
            )}

            {/* Add Employee Modal */}
            {showAddModal && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Tambah Karyawan Baru</h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => setShowAddModal(false)}
                                ></button>
                            </div>
                            <form onSubmit={handleAddEmployee}>
                                <div className="modal-body">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Nama Lengkap</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.name}
                                                    onChange={(e) => setNewEmployee({...newEmployee, name: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Email</label>
                                                <input
                                                    type="email"
                                                    className="form-control"
                                                    value={newEmployee.email}
                                                    onChange={(e) => setNewEmployee({...newEmployee, email: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>No. HP</label>
                                                <input
                                                    type="tel"
                                                    className="form-control"
                                                    value={newEmployee.phone}
                                                    onChange={(e) => setNewEmployee({...newEmployee, phone: e.target.value})}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Tanggal Lahir</label>
                                                <input
                                                    type="date"
                                                    className="form-control"
                                                    value={newEmployee.birth_date}
                                                    onChange={(e) => setNewEmployee({...newEmployee, birth_date: e.target.value})}
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Tanggal Masuk</label>
                                                <input
                                                    type="date"
                                                    className="form-control"
                                                    value={newEmployee.join_date}
                                                    onChange={(e) => setNewEmployee({...newEmployee, join_date: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Alamat</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.address}
                                                    onChange={(e) => setNewEmployee({...newEmployee, address: e.target.value})}
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="form-group">
                                        <label>Tingkat Pendidikan</label>
                                        <select
                                            className="form-control"
                                            value={newEmployee.education_level}
                                            onChange={(e) => setNewEmployee({...newEmployee, education_level: e.target.value})}
                                            required
                                        >
                                            <option value="">Pilih Tingkat Pendidikan</option>
                                            <option value="SMA/K">SMA/K</option>
                                            <option value="D3">D3</option>
                                            <option value="S1">S1</option>
                                            <option value="S2">S2</option>
                                            <option value="S3">S3</option>
                                        </select>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Jurusan</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.major}
                                                    onChange={(e) => setNewEmployee({...newEmployee, major: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Role</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.role}
                                                    onChange={(e) => setNewEmployee({...newEmployee, role: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Divisi</label>
                                                <select
                                                    className="form-control"
                                                    value={newEmployee.division_id}
                                                    onChange={(e) => setNewEmployee({...newEmployee, division_id: e.target.value})}
                                                    required
                                                >
                                                    <option value="">Pilih Divisi</option>
                                                    {divisions.map(division => (
                                                        <option key={division.division_id} value={division.division_id}>
                                                            {division.division_name}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button 
                                        type="button" 
                                        className="btn btn-secondary"
                                        onClick={() => setShowAddModal(false)}
                                    >
                                        Batal
                                    </button>
                                    <button type="submit" className="btn btn-primary">
                                        Tambah Karyawan
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
            {showAddModal && <div className="modal-backdrop fade show"></div>}

            {/* Footer */}
            <div className="footer">
                <div className="footer-links">
                    <a href="#">Tentang Kami</a>
                    <a href="#">Kontak</a>
                    <a href="#">Kebijakan Privasi</a>
                    <a href="#">Ketentuan Layanan</a>
                </div>
                <p>2025 CareerMatch. Semua hak cipta dilindungi.</p>
            </div>
        </div>
    );
};

export default Employees;
