import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../App';

const Users = () => {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [newUser, setNewUser] = useState({
    username: '',
    password: '',
    email: '',
    role: 'hr'
  });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('/auth.php?action=users');
      if (response.data.success) {
        setUsers(response.data.data);
      }
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('action', 'register');
      Object.keys(newUser).forEach(key => {
        formData.append(key, newUser[key]);
      });

      const response = await axios.post('/auth.php', formData);
      if (response.data.success) {
        alert('User berhasil ditambahkan!');
        setShowAddModal(false);
        setNewUser({
          username: '',
          password: '',
          email: '',
          role: 'hr'
        });
        fetchUsers();
      } else {
        alert('Error: ' + response.data.error);
      }
    } catch (error) {
      console.error('Error adding user:', error);
      alert('Terjadi kesalahan saat menambah user');
    }
  };

  const getRoleDisplay = (role) => {
    switch (role) {
      case 'admin':
        return 'Administrator';
      case 'hr':
        return 'Human Resource';
      case 'manager':
        return 'Manager';
      default:
        return role;
    }
  };

  const getRoleLocation = (role) => {
    switch (role) {
      case 'admin':
        return 'System Admin';
      case 'hr':
        return 'Baloi | Jakarta, Indonesia';
      case 'manager':
        return 'Management | Jakarta, Indonesia';
      default:
        return 'Jakarta, Indonesia';
    }
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner"></div>
      </div>
    );
  }

  // Redirect if not admin
  if (user.role !== 'admin') {
    return (
      <div className="main-container">
        <div className="alert alert-warning">
          Anda tidak memiliki akses ke halaman ini
        </div>
      </div>
    );
  }

  return (
    <div className="main-container">
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <button className="btn btn-outline-secondary me-3">
            Home
          </button>
          <h1 className="d-inline">Users</h1>
        </div>
        <button className="btn btn-danger">
          Keluar
        </button>
      </div>

      {/* Users Grid */}
      <div className="row">
        {users && users.length > 0 ? users.map(userItem => (
          <div key={userItem.id} className="col-md-6 mb-4">
            <div className="user-card">
              <div className="d-flex align-items-center">
                <div className="user-logo">
                  <i className="fas fa-user"></i>
                </div>
                <div className="user-info flex-grow-1">
                  <h4 className="user-title">{getRoleDisplay(userItem.role)}</h4>
                  <p className="user-location mb-1">{getRoleLocation(userItem.role)}</p>
                  <p className="user-salary mb-0">
                    <small className="text-muted">Range Gaji -</small>
                  </p>
                </div>
                <div className="user-actions">
                  <button className="btn btn-primary btn-sm">
                    Details
                  </button>
                </div>
              </div>
            </div>
          </div>
        )) : (
          <div className="col-12">
            <div className="alert alert-info">
              {users === null ? 'Memuat data users...' : 'Tidak ada users yang ditemukan'}
            </div>
          </div>
        )}

        {/* Add User Card */}
        <div className="col-md-6 mb-4">
          <div 
            className="user-card add-user-card" 
            onClick={() => setShowAddModal(true)}
            style={{ cursor: 'pointer' }}
          >
            <div className="text-center">
              <div className="add-user-icon mb-2">
                <i className="fas fa-plus-circle"></i>
              </div>
              <h5>Tambah User Baru</h5>
            </div>
          </div>
        </div>
      </div>

      {/* Add User Modal */}
      {showAddModal && (
        <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Tambah User Baru</h5>
                <button 
                  type="button" 
                  className="btn-close"
                  onClick={() => setShowAddModal(false)}
                ></button>
              </div>
              <form onSubmit={handleAddUser}>
                <div className="modal-body">
                  <div className="form-group">
                    <label>Username</label>
                    <input
                      type="text"
                      className="form-control"
                      value={newUser.username}
                      onChange={(e) => setNewUser({...newUser, username: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      className="form-control"
                      value={newUser.email}
                      onChange={(e) => setNewUser({...newUser, email: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Password</label>
                    <input
                      type="password"
                      className="form-control"
                      value={newUser.password}
                      onChange={(e) => setNewUser({...newUser, password: e.target.value})}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Role</label>
                    <select
                      className="form-control"
                      value={newUser.role}
                      onChange={(e) => setNewUser({...newUser, role: e.target.value})}
                      required
                    >
