import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Doughnut, Line } from 'react-chartjs-2';
import { useAuth } from '../App';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const ManagerDashboard = () => {
  const [analytics, setAnalytics] = useState(null);
  const [educationAnalysis, setEducationAnalysis] = useState(null);
  const [divisionIntelligence, setDivisionIntelligence] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const { user } = useAuth();

  useEffect(() => {
    if (user?.role === 'manager') {
      fetchManagerData();
    }
  }, [user]);

  const fetchManagerData = async () => {
    try {
      setLoading(true);
      
      // Parallel fetch untuk semua data dinamis
      const [analyticsRes, educationRes, intelligenceRes] = await Promise.all([
        axios.get('/employees.php?action=manager_analytics'),
        axios.get('/employees.php?action=education_job_analysis'),
        axios.get('/employees.php?action=division_intelligence')
      ]);

      if (analyticsRes.data.success) {
        setAnalytics(analyticsRes.data.data);
      }
      
      if (educationRes.data.success) {
        setEducationAnalysis(educationRes.data.data);
      }
      
      if (intelligenceRes.data.success) {
        setDivisionIntelligence(intelligenceRes.data.data);
      }
      
    } catch (error) {
      console.error('Error fetching manager data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getMatchDistributionChart = () => {
    if (!educationAnalysis?.match_distribution) return null;

    const data = educationAnalysis.match_distribution;
    return {
      labels: ['Kecocokan Tinggi', 'Kecocokan Sedang', 'Kecocokan Rendah'],
      datasets: [{
        data: [data.Tinggi || 0, data.Sedang || 0, data.Rendah || 0],
        backgroundColor: ['#28a745', '#ffc107', '#dc3545'],
        borderWidth: 2,
        borderColor: '#fff'
      }]
    };
  };

  const getPerformancePredictionChart = () => {
    if (!divisionIntelligence?.predictions) return null;

    const predictions = divisionIntelligence.predictions;
    const labels = predictions.map(p => p.name);
    const successData = predictions.map(p => p.success_probability);
    const matchData = predictions.map(p => p.match_score);

    return {
      labels,
      datasets: [
        {
          label: 'Probabilitas Sukses (%)',
          data: successData,
          borderColor: '#007bff',
          backgroundColor: 'rgba(0, 123, 255, 0.1)',
          tension: 0.4
        },
        {
          label: 'Skor Kecocokan Pendidikan (%)',
          data: matchData,
          borderColor: '#28a745',
          backgroundColor: 'rgba(40, 167, 69, 0.1)',
          tension: 0.4
        }
      ]
    };
  };

  const getRiskLevelChart = () => {
    if (!divisionIntelligence?.division_health) return null;

    const health = divisionIntelligence.division_health;
    return {
      labels: ['Low Risk', 'Medium Risk', 'High Risk'],
      datasets: [{
        label: 'Distribusi Risk Level',
        data: [
          health.low_risk_percentage || 0,
          health.medium_risk_percentage || 0,
          health.high_risk_percentage || 0
        ],
        backgroundColor: ['#0d6efd', '#ffc107', '#dc3545'],
        borderWidth: 1
      }]
    };
  };

  // Fungsi untuk mendapatkan rekomendasi kontrak berdasarkan data intelligence
  const getContractRecommendation = (employee) => {
    const matchScore = employee.education_job_match || 0;
    const tenure = employee.tenure_months || 0;
    const currentContract = employee.current_contract_type;

    if (currentContract === 'probation' && tenure >= 3) {
      if (matchScore >= 70) {
        return {
          type: 'permanent',
          reason: 'High education-job match score',
          duration: 'Permanent',
          confidence: 'High'
        };
      } else if (matchScore >= 50) {
        return {
          type: '1_year',
          reason: 'Good match with development potential',
          duration: '12 bulan',
          confidence: 'Medium'
        };
      } else {
        return {
          type: 'review',
          reason: 'Need performance evaluation',
          duration: 'Review diperlukan',
          confidence: 'Low'
        };
      }
    }

    if (tenure >= 12 && currentContract !== 'permanent') {
      return {
        type: 'extension',
        reason: 'Long tenure with good performance',
        duration: 'Extension recommended',
        confidence: 'High'
      };
    }

    return {
      type: 'monitor',
      reason: 'Continue monitoring',
      duration: 'Monitoring berkelanjutan',
      confidence: 'Medium'
    };
  };

  // Fungsi untuk mendapatkan status kontrak dengan warna
  const getContractStatusBadge = (contractType, tenure) => {
    const badges = {
      'probation': { class: 'bg-warning', text: 'Probation' },
      '1': { class: 'bg-info', text: 'Kontrak 1 Tahun' },
      '2': { class: 'bg-primary', text: 'Kontrak 2 Tahun' },
      '3': { class: 'bg-secondary', text: 'Kontrak 3 Tahun' },
      'permanent': { class: 'bg-success', text: 'Permanent' },
      null: { class: 'bg-light text-dark', text: 'N/A' }
    };

    const badge = badges[contractType] || badges[null];
    
    if (contractType === 'probation' && tenure >= 3) {
      return { class: 'bg-warning border border-danger', text: 'Probation (Ready for Review)' };
    }

    return badge;
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
          <div className="text-center">
            <div className="spinner-border text-primary mb-3" style={{ width: '3rem', height: '3rem' }} role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="text-muted">Memuat data intelligence...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Header */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="card border-0 shadow-sm" style={{ background: 'linear-gradient(135deg, #0d6efd 0%, #6f42c1 100%)' }}>
            <div className="card-body text-white">
              <div className="d-flex align-items-center justify-content-between">
                <div className="d-flex align-items-center">
                  <div className="me-3">
                    <i className="fas fa-chart-line fa-3x text-white"></i>
                  </div>
                  <div>
                    <h2 className="mb-1 text-white">Manager Dashboard - Data Intelligence</h2>
                    <p className="text-white-50 mb-0">
                      Analisis Kecocokan Pendidikan & Rekomendasi Kontrak untuk Divisi {user.division_name}
                    </p>
                    <small className="text-white-50">
                      PT Bumi Armatha Teknologi | Business Intelligence System
                    </small>
                  </div>
                </div>
                <div className="text-end">
                  <div className="display-4 fw-bold text-white">
                    {analytics?.division_stats?.active_employees || 0}
                  </div>
                  <div className="text-white-50">Karyawan Aktif</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="row mb-4">
        <div className="col-12">
          <nav className="nav nav-pills nav-fill">
            <button 
              className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`}
              onClick={() => setActiveTab('overview')}
            >
              <i className="fas fa-tachometer-alt me-2"></i>Overview
            </button>
            <button 
              className={`nav-link ${activeTab === 'education' ? 'active' : ''}`}
              onClick={() => setActiveTab('education')}
            >
              <i className="fas fa-graduation-cap me-2"></i>Analisis Pendidikan
            </button>
            <button 
              className={`nav-link ${activeTab === 'prediction' ? 'active' : ''}`}
              onClick={() => setActiveTab('prediction')}
            >
              <i className="fas fa-crystal-ball me-2"></i>Prediksi & Intelligence
            </button>
            <button 
              className={`nav-link ${activeTab === 'employees' ? 'active' : ''}`}
              onClick={() => setActiveTab('employees')}
            >
              <i className="fas fa-users me-2"></i>Detail Karyawan
            </button>
          </nav>
        </div>
      </div>

      {/* Overview Tab */}
      {activeTab === 'overview' && (
        <>
          {/* Key Metrics */}
          <div className="row mb-4">
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card primary">
                  <div className="stat-number">{analytics?.analytics?.avg_match_score || 0}%</div>
                  <div className="stat-label">Rata-rata Match Score</div>
                </div>
              </div>
            </div>
            
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card success">
                  <div className="stat-number">{analytics?.analytics?.high_match_percentage || 0}%</div>
                  <div className="stat-label">High Match Employees</div>
                </div>
              </div>
            </div>
            
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card warning">
                  <div className="stat-number">{analytics?.analytics?.avg_tenure || 0}</div>
                  <div className="stat-label">Rata-rata Tenure (Bulan)</div>
                </div>
              </div>
            </div>
            
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card danger">
                  <div className="stat-number">{analytics?.analytics?.retention_risk || 0}%</div>
                  <div className="stat-label">Retention Risk</div>
                </div>
              </div>
            </div>
          </div>

          {/* Charts Row */}
          <div className="row mb-4">
            <div className="col-lg-6 mb-4">
              <div className="dashboard-card">
                <h5 className="mb-3">
                  <i className="fas fa-chart-pie me-2"></i>
                  Distribusi Kecocokan Pendidikan-Pekerjaan
                </h5>
                <div className="chart-container" style={{ height: '300px' }}>
                  {getMatchDistributionChart() ? (
                    <Doughnut 
                      data={getMatchDistributionChart()} 
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        plugins: {
                          legend: { position: 'bottom' }
                        }
                      }}
                    />
                  ) : (
                    <div className="d-flex justify-content-center align-items-center h-100">
                      <p className="text-muted">Tidak ada data</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
            
            <div className="col-lg-6 mb-4">
              <div className="dashboard-card">
                <h5 className="mb-3">
                  <i className="fas fa-chart-bar me-2"></i>
                  Risk Level Distribution
                </h5>
                <div className="chart-container" style={{ height: '300px' }}>
                  {getRiskLevelChart() ? (
                    <Bar 
                      data={getRiskLevelChart()} 
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        plugins: {
                          legend: { display: false }
                        }
                      }}
                    />
                  ) : (
                    <div className="d-flex justify-content-center align-items-center h-100">
                      <p className="text-muted">Tidak ada data</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Division Health */}
          {divisionIntelligence?.division_health && (
            <div className="row mb-4">
              <div className="col-12">
                <div className="dashboard-card">
                  <h5 className="mb-3">
                    <i className="fas fa-heartbeat me-2"></i>
                    Kesehatan Divisi: {divisionIntelligence.division_health.overall_health}
                  </h5>
                  <div className="row">
                    {divisionIntelligence.division_health.recommendations.map((rec, index) => (
                      <div key={index} className="col-md-4 mb-3">
                        <div className="alert alert-info">
                          <i className="fas fa-lightbulb me-2"></i>
                          {rec}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}
        </>
      )}

      {/* Education Analysis Tab */}
      {activeTab === 'education' && educationAnalysis && (
        <div className="row">
          <div className="col-12">
            <div className="dashboard-card">
              <h5 className="mb-4">
                <i className="fas fa-graduation-cap me-2"></i>
                Analisis Detail Kecocokan Pendidikan - Pekerjaan
              </h5>
              
              <div className="table-responsive">
                <table className="table table-hover">
                  <thead className="table-dark">
                    <tr>
                      <th>Nama Karyawan</th>
                      <th>Pendidikan</th>
                      <th>Jurusan</th>
                      <th>Posisi</th>
                      <th>Match Score</th>
                      <th>Match Level</th>
                      <th>Tenure (Bulan)</th>
                      <th>Rekomendasi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {educationAnalysis.education_analysis.map((emp, index) => (
                      <tr key={index}>
                        <td>
                          <strong>{emp.name}</strong>
                        </td>
                        <td>{emp.education_level}</td>
                        <td>{emp.major}</td>
                        <td>{emp.role}</td>
                        <td>
                          <div className="d-flex align-items-center">
                            <div className="progress me-2" style={{ width: '60px', height: '8px' }}>
                              <div 
                                className={`progress-bar ${emp.match_score >= 70 ? 'bg-success' : emp.match_score >= 50 ? 'bg-warning' : 'bg-danger'}`}
                                style={{ width: `${emp.match_score}%` }}
                              ></div>
                            </div>
                            <small>{emp.match_score}%</small>
                          </div>
                        </td>
                        <td>
                          <span className={`badge ${emp.match_level === 'Tinggi' ? 'bg-success' : emp.match_level === 'Sedang' ? 'bg-warning' : 'bg-danger'}`}>
                            {emp.match_level}
                          </span>
                        </td>
                        <td>{emp.tenure_months}</td>
                        <td>
                          <small className="text-muted">{emp.recommendation}</small>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Contract Progression */}
              {educationAnalysis.contract_progression.length > 0 && (
                <div className="mt-4">
                  <h6 className="mb-3">
                    <i className="fas fa-arrow-up me-2"></i>
                    Karyawan Siap Perpanjangan Kontrak
                  </h6>
                  <div className="row">
                    {educationAnalysis.contract_progression.map((prog, index) => (
                      <div key={index} className="col-md-4 mb-3">
                        <div className="card border-success">
                          <div className="card-body">
                            <h6 className="card-title text-success">{prog.name}</h6>
                            <p className="card-text">
                              <small className="text-muted">Status: {prog.status}</small><br/>
                              <strong>{prog.recommendation}</strong>
                            </p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Prediction Tab */}
      {activeTab === 'prediction' && divisionIntelligence && (
        <>
          <div className="row mb-4">
            <div className="col-12">
              <div className="dashboard-card">
                <h5 className="mb-3">
                  <i className="fas fa-chart-line me-2"></i>
                  Prediksi Performa & Success Probability
                </h5>
                <div className="chart-container" style={{ height: '400px' }}>
                  {getPerformancePredictionChart() ? (
                    <Line 
                      data={getPerformancePredictionChart()} 
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        scales: {
                          y: {
                            beginAtZero: true,
                            max: 100
                          }
                        }
                      }}
                    />
                  ) : (
                    <div className="d-flex justify-content-center align-items-center h-100">
                      <p className="text-muted">Tidak ada data prediksi</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Upcoming Decisions */}
          {divisionIntelligence.upcoming_decisions.length > 0 && (
            <div className="row mb-4">
              <div className="col-12">
                <div className="dashboard-card">
                  <h5 className="mb-3">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    Keputusan Kontrak yang Mendesak
                  </h5>
                  <div className="row">
                    {divisionIntelligence.upcoming_decisions.map((decision, index) => (
                      <div key={index} className="col-md-6 mb-3">
                        <div className={`card border-${decision.urgency === 'high' ? 'danger' : 'warning'}`}>
                          <div className="card-body">
                            <div className="d-flex justify-content-between align-items-start">
                              <div>
                                <h6 className="card-title">{decision.employee.name}</h6>
                                <p className="card-text">
                                  <small className="text-muted">
                                    Kontrak berakhir dalam {Math.abs(decision.employee.days_to_contract_end)} hari
                                  </small><br/>
                                  <strong>Success Probability: {decision.prediction.success_probability}%</strong><br/>
                                  <span className={`badge bg-${decision.prediction.risk_level === 'Low' ? 'success' : decision.prediction.risk_level === 'Medium' ? 'warning' : 'danger'}`}>
                                    {decision.prediction.risk_level} Risk
                                  </span>
                                </p>
                              </div>
                              <span className={`badge bg-${decision.urgency === 'high' ? 'danger' : 'warning'}`}>
                                {decision.urgency === 'high' ? 'Urgent' : 'Medium'}
                              </span>
                            </div>
                            <hr/>
                            <small className="text-muted">
                              <strong>Rekomendasi:</strong> {decision.prediction.recommendation}
                            </small>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Performance Insights */}
          <div className="row">
            <div className="col-12">
              <div className="dashboard-card">
                <h5 className="mb-3">
                  <i className="fas fa-brain me-2"></i>
                  Performance Insights
                </h5>
                <div className="table-responsive">
                  <table className="table table-sm">
                    <thead className="table-light">
                      <tr>
                        <th>Karyawan</th>
                        <th>Education-Job Alignment</th>
                        <th>Strengths</th>
                        <th>Development Areas</th>
                      </tr>
                    </thead>
                    <tbody>
                      {divisionIntelligence.performance_insights.map((insight, index) => (
                        <tr key={index}>
                          <td><strong>{insight.employee_name}</strong></td>
                          <td>
                            <span className={`badge bg-${insight.education_job_alignment === 'Excellent' ? 'success' : insight.education_job_alignment === 'Good' ? 'primary' : 'warning'}`}>
                              {insight.education_job_alignment}
                            </span>
                          </td>
                          <td>
                            {insight.strengths.map((strength, i) => (
                              <small key={i} className="d-block text-success">
                                <i className="fas fa-check me-1"></i>{strength}
                              </small>
                            ))}
                          </td>
                          <td>
                            {insight.development_areas.map((area, i) => (
                              <small key={i} className="d-block text-warning">
                                <i className="fas fa-arrow-up me-1"></i>{area}
                              </small>
                            ))}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </>
      )}

      {/* Employees Detail Tab */}
      {activeTab === 'employees' && analytics && (
        <div className="row">
          <div className="col-12">
            <div className="dashboard-card">
              <h5 className="mb-4">
                <i className="fas fa-users me-2"></i>
                Detail Karyawan Divisi {user.division_name}
              </h5>
              
              <div className="table-responsive">
                <table className="table table-hover">
                  <thead style={{ backgroundColor: '#0d6efd', color: 'white' }}>
                    <tr>
                      <th>Nama</th>
                      <th>Posisi & Department</th>
                      <th>Pendidikan</th>
                      <th>Join Date</th>
                      <th>Tenure</th>
                      <th>Status Kontrak</th>
                      <th>Match Score</th>
                      <th>Rekomendasi Kontrak</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {analytics.employees.map((emp, index) => {
                      const contractBadge = getContractStatusBadge(emp.current_contract_type, emp.tenure_months);
                      const recommendation = getContractRecommendation(emp);
                      
                      return (
                        <tr key={index}>
                          <td>
                            <div>
                              <strong className="text-primary">{emp.name}</strong>
                              {emp.age && (
                                <>
                                  <br/>
                                  <small className="text-muted">{emp.age} tahun</small>
                                </>
                              )}
                            </div>
                          </td>
                          <td>
                            <div>
                              <strong>{emp.role}</strong>
                              <br/>
                              <small className="text-primary">{emp.division_name}</small>
                              {emp.designation && (
                                <>
                                  <br/>
                                  <small className="text-muted">{emp.designation}</small>
                                </>
                              )}
                            </div>
                          </td>
                          <td>
                            <div>
                              <strong>{emp.education_level}</strong>
                              <>
                                <br/>
                                <small className="text-muted">{emp.major}</small>
                              </>
                            </div>
                          </td>
                          <td>
                            <span className="text-primary fw-bold">
                              {new Date(emp.join_date).toLocaleDateString('id-ID')}
                            </span>
                          </td>
                          <td>
                            <span className="badge bg-info text-dark">
                              {emp.tenure_months} bulan
                            </span>
                          </td>
                          <td>
                            <span className={`badge ${contractBadge.class}`}>
                              {contractBadge.text}
                            </span>
                            {emp.contract_end && (
                              <>
                                <br/>
                                <small className="text-muted">
                                  Berakhir: {new Date(emp.contract_end).toLocaleDateString('id-ID')}
                                </small>
                              </>
                            )}
                          </td>
                          <td>
                            <div className="d-flex align-items-center">
                              <div className="progress me-2" style={{ width: '60px', height: '8px' }}>
                                <div 
                                  className={`progress-bar ${emp.education_job_match >= 70 ? 'bg-success' : emp.education_job_match >= 50 ? 'bg-warning' : 'bg-danger'}`}
                                  style={{ width: `${emp.education_job_match}%` }}
                                ></div>
                              </div>
                              <small className="fw-bold">{emp.education_job_match}%</small>
                            </div>
                          </td>
                          <td>
                            <div className="recommendation-cell">
                              <span className={`badge mb-1 ${
                                recommendation.confidence === 'High' ? 'bg-success' : 
                                recommendation.confidence === 'Medium' ? 'bg-warning text-dark' : 'bg-danger'
                              }`}>
                                {recommendation.duration}
                              </span>
                              <br/>
                              <small className="text-muted">{recommendation.reason}</small>
                            </div>
                          </td>
                          <td>
                            <span className={`badge ${emp.status === 'active' ? 'bg-success' : 'bg-secondary'}`}>
                              {emp.status === 'active' ? 'Aktif' : emp.status}
                            </span>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManagerDashboard;
