import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const Employees = () => {
    const { user } = useAuth();
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAddModal, setShowAddModal] = useState(false);
    const [newEmployee, setNewEmployee] = useState({
        name: '',
        email: '',
        phone: '',
        birth_date: '',
        address: '',
        education_level: '',
        major: '',
        role: '',
        division_id: ''
    });
    const [divisions, setDivisions] = useState([]);

    useEffect(() => {
        fetchEmployees();
        fetchDivisions();
    }, []);

    const fetchEmployees = async () => {
        try {
            const response = await axios.get('/employees.php');
            if (response.data.success) {
                setEmployees(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching employees:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchDivisions = async () => {
        try {
            const response = await axios.get('/divisions.php');
            console.log('Divisions response:', response.data); // Debug log
            if (response.data.success) {
                setDivisions(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching divisions:', error);
        }
    };

    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            console.log('Adding employee with data:', newEmployee);

            const formData = new FormData();
            formData.append('action', 'create');
            Object.keys(newEmployee).forEach(key => {
                formData.append(key, newEmployee[key]);
                console.log(`FormData: ${key} = ${newEmployee[key]}`);
            });

            console.log('Sending request to /employees.php');
            const response = await axios.post('/employees.php', formData);
            console.log('Response received:', response.data);

            if (response.data.success) {
                alert('Karyawan berhasil ditambahkan!');
                setShowAddModal(false);
                setNewEmployee({
                    name: '',
                    email: '',
                    phone: '',
                    birth_date: '',
                    address: '',
                    education_level: '',
                    major: '',
                    role: '',
                    division_id: ''
                });
                fetchEmployees();
            } else {
                console.error('Server error:', response.data);
                alert('Error: ' + (response.data.error || 'Unknown error'));
            }
        } catch (error) {
            console.error('Error adding employee:', error);
            console.error('Error response:', error.response?.data);
            alert('Terjadi kesalahan saat menambah karyawan: ' + (error.response?.data?.error || error.message));
        }
    };

    const getStatusBadge = (status) => {
        switch (status) {
            case 'Active':
                return 'status-active';
            case 'Probation':
                return 'status-probation';
            default:
                return 'status-inactive';
        }
    };

    const getStatusText = (status) => {
        switch (status) {
            case 'Active':
                return 'Aktif';
            case 'Probation':
                return 'Probation';
            case 'Inactive':
                return 'Resign';
            default:
                return 'Tidak Aktif';
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'active':
                return 'status-active-color';
            case 'probation':
                return 'status-probation-color';
            case 'resigned':
            case 'terminated':
                return 'status-inactive-color';
            default:
                return 'status-inactive-color';
        }
    };

    if (loading) {
        return (
            <div className="loading-spinner">
                <div className="spinner"></div>
            </div>
        );
    }

    return (
        <div className="employees-page">
            <div className="container-fluid">
                {/* Active Employees Section */}
                <div className="section-header">
                    <h3>Karyawan Profile</h3>
                    <p className="text-muted">Total Talents {employees.filter(emp => emp.status === 'active' || emp.status === 'probation').length}</p>
                </div>

                <div className="employees-grid">
                    {/* Add Employee Card */}
                    <div className="employee-card add-employee-card" onClick={() => setShowAddModal(true)}>
                        <div className="add-icon">
                            <i className="fas fa-plus"></i>
                        </div>
                    </div>

                    {/* Active Employee Cards */}
                    {employees.filter(emp => emp.status === 'active' || emp.status === 'probation').map(employee => (
                        <div key={employee.eid} className="employee-card">
                            <div className="employee-avatar">
                                <img 
                                    src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=60`} 
                                    alt={employee.name}
                                />
                            </div>

                            <div className="employee-info">
                                <h5 className="employee-name">{employee.name}</h5>
                                <p className="employee-years">{employee.age ? `${employee.age} Tahun` : 'N/A Tahun'}</p>
                                <p className="employee-division">{employee.division_name}</p>

                                {/* Status Dots */}
                                <div className="status-dots">
                                    <span className={`dot ${employee.status === 'active' ? 'active' : ''}`}></span>
                                    <span className={`dot ${employee.status === 'probation' ? 'probation' : ''}`}></span>
                                    <span className={`dot ${employee.status === 'resigned' || employee.status === 'terminated' ? 'resigned' : ''}`}></span>
                                </div>
                            </div>

                            <div className="employee-actions">
                                <button 
                                    className="btn btn-primary btn-sm"
                                    onClick={() => Link(`/employees/${employee.eid}`)}
                                >
                                    Details
                                </button>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Resigned Employees Section */}
                {employees.filter(emp => emp.status === 'resigned' || emp.status === 'terminated').length > 0 && (
                    <>
                        <div className="section-header mt-5">
                            <h3>Karyawan Resign</h3>
                            <p className="text-muted">Total Karyawan {employees.filter(emp => emp.status === 'resigned' || emp.status === 'terminated').length}</p>
                        </div>

                        <div className="employees-grid">
                            {employees.filter(emp => emp.status === 'resigned' || emp.status === 'terminated').map(employee => (
                                <div key={employee.eid} className="employee-card resigned-card">
                                    <div className="employee-avatar">
                                        <img 
                                            src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=60`} 
                                            alt={employee.name}
                                        />
                                    </div>

                                    <div className="employee-info">
                                        <h5 className="employee-name">{employee.name}</h5>
                                        <p className="employee-years">{employee.age ? `${employee.age} Tahun` : 'N/A Tahun'}</p>
                                        <p className="employee-division">{employee.division_name}</p>

                                        {/* Status Dots */}
                                        <div className="status-dots">
                                            <span className="dot"></span>
                                            <span className="dot"></span>
                                            <span className="dot resigned"></span>
                                        </div>
                                    </div>

                                    <div className="employee-actions">
                                        <button 
                                            className="btn btn-secondary btn-sm"
                                            onClick={() => Link(`/employees/${employee.eid}`)}
                                        >
                                            Update
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </>
                )}
            </div>

            {/* Add Employee Modal */}
            {showAddModal && (
                <div className="modal fade show" style={{ display: 'block' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Tambah Karyawan Baru</h5>
                                <button type="button" className="btn-close" onClick={() => setShowAddModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleAddEmployee}>
                                    <div className="mb-3">
                                        <label className="form-label">Nama Lengkap</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={newEmployee.name}
                                            onChange={(e) => setNewEmployee({...newEmployee, name: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Email</label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            value={newEmployee.email}
                                            onChange={(e) => setNewEmployee({...newEmployee, email: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Nomor Telepon</label>
                                        <input
                                            type="tel"
                                            className="form-control"
                                            value={newEmployee.phone}
                                            onChange={(e) => setNewEmployee({...newEmployee, phone: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Tanggal Lahir</label>
                                        <input
                                            type="date"
                                            className="form-control"
                                            value={newEmployee.birth_date}
                                            onChange={(e) => setNewEmployee({...newEmployee, birth_date: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Alamat</label>
                                        <textarea
                                            className="form-control"
                                            rows="3"
                                            value={newEmployee.address}
                                            onChange={(e) => setNewEmployee({...newEmployee, address: e.target.value})}
                                        ></textarea>
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Tingkat Pendidikan</label>
                                        <select
                                            className="form-control"
                                            value={newEmployee.education_level}
                                            onChange={(e) => setNewEmployee({...newEmployee, education_level: e.target.value})}
                                            required
                                        >
                                            <option value="">Pilih Tingkat Pendidikan</option>
                                            <option value="SMA/K">SMA/K</option>
                                            <option value="D3">D3</option>
                                            <option value="S1">S1</option>
                                            <option value="S2">S2</option>
                                            <option value="S3">S3</option>
                                        </select>
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Jurusan</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={newEmployee.major}
                                            onChange={(e) => setNewEmployee({...newEmployee, major: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Role</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={newEmployee.role}
                                            onChange={(e) => setNewEmployee({...newEmployee, role: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Divisi</label>
                                        <select
                                            className="form-control"
                                            value={newEmployee.division_id}
                                            onChange={(e) => setNewEmployee({...newEmployee, division_id: e.target.value})}
                                            required
                                        >
                                            <option value="">Pilih Divisi</option>
                                            {divisions.map(division => (
                                                <option key={division.division_id} value={division.division_id}>
                                                    {division.division_name}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                    <div className="modal-footer">
                                        <button type="button" className="btn btn-secondary" onClick={() => setShowAddModal(false)}>
                                            Batal
                                        </button>
                                        <button type="submit" className="btn btn-primary">
                                            Simpan
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Employees;
