import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../App';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [divisions, setDivisions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'hr',
    division_id: ''
  });
  const [submitting, setSubmitting] = useState(false);
  
  const { user } = useAuth();

  // Redirect if not admin
  useEffect(() => {
    if (user.role !== 'admin') {
      window.location.href = '/dashboard';
    }
  }, [user]);

  useEffect(() => {
    fetchUsers();
    fetchDivisions();
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      // This would need a users API endpoint
      setUsers([
        { user_id: 1, username: 'admin', email: 'admin@contractrec.com', role: 'admin', division_name: null },
        { user_id: 2, username: 'hr1', email: 'hr@company.com', role: 'hr', division_name: 'HR' },
        { user_id: 3, username: 'manager1', email: 'manager@company.com', role: 'manager', division_name: 'IT' },
      ]);
    } catch (error) {
      setError('Terjadi kesalahan saat memuat data');
      console.error('Users error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchDivisions = async () => {
    try {
      setDivisions([
        { division_id: 1, division_name: 'IT' },
        { division_id: 2, division_name: 'HR' },
        { division_id: 3, division_name: 'Finance' },
        { division_id: 4, division_name: 'Marketing' },
        { division_id: 5, division_name: 'Operations' }
      ]);
    } catch (error) {
      console.error('Divisions error:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('action', 'register');
      formDataToSend.append('username', formData.username);
      formDataToSend.append('email', formData.email);
      formDataToSend.append('password', formData.password);
      formDataToSend.append('role', formData.role);
      if (formData.division_id) {
        formDataToSend.append('division_id', formData.division_id);
      }

      const response = await axios.post('/auth.php', formDataToSend);

      if (response.data.success) {
        setShowModal(false);
        setFormData({
          username: '',
          email: '',
          password: '',
          role: 'hr',
          division_id: ''
        });
        fetchUsers();
        alert('User berhasil ditambahkan');
      } else {
        alert(response.data.error || 'Gagal menambahkan user');
      }
    } catch (error) {
      alert('Terjadi kesalahan saat menambahkan user');
      console.error('Add user error:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const getRoleBadge = (role) => {
    const badges = {
      'admin': <span className="badge bg-danger">Admin</span>,
      'hr': <span className="badge bg-primary">HR</span>,
      'manager': <span className="badge bg-success">Manager</span>
    };
    
    return badges[role] || <span className="badge bg-secondary">{role}</span>;
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
          <div className="text-center">
            <div className="spinner-border text-primary mb-3" style={{ width: '3rem', height: '3rem' }} role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="text-muted">Memuat data users...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Header */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <h2 className="mb-1">
                <i className="fas fa-users-cog me-2 text-primary"></i>
                Kelola Users
              </h2>
              <p className="text-muted mb-0">Tambah dan kelola pengguna sistem</p>
            </div>
            <button
              className="btn btn-primary"
              onClick={() => setShowModal(true)}
            >
              <i className="fas fa-plus me-2"></i>
              Tambah User
            </button>
          </div>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          <i className="fas fa-exclamation-triangle me-2"></i>
          {error}
        </div>
      )}

      {/* Users Table */}
      <div className="dashboard-card">
        <div className="table-responsive">
          <table className="table">
            <thead>
              <tr>
                <th>Username</th>
                <th>Email</th>
                <th>Role</th>
                <th>Divisi</th>
                <th>Aksi</th>
              </tr>
            </thead>
            <tbody>
              {users.length === 0 ? (
                <tr>
                  <td colSpan="5" className="text-center py-4">
                    <i className="fas fa-users fa-3x text-muted mb-3 d-block"></i>
                    <p className="text-muted">Tidak ada data users</p>
                  </td>
                </tr>
              ) : (
                users.map((userItem) => (
                  <tr key={userItem.user_id}>
                    <td>
                      <div className="d-flex align-items-center">
                        <div className="employee-avatar me-2" style={{ width: '40px', height: '40px', fontSize: '1rem' }}>
                          {userItem.username.charAt(0).toUpperCase()}
                        </div>
                        <div>
                          <div className="fw-semibold">{userItem.username}</div>
                        </div>
                      </div>
                    </td>
                    <td>{userItem.email}</td>
                    <td>{getRoleBadge(userItem.role)}</td>
                    <td>{userItem.division_name || '-'}</td>
                    <td>
                      <div className="btn-group" role="group">
                        <button className="btn btn-outline-warning btn-sm">
                          <i className="fas fa-edit"></i>
                        </button>
                        {userItem.username !== 'admin' && (
                          <button className="btn btn-outline-danger btn-sm">
                            <i className="fas fa-trash"></i>
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Add User Modal */}
      {showModal && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  <i className="fas fa-user-plus me-2"></i>
                  Tambah User Baru
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">Username *</label>
                    <input
                      type="text"
                      className="form-control"
                      name="username"
                      value={formData.username}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Email *</label>
                    <input
                      type="email"
                      className="form-control"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Password *</label>
                    <input
                      type="password"
                      className="form-control"
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Role *</label>
                    <select
                      className="form-select"
                      name="role"
                      value={formData.role}
                      onChange={handleChange}
                      required
                    >
                      <option value="hr">HR</option>
                      <option value="manager">Manager</option>
                      <option value="admin">Admin</option>
                    </select>
                  </div>

                  {formData.role === 'manager' && (
                    <div className="mb-3">
                      <label className="form-label">Divisi *</label>
                      <select
                        className="form-select"
                        name="division_id"
                        value={formData.division_id}
                        onChange={handleChange}
                        required
                      >
                        <option value="">Pilih Divisi</option>
                        {divisions.map(division => (
                          <option key={division.division_id} value={division.division_id}>
                            {division.division_name}
                          </option>
                        ))}
                      </select>
                    </div>
                  )}
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowModal(false)}
                    disabled={submitting}
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={submitting}
                  >
                    {submitting ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Menyimpan...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-save me-2"></i>
                        Simpan
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Users;
