import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../App';
import axios from 'axios';

const Users = () => {
    const { user } = useContext(AuthContext);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost/web_srk_BI/backend/api/auth.php?action=users', {
                withCredentials: true
            });
            
            if (response.data.success) {
                setUsers(response.data.data);
            } else {
                setError('Gagal memuat data users');
            }
        } catch (error) {
            console.error('Error fetching users:', error);
            setError('Gagal memuat data users');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="container mt-5">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="users-container">
            <div className="users-header">
                <button className="btn btn-back">
                    <i className="fas fa-arrow-left me-2"></i>
                    Home
                </button>
                <button className="btn btn-danger">
                    Keluar
                </button>
            </div>

            <div className="users-content">
                <h2 className="page-title">Users</h2>

                {error && (
                    <div className="alert alert-danger">
                        {error}
                    </div>
                )}

                <div className="users-grid">
                    {/* Human Resource Card */}
                    <div className="user-card">
                        <div className="user-logo">
                            <i className="fas fa-users"></i>
                        </div>
                        <div className="user-info">
                            <h4 className="user-title">Human Resource</h4>
                            <p className="user-location">Baleoj | Jakarta, Indonesia</p>
                            <p className="user-salary">Range Gaji: -</p>
                        </div>
                        <div className="user-actions">
                            <button className="btn btn-primary btn-sm">
                                Details
                            </button>
                        </div>
                    </div>

                    {/* Add User Card */}
                    <div className="user-card add-user-card" onClick={() => setShowModal(true)}>
                        <div className="add-user-icon">
                            <i className="fas fa-plus"></i>
                        </div>
                    </div>
                </div>

                {users.length === 0 && !loading && !error && (
                    <div className="text-center py-5">
                        <i className="fas fa-user-plus fa-3x text-muted mb-3"></i>
                        <h5 className="text-muted">Belum ada users</h5>
                        <p className="text-muted">Klik tombol tambah untuk menambahkan user baru</p>
                    </div>
                )}
            </div>

            {/* Footer */}
            <div className="footer-links">
                <a href="#">Tentang Kami</a>
                <a href="#">Kontak</a>
                <a href="#">Kebijakan Privasi</a>
                <a href="#">Ketentuan Layanan</a>
            </div>

            <div className="footer-copyright">
                ©2025 CareerMatch. Semua hak cipta dilindungi.
            </div>

            {/* Add User Modal */}
            {showModal && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Tambah User</h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => setShowModal(false)}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <form>
                                    <div className="mb-3">
                                        <label className="form-label">Username</label>
                                        <input type="text" className="form-control" />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Password</label>
                                        <input type="password" className="form-control" />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Role</label>
                                        <select className="form-control">
                                            <option value="hr">HR</option>
                                            <option value="manager">Manager</option>
                                            <option value="admin">Admin</option>
                                        </select>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button 
                                    type="button" 
                                    className="btn btn-secondary"
                                    onClick={() => setShowModal(false)}
                                >
                                    Batal
                                </button>
                                <button 
                                    type="button" 
                                    className="btn btn-primary"
                                >
                                    Simpan
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
            {showModal && <div className="modal-backdrop fade show"></div>}
        </div>
    );
};

export default Users;
