import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const EmployeeDetail = () => {
  const { eid } = useParams();
  const [employee, setEmployee] = useState(null);
  const [contracts, setContracts] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [systemRecommendation, setSystemRecommendation] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('info');
  const [showRecommendModal, setShowRecommendModal] = useState(false);
  const [recommendForm, setRecommendForm] = useState({
    recommendation_type: 'extend',
    recommended_duration: 6,
    reason: ''
  });
  const [submitting, setSubmitting] = useState(false);
  
  const { user } = useAuth();

  useEffect(() => {
    if (eid) {
      fetchEmployeeDetail();
      fetchSystemRecommendation();
    }
  }, [eid]);

  const fetchEmployeeDetail = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/employees.php?action=detail&eid=${eid}`);
      
      if (response.data.success) {
        setEmployee(response.data.employee);
        setContracts(response.data.contracts || []);
        setRecommendations(response.data.recommendations || []);
      } else {
        setError('Gagal memuat detail karyawan');
      }
    } catch (error) {
      setError('Terjadi kesalahan saat memuat data');
      console.error('Employee detail error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchSystemRecommendation = async () => {
    try {
      const response = await axios.get(`/contracts.php?action=recommendation&eid=${eid}`);
      
      if (response.data.success) {
        setSystemRecommendation(response.data.recommendation);
      }
    } catch (error) {
      console.error('System recommendation error:', error);
    }
  };

  const handleRecommendSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const response = await axios.post('/employees.php', {
        action: 'recommend',
        eid: parseInt(eid),
        ...recommendForm,
        system_recommendation: systemRecommendation ? JSON.stringify(systemRecommendation) : null
      });

      if (response.data.success) {
        setShowRecommendModal(false);
        setRecommendForm({
          recommendation_type: 'extend',
          recommended_duration: 6,
          reason: ''
        });
        fetchEmployeeDetail();
        alert('Rekomendasi berhasil dikirim');
      } else {
        alert(response.data.error || 'Gagal mengirim rekomendasi');
      }
    } catch (error) {
      alert('Terjadi kesalahan saat mengirim rekomendasi');
      console.error('Recommend error:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const getContractBadge = (contractType) => {
    const badges = {
      'probation': <span className="badge bg-warning">Probation</span>,
      '1': <span className="badge bg-primary">Kontrak 1</span>,
      '2': <span className="badge bg-success">Kontrak 2</span>,
      '3': <span className="badge bg-info">Kontrak 3</span>,
      'permanent': <span className="badge bg-dark">Permanent</span>
    };
    
    return badges[contractType] || <span className="badge bg-secondary">{contractType}</span>;
  };

  const getStatusBadge = (status) => {
    const badges = {
      'active': <span className="badge bg-success">Aktif</span>,
      'completed': <span className="badge bg-info">Selesai</span>,
      'terminated': <span className="badge bg-danger">Diberhentikan</span>,
      'extended': <span className="badge bg-warning">Diperpanjang</span>
    };
    
    return badges[status] || <span className="badge bg-secondary">{status}</span>;
  };

  const getConfidenceColor = (level) => {
    switch (level) {
      case 'Very High': return 'success';
      case 'High': return 'primary';
      case 'Medium': return 'warning';
      case 'Low': return 'danger';
      default: return 'secondary';
    }
  };

  const getConfidenceWidth = (level) => {
    switch (level) {
      case 'Very High': return '95%';
      case 'High': return '80%';
      case 'Medium': return '60%';
      case 'Low': return '30%';
      default: return '50%';
    }
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
          <div className="text-center">
            <div className="spinner-border text-primary mb-3" style={{ width: '3rem', height: '3rem' }} role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="text-muted">Memuat detail karyawan...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error || !employee) {
    return (
      <div className="container-fluid py-4">
        <div className="alert alert-danger" role="alert">
          <i className="fas fa-exclamation-triangle me-2"></i>
          {error || 'Karyawan tidak ditemukan'}
        </div>
        <Link to="/employees" className="btn btn-secondary">
          <i className="fas fa-arrow-left me-2"></i>
          Kembali ke Daftar Karyawan
        </Link>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Header */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <Link to="/employees" className="btn btn-outline-secondary btn-sm mb-2">
                <i className="fas fa-arrow-left me-1"></i>
                Kembali
              </Link>
              <h2 className="mb-1">
                <i className="fas fa-user me-2 text-primary"></i>
                {employee.name}
              </h2>
              <p className="text-muted mb-0">{employee.role} - {employee.division_name || 'Tidak ada divisi'}</p>
            </div>
            {user.role === 'manager' && employee.status === 'active' && (
              <button
                className="btn btn-success"
                onClick={() => setShowRecommendModal(true)}
              >
                <i className="fas fa-thumbs-up me-2"></i>
                Beri Rekomendasi
              </button>
            )}
          </div>
        </div>
      </div>

      <div className="row">
        {/* Left Column - Employee Info */}
        <div className="col-lg-4 mb-4">
          <div className="dashboard-card">
            <div className="text-center mb-4">
              <div className="employee-avatar mx-auto mb-3" style={{ width: '100px', height: '100px', fontSize: '2.5rem' }}>
                {employee.name.charAt(0).toUpperCase()}
              </div>
              <h4 className="mb-1">{employee.name}</h4>
              <p className="text-muted">{employee.role}</p>
              <div className="d-flex justify-content-center gap-2">
                {getStatusBadge(employee.status)}
                {contracts.length > 0 && getContractBadge(contracts[0].type)}
              </div>
            </div>

            <div className="employee-details">
              <div className="detail-item mb-3">
                <i className="fas fa-envelope text-primary me-2"></i>
                <strong>Email:</strong> {employee.email}
              </div>
              
              {employee.phone && (
                <div className="detail-item mb-3">
                  <i className="fas fa-phone text-primary me-2"></i>
                  <strong>Telepon:</strong> {employee.phone}
                </div>
              )}
              
              <div className="detail-item mb-3">
                <i className="fas fa-calendar text-primary me-2"></i>
                <strong>Bergabung:</strong> {new Date(employee.join_date).toLocaleDateString('id-ID')}
              </div>
              
              <div className="detail-item mb-3">
                <i className="fas fa-graduation-cap text-primary me-2"></i>
                <strong>Pendidikan:</strong> {employee.education_level}
              </div>
              
              <div className="detail-item mb-3">
                <i className="fas fa-book text-primary me-2"></i>
                <strong>Jurusan:</strong> {employee.major}
              </div>
              
              {employee.last_education_place && (
                <div className="detail-item mb-3">
                  <i className="fas fa-university text-primary me-2"></i>
                  <strong>Institusi:</strong> {employee.last_education_place}
                </div>
              )}
              
              <div className="detail-item mb-3">
                <i className="fas fa-building text-primary me-2"></i>
                <strong>Divisi:</strong> {employee.division_name || 'Tidak ada divisi'}
              </div>
              
              {employee.designation && (
                <div className="detail-item mb-3">
                  <i className="fas fa-id-badge text-primary me-2"></i>
                  <strong>Designation:</strong> {employee.designation}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Right Column - Tabs */}
        <div className="col-lg-8">
          {/* System Recommendation */}
          {systemRecommendation && (
            <div className="recommendation-card mb-4">
              <h4>
                <i className="fas fa-robot me-2"></i>
                Rekomendasi Sistem
              </h4>
              
              <div className="recommendation-type">
                {systemRecommendation.recommended_contract}
                {systemRecommendation.recommended_duration && (
                  <span className="ms-2">({systemRecommendation.recommended_duration} bulan)</span>
                )}
              </div>
              
              <div className="confidence-level">
                <span>Tingkat Keyakinan: {systemRecommendation.confidence_level}</span>
                <div className="confidence-bar">
                  <div 
                    className="confidence-fill" 
                    style={{ width: getConfidenceWidth(systemRecommendation.confidence_level) }}
                  ></div>
                </div>
                <span>{systemRecommendation.success_probability}%</span>
              </div>

              {systemRecommendation.reasons && systemRecommendation.reasons.length > 0 && (
                <div className="recommendation-reasons">
                  <h6>Alasan Rekomendasi:</h6>
                  <ul>
                    {systemRecommendation.reasons.map((reason, index) => (
                      <li key={index}>{reason}</li>
                    ))}
                  </ul>
                </div>
              )}

              {systemRecommendation.risk_factors && systemRecommendation.risk_factors.length > 0 && (
                <div className="mt-3">
                  <h6>Faktor Risiko:</h6>
                  <ul className="list-unstyled">
                    {systemRecommendation.risk_factors.map((risk, index) => (
                      <li key={index} className="text-warning">
                        <i className="fas fa-exclamation-triangle me-2"></i>
                        {risk}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}

          {/* Tabs */}
          <div className="dashboard-card">
            <ul className="nav nav-tabs" id="employeeTabs" role="tablist">
              <li className="nav-item" role="presentation">
                <button
                  className={`nav-link ${activeTab === 'info' ? 'active' : ''}`}
                  onClick={() => setActiveTab('info')}
                  type="button"
                >
                  <i className="fas fa-info-circle me-1"></i>
                  Info Detail
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className={`nav-link ${activeTab === 'contracts' ? 'active' : ''}`}
                  onClick={() => setActiveTab('contracts')}
                  type="button"
                >
                  <i className="fas fa-file-contract me-1"></i>
                  Riwayat Kontrak
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className={`nav-link ${activeTab === 'recommendations' ? 'active' : ''}`}
                  onClick={() => setActiveTab('recommendations')}
                  type="button"
                >
                  <i className="fas fa-thumbs-up me-1"></i>
                  Rekomendasi
                </button>
              </li>
            </ul>

            <div className="tab-content mt-4">
              {/* Info Tab */}
              {activeTab === 'info' && (
                <div className="tab-pane fade show active">
                  <div className="row">
                    <div className="col-md-6">
                      <h6 className="mb-3">Informasi Personal</h6>
                      
                      {employee.birth_date && (
                        <div className="detail-item mb-2">
                          <strong>Tanggal Lahir:</strong> {new Date(employee.birth_date).toLocaleDateString('id-ID')}
                        </div>
                      )}
                      
                      {employee.address && (
                        <div className="detail-item mb-2">
                          <strong>Alamat:</strong> {employee.address}
                        </div>
                      )}
                      
                      <div className="detail-item mb-2">
                        <strong>Status:</strong> {getStatusBadge(employee.status)}
                      </div>
                      
                      {employee.resign_date && (
                        <div className="detail-item mb-2">
                          <strong>Tanggal Resign:</strong> {new Date(employee.resign_date).toLocaleDateString('id-ID')}
                        </div>
                      )}
                    </div>
                    
                    <div className="col-md-6">
                      <h6 className="mb-3">Informasi Karir</h6>
                      
                      <div className="detail-item mb-2">
                        <strong>Masa Kerja:</strong> 
                        {(() => {
                          const joinDate = new Date(employee.join_date);
                          const now = new Date();
                          const diffTime = Math.abs(now - joinDate);
                          const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                          const years = Math.floor(diffDays / 365);
                          const months = Math.floor((diffDays % 365) / 30);
                          return ` ${years} tahun ${months} bulan`;
                        })()}
                      </div>
                      
                      <div className="detail-item mb-2">
                        <strong>Jumlah Kontrak:</strong> {contracts.length}
                      </div>
                      
                      <div className="detail-item mb-2">
                        <strong>Kontrak Aktif:</strong> 
                        {contracts.find(c => c.status === 'active') ? 
                          getContractBadge(contracts.find(c => c.status === 'active').type) : 
                          <span className="text-muted"> Tidak ada</span>
                        }
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Contracts Tab */}
              {activeTab === 'contracts' && (
                <div className="tab-pane fade show active">
                  <h6 className="mb-3">Riwayat Kontrak</h6>
                  
                  {contracts.length === 0 ? (
                    <div className="text-center py-4">
                      <i className="fas fa-file-contract fa-3x text-muted mb-3"></i>
                      <p className="text-muted">Belum ada riwayat kontrak</p>
                    </div>
                  ) : (
                    <div className="table-responsive">
                      <table className="table">
                        <thead>
                          <tr>
                            <th>Tipe Kontrak</th>
                            <th>Tanggal Mulai</th>
                            <th>Tanggal Berakhir</th>
                            <th>Status</th>
                            <th>Catatan</th>
                          </tr>
                        </thead>
                        <tbody>
                          {contracts.map((contract, index) => (
                            <tr key={index}>
                              <td>{getContractBadge(contract.type)}</td>
                              <td>{new Date(contract.start_date).toLocaleDateString('id-ID')}</td>
                              <td>
                                {contract.end_date ? 
                                  new Date(contract.end_date).toLocaleDateString('id-ID') : 
                                  'Tidak ada batas'
                                }
                              </td>
                              <td>{getStatusBadge(contract.status)}</td>
                              <td>{contract.notes || '-'}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                </div>
              )}

              {/* Recommendations Tab */}
              {activeTab === 'recommendations' && (
                <div className="tab-pane fade show active">
                  <h6 className="mb-3">Rekomendasi dari Tim</h6>
                  
                  {recommendations.length === 0 ? (
                    <div className="text-center py-4">
                      <i className="fas fa-thumbs-up fa-3x text-muted mb-3"></i>
                      <p className="text-muted">Belum ada rekomendasi</p>
                    </div>
                  ) : (
                    <div className="row">
                      {recommendations.map((recommendation, index) => (
                        <div key={index} className="col-12 mb-3">
                          <div className="card">
                            <div className="card-body">
                              <div className="d-flex justify-content-between align-items-start mb-2">
                                <h6 className="card-title mb-0">
                                  {recommendation.recommended_by_name}
                                </h6>
                                <small className="text-muted">
                                  {new Date(recommendation.created_at).toLocaleDateString('id-ID')}
                                </small>
                              </div>
                              
                              <div className="mb-2">
                                <span className="badge bg-primary me-2">
                                  {recommendation.recommendation_type}
                                </span>
                                {recommendation.recommended_duration && (
                                  <span className="badge bg-info">
                                    {recommendation.recommended_duration} bulan
                                  </span>
                                )}
                              </div>
                              
                              {recommendation.reason && (
                                <p className="card-text">{recommendation.reason}</p>
                              )}
                              
                              <div className="text-end">
                                <span className={`badge bg-${
                                  recommendation.status === 'approved' ? 'success' :
                                  recommendation.status === 'rejected' ? 'danger' : 'warning'
                                }`}>
                                  {recommendation.status}
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Recommendation Modal */}
      {showRecommendModal && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  <i className="fas fa-thumbs-up me-2"></i>
                  Beri Rekomendasi untuk {employee.name}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowRecommendModal(false)}
                ></button>
              </div>
              <form onSubmit={handleRecommendSubmit}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">Tipe Rekomendasi</label>
                    <select
                      className="form-select"
                      value={recommendForm.recommendation_type}
                      onChange={(e) => setRecommendForm({...recommendForm, recommendation_type: e.target.value})}
                      required
                    >
                      <option value="extend">Perpanjang Kontrak</option>
                      <option value="permanent">Jadikan Permanent</option>
                      <option value="terminate">Akhiri Kontrak</option>
                      <option value="review">Review Mendalam</option>
                    </select>
                  </div>

                  {(recommendForm.recommendation_type === 'extend' || recommendForm.recommendation_type === 'review') && (
                    <div className="mb-3">
                      <label className="form-label">Durasi (bulan)</label>
                      <input
                        type="number"
                        className="form-control"
                        value={recommendForm.recommended_duration}
                        onChange={(e) => setRecommendForm({...recommendForm, recommended_duration: parseInt(e.target.value)})}
                        min="1"
                        max="24"
                        required
                      />
                    </div>
                  )}

                  <div className="mb-3">
                    <label className="form-label">Alasan Rekomendasi</label>
                    <textarea
                      className="form-control"
                      rows="4"
                      value={recommendForm.reason}
                      onChange={(e) => setRecommendForm({...recommendForm, reason: e.target.value})}
                      placeholder="Jelaskan alasan rekomendasi Anda..."
                      required
                    ></textarea>
                  </div>

                  {systemRecommendation && (
                    <div className="alert alert-info">
                      <h6>Rekomendasi Sistem:</h6>
                      <p className="mb-0">
                        <strong>{systemRecommendation.recommended_contract}</strong>
                        {systemRecommendation.recommended_duration && (
                          <span> ({systemRecommendation.recommended_duration} bulan)</span>
                        )}
                        <br />
                        <small>Tingkat keyakinan: {systemRecommendation.confidence_level} ({systemRecommendation.success_probability}%)</small>
                      </p>
                    </div>
                  )}
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowRecommendModal(false)}
                    disabled={submitting}
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="btn btn-success"
                    disabled={submitting}
                  >
                    {submitting ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Mengirim...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-paper-plane me-2"></i>
                        Kirim Rekomendasi
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployeeDetail;
