import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const EmployeeDetail = () => {
    const { eid } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [employee, setEmployee] = useState(null);
    const [contracts, setContracts] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [activePanel, setActivePanel] = useState('recommendation');
    const [resignReason, setResignReason] = useState('');
    const [showResignForm, setShowResignForm] = useState(false);

    useEffect(() => {
        fetchEmployeeData();
    }, [eid]);

    const fetchEmployeeData = async () => {
        try {
            setLoading(true);

            // Fetch employee details dengan API yang benar
            const employeeResponse = await axios.get(`/employees.php?action=detail&eid=${eid}`);
            console.log('Employee detail response:', employeeResponse.data);

            if (employeeResponse.data.success) {
                setEmployee(employeeResponse.data.employee);
                setContracts(employeeResponse.data.contracts || []);
                setRecommendations(employeeResponse.data.recommendations || []);
            } else {
                console.error('Error fetching employee:', employeeResponse.data.error);
            }
        } catch (error) {
            console.error('Error fetching employee data:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleProbation = async () => {
        try {
            const formData = new FormData();
            formData.append('action', 'add_contract');
            formData.append('employee_id', eid);
            formData.append('contract_type', 'Probation');
            formData.append('start_date', new Date().toISOString().split('T')[0]);
            formData.append('end_date', new Date(Date.now() + 90 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]);

            const response = await axios.post('/contracts.php', formData);
            if (response.data.success) {
                alert('Kontrak probation berhasil ditambahkan!');
                fetchEmployeeData();
            } else {
                alert('Error: ' + response.data.error);
            }
        } catch (error) {
            console.error('Error adding probation contract:', error);
            alert('Terjadi kesalahan saat menambah kontrak probation');
        }
    };

    const handleResign = async () => {
        if (!resignReason.trim()) {
            alert('Mohon isi alasan resign');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('action', 'update');
            formData.append('eid', eid);
            formData.append('status', 'Inactive');
            formData.append('resign_reason', resignReason);

            const response = await axios.post('/employees.php', formData);
            if (response.data.success) {
                alert('Status karyawan berhasil diupdate menjadi resign');
                fetchEmployeeData();
                setShowResignForm(false);
                setResignReason('');
            } else {
                alert('Error: ' + response.data.error);
            }
        } catch (error) {
            console.error('Error updating employee status:', error);
            alert('Terjadi kesalahan saat mengupdate status karyawan');
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'N/A';
        return new Date(dateString).toLocaleDateString('id-ID');
    };

    const getContractTypeColor = (type) => {
        switch (type) {
            case 'Probation': return 'warning';
            case 'Kontrak 1': return 'primary';
            case 'Kontrak 2': return 'info';
            case 'Kontrak 3': return 'secondary';
            case 'Permanent': return 'success';
            default: return 'secondary';
        }
    };

    if (loading) {
        return (
            <div className="loading-spinner">
                <div className="spinner"></div>
            </div>
        );
    }

    if (!employee) {
        return (
            <div className="main-container">
                <div className="alert alert-warning">
                    Karyawan tidak ditemukan
                </div>
            </div>
        );
    }

    return (
        <div className="main-container">
            {/* Header dengan tombol aksi */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <button 
                        className="btn btn-outline-secondary me-3" 
                        onClick={() => navigate('/employees')}
                    >
                        ← Kembali
                    </button>
                    <h1 className="d-inline">Karyawan</h1>
                </div>
                <div className="action-buttons">
                    <button 
                        className="btn-action btn-probation"
                        onClick={handleProbation}
                        disabled={employee.status === 'Inactive'}
                    >
                        Probation
                    </button>
                    <button 
                        className="btn-action btn-resign"
                        onClick={() => setShowResignForm(true)}
                        disabled={employee.status === 'Inactive'}
                    >
                        Resign
                    </button>
                </div>
            </div>

            {/* Three Column Layout */}
            <div className="three-column-layout">

                {/* Left Column - Employee Profile */}
                <div className="employee-profile-card">
                    <div className="employee-info">
                        <img 
                            src={`https://via.placeholder.com/120x120/007bff/white?text=${employee.name?.charAt(0) || 'N'}`}
                            alt={employee.name}
                            className="employee-avatar"
                        />
                        <h3>{employee.name}</h3>

                        <div className="info-item">
                            <strong>Tanggal Lahir:</strong><br />
                            {formatDate(employee.birth_date)}
                        </div>

                        <div className="info-item">
                            <strong>EID:</strong><br />
                            {employee.eid}
                        </div>

                        <div className="info-item">
                            <strong>No HP:</strong><br />
                            {employee.phone || 'N/A'}
                        </div>

                        <div className="info-item">
                            <strong>Email:</strong><br />
                            {employee.email || 'N/A'}
                        </div>

                        <div className="info-item">
                            <strong>Alamat:</strong><br />
                            {employee.address || 'N/A'}
                        </div>
                    </div>

                    <div className="cv-section">
                        <div className="cv-placeholder">
                            <strong>CV</strong><br />
                            <small>Dokumen belum tersedia</small>
                        </div>
                    </div>
                </div>

                {/* Center Column - Employee Data */}
                <div className="employee-data-card">
                    <div className="card-header">
                        <h2>Data Karyawan</h2>
                    </div>

                    <div className="data-field">
                        <strong>Nama Department:</strong><br />
                        {employee.division_name || 'N/A'}
                    </div>

                    <div className="data-field">
                        <strong>Role:</strong><br />
                        {employee.role || 'N/A'}
                    </div>

                    <div className="data-field">
                        <strong>Jurusan:</strong><br />
                        {employee.major || 'N/A'}
                    </div>

                    <div className="data-field">
                        <strong>Tingkat Pendidikan:</strong><br />
                        {employee.education_level || 'N/A'}
                    </div>

                    <div className="data-field">
                        <strong>Status Karyawan:</strong><br />
                        <span className={`status-badge-modern ${getStatusBadge(employee.status)}`}>
                            {getStatusText(employee.status)}
                        </span>
                    </div>

                    <div className="data-field">
                        <strong>Tanggal Bergabung:</strong><br />
                        {formatDate(employee.join_date)}
                    </div>

                    {employee.resign_date && (
                        <div className="data-field">
                            <strong>Tanggal Resign:</strong><br />
                            {formatDate(employee.resign_date)}
                        </div>
                    )}
                </div>

                {/* Right Column - Recommendations/Actions */}
                <div className="recommendation-card">
                    {/* Tab Navigation */}
                    <div className="d-flex mb-3">
                        <button 
                            className={`btn btn-sm me-2 ${activePanel === 'recommendation' ? 'btn-primary' : 'btn-outline-primary'}`}
                            onClick={() => setActivePanel('recommendation')}
                        >
                            Kontrak Recommendation
                        </button>
                        <button 
                            className={`btn btn-sm me-2 ${activePanel === 'history' ? 'btn-primary' : 'btn-outline-primary'}`}
                            onClick={() => setActivePanel('history')}
                        >
                            Riwayat Kontrak
                        </button>
                        <button 
                            className={`btn btn-sm ${activePanel === 'resign' ? 'btn-primary' : 'btn-outline-primary'}`}
                            onClick={() => setActivePanel('resign')}
                        >
                            Alasan Resign
                        </button>
                    </div>

                    {/* Panel Content */}
                    {activePanel === 'recommendation' && (
                        <div>
                            <h3>Kontrak Recommendation</h3>
                            {recommendations.length > 0 ? (
                                recommendations.map((rec, index) => (
                                    <div key={index} className="recommendation-item">
                                        <strong>Contract {rec.recommended_type}:</strong> {rec.recommended_type}
                                        <br />
                                        <small>Confidence: {rec.confidence_level}</small>
                                        <br />
                                        <small>Success Rate: {rec.success_probability}%</small>
                                    </div>
                                ))
                            ) : (
                                <div className="text-center text-muted">
                                    Tidak ada rekomendasi tersedia
                                </div>
                            )}
                        </div>
                    )}

                    {activePanel === 'history' && (
                        <div>
                            <h3>Riwayat Kontrak</h3>
                            {contracts.length > 0 ? (
                                <div>
                                    {contracts.map((contract, index) => (
                                        <div key={index} className="contract-history-item">
                                            <strong>Role:</strong> {contract.contract_type || 'N/A'}<br />
                                            <strong>Tipe Kontrak:</strong> {contract.contract_type || 'N/A'}<br />
                                            <strong>Tanggal Mulai Kontrak:</strong> {formatDate(contract.start_date)}<br />
                                            <strong>Tanggal Akhir Kontrak:</strong> {formatDate(contract.end_date)}
                                        </div>
                                    ))}
                                </div>
                            ) : (
                                <div className="text-center text-muted">
                                    Belum ada riwayat kontrak
                                </div>
                            )}
                        </div>
                    )}

                    {activePanel === 'resign' && (
                        <div>
                            <h3>Alasan Resign</h3>
                            {showResignForm ? (
                                <div>
                                    <textarea 
                                        className="form-control mb-3"
                                        rows="4"
                                        placeholder="Masukkan alasan resign..."
                                        value={resignReason}
                                        onChange={(e) => setResignReason(e.target.value)}
                                    />
                                    <div className="d-flex gap-2">
                                        <button 
                                            className="btn btn-danger flex-fill"
                                            onClick={handleResign}
                                        >
                                            Send
                                        </button>
                                        <button 
                                            className="btn btn-secondary"
                                            onClick={() => {
                                                setShowResignForm(false);
                                                setResignReason('');
                                            }}
                                        >
                                            Batal
                                        </button>
                                    </div>
                                </div>
                            ) : (
                                <div className="text-center">
                                    {employee.resign_reason ? (
                                        <div className="alert alert-info">
                                            <strong>Alasan Resign:</strong><br />
                                            {employee.resign_reason}
                                        </div>
                                    ) : (
                                        <div className="text-muted">
                                            <p>Detail</p>
                                            <button 
                                                className="btn btn-primary mt-3"
                                                onClick={() => setShowResignForm(true)}
                                            >
                                                Send
                                            </button>
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </div>

            {/* Footer */}
            <div className="footer">
                <div className="footer-links">
                    <a href="#">Tentang Kami</a>
                    <a href="#">Kontak</a>
                    <a href="#">Kebijakan Privasi</a>
                    <a href="#">Ketentuan Layanan</a>
                </div>
                <p>©2025 CareerMatch. Semua hak cipta dilindungi.</p>
            </div>
        </div>
    );
};

export default EmployeeDetail;
