import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../App';
import axios from 'axios';

const EmployeeDetail = () => {
    const { eid } = useParams();
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const [employee, setEmployee] = useState(null);
    const [recommendation, setRecommendation] = useState(null);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState('data');
    const [showResignModal, setShowResignModal] = useState(false);
    const [resignReason, setResignReason] = useState('');

    useEffect(() => {
        fetchEmployeeData();
        fetchRecommendation();
    }, [eid]);

    const fetchEmployeeData = async () => {
        try {
            const response = await axios.get(`http://localhost/web_srk_BI/backend/api/employees.php?action=detail&eid=${eid}`, {
                withCredentials: true
            });
            if (response.data.success) {
                setEmployee(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching employee:', error);
        }
    };

    const fetchRecommendation = async () => {
        try {
            const response = await axios.get(`http://localhost/web_srk_BI/backend/api/employees.php?action=recommendation&eid=${eid}`, {
                withCredentials: true
            });
            if (response.data.success) {
                setRecommendation(response.data.recommendation);
            }
        } catch (error) {
            console.error('Error fetching recommendation:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleResign = async () => {
        if (!resignReason.trim()) {
            alert('Alasan resign harus diisi');
            return;
        }

        try {
            const response = await axios.post('http://localhost/web_srk_BI/backend/api/employees.php', {
                action: 'resign',
                eid: eid,
                reason: resignReason
            }, { withCredentials: true });

            if (response.data.success) {
                setShowResignModal(false);
                fetchEmployeeData();
                alert('Karyawan berhasil di-resign');
            }
        } catch (error) {
            console.error('Error resigning employee:', error);
            alert('Gagal melakukan resign');
        }
    };

    const getConfidenceBadge = (confidence) => {
        const badges = {
            'very_high': 'bg-success',
            'high': 'bg-info',
            'medium': 'bg-warning',
            'low': 'bg-danger'
        };
        return badges[confidence] || 'bg-secondary';
    };

    const getRecommendationTypeDisplay = (type) => {
        const types = {
            'probation': 'Probation',
            '1': 'Kontrak 1',
            '2': 'Kontrak 2',
            '3': 'Kontrak 3',
            'permanent': 'Permanent'
        };
        return types[type] || type;
    };

    if (loading) {
        return (
            <div className="container mt-5">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            </div>
        );
    }

    if (!employee) {
        return (
            <div className="container mt-5">
                <div className="alert alert-danger">
                    Karyawan tidak ditemukan
                </div>
            </div>
        );
    }

    return (
        <div className="employee-detail-container">
            {/* Header dengan tombol back */}
            <div className="detail-header">
                <button className="btn btn-back" onClick={() => navigate(-1)}>
                    <i className="fas fa-arrow-left me-2"></i>
                    Kembali
                </button>
                <h1 className="detail-title">Karyawan</h1>
                <div className="header-actions">
                    <button className="btn btn-secondary me-2">Probation</button>
                    <button className="btn btn-danger" onClick={() => setShowResignModal(true)}>Resign</button>
                </div>
            </div>

            <div className="detail-content">
                <div className="row">
                    {/* Left Column - Profile */}
                    <div className="col-md-3">
                        <div className="profile-card">
                            <div className="profile-avatar">
                                <img 
                                    src={employee.profile_picture || '/api/placeholder/120/120'} 
                                    alt="Profile" 
                                    className="avatar-img"
                                />
                            </div>
                            <div className="profile-info">
                                <div className="info-row">
                                    <span className="info-label">Nama Lengkap</span>
                                    <span className="info-value">{employee.full_name}</span>
                                </div>
                                <div className="info-row">
                                    <span className="info-label">Tanggal Lahir</span>
                                    <span className="info-value">
                                        {employee.birthdate ? new Date(employee.birthdate).toLocaleDateString('id-ID') : '-'}
                                    </span>
                                </div>
                                <div className="info-row">
                                    <span className="info-label">EID</span>
                                    <span className="info-value">{employee.eid}</span>
                                </div>
                                <div className="info-row">
                                    <span className="info-label">No HP</span>
                                    <span className="info-value">{employee.phone || '-'}</span>
                                </div>
                            </div>
                        </div>

                        <div className="cv-card">
                            <h6>CV</h6>
                            <button className="btn btn-outline-primary btn-sm w-100">
                                <i className="fas fa-download me-2"></i>
                                Download
                            </button>
                        </div>
                    </div>

                    {/* Middle Column - Main Content */}
                    <div className="col-md-6">
                        <div className="main-content-card">
                            {activeTab === 'data' && (
                                <div className="tab-content-area">
                                    <h5 className="tab-title">Data Karyawan</h5>
                                    <div className="data-grid">
                                        <div className="data-row">
                                            <span className="data-label">Nama Department</span>
                                            <div className="data-input">{employee.division_name}</div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Role</span>
                                            <div className="data-input">{employee.role}</div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Jurusan</span>
                                            <div className="data-input">{employee.major}</div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Tingkat Pendidikan</span>
                                            <div className="data-input">{employee.education_level}</div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Tanggal Mulai Probation</span>
                                            <div className="data-input">
                                                {employee.probation_start ? 
                                                    new Date(employee.probation_start).toLocaleDateString('id-ID') : '-'}
                                            </div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Tanggal Selesai Probation</span>
                                            <div className="data-input">
                                                {employee.probation_end ? 
                                                    new Date(employee.probation_end).toLocaleDateString('id-ID') : '-'}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {activeTab === 'recommendation' && (
                                <div className="tab-content-area">
                                    <h5 className="tab-title">Kontrak Recommendation</h5>
                                    {recommendation && (
                                        <div className="recommendation-display">
                                            <div className="recommendation-badge-large">
                                                Contract Permanent: Permanent
                                            </div>
                                        </div>
                                    )}
                                </div>
                            )}

                            {activeTab === 'history' && (
                                <div className="tab-content-area">
                                    <h5 className="tab-title">Riwayat Kontrak</h5>
                                    <div className="history-grid">
                                        <div className="data-row">
                                            <span className="data-label">Role</span>
                                            <div className="data-input">{employee.role}</div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Tipe Kontrak</span>
                                            <div className="data-input">Probation</div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Tanggal Mulai Kontrak</span>
                                            <div className="data-input">
                                                {employee.join_date ? 
                                                    new Date(employee.join_date).toLocaleDateString('id-ID') : '-'}
                                            </div>
                                        </div>
                                        <div className="data-row">
                                            <span className="data-label">Tanggal Akhir Kontrak</span>
                                            <div className="data-input">
                                                {employee.probation_end ? 
                                                    new Date(employee.probation_end).toLocaleDateString('id-ID') : '-'}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Right Column - Action Panel */}
                    <div className="col-md-3">
                        {activeTab === 'data' && (
                            <div className="action-card">
                                <h6>Alasan Resign</h6>
                                <div className="resign-form">
                                    <textarea
                                        className="form-control mb-3"
                                        rows="8"
                                        placeholder="Detail"
                                        value={resignReason}
                                        onChange={(e) => setResignReason(e.target.value)}
                                    ></textarea>
                                    <button 
                                        className="btn btn-primary w-100"
                                        onClick={handleResign}
                                    >
                                        Send
                                    </button>
                                </div>
                            </div>
                        )}

                        {activeTab === 'recommendation' && (
                            <div className="action-card">
                                <div className="recommendation-detail">
                                    {/* Additional recommendation details can go here */}
                                </div>
                            </div>
                        )}

                        {activeTab === 'history' && (
                            <div className="action-card">
                                <div className="history-actions">
                                    <button className="btn btn-success w-100 mb-2">Aktif</button>
                                    <button className="btn btn-danger w-100">Resign</button>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Footer Navigation */}
            <div className="footer-nav">
                <div className="nav-tabs-custom">
                    <button 
                        className={`nav-tab ${activeTab === 'data' ? 'active' : ''}`}
                        onClick={() => setActiveTab('data')}
                    >
                        Data Karyawan
                    </button>
                    <button 
                        className={`nav-tab ${activeTab === 'recommendation' ? 'active' : ''}`}
                        onClick={() => setActiveTab('recommendation')}
                    >
                        Kontrak Recommendation
                    </button>
                    <button 
                        className={`nav-tab ${activeTab === 'history' ? 'active' : ''}`}
                        onClick={() => setActiveTab('history')}
                    >
                        Riwayat Kontrak
                    </button>
                </div>
            </div>

            <div className="footer-links">
                <a href="#">Tentang Kami</a>
                <a href="#">Kontak</a>
                <a href="#">Kebijakan Privasi</a>
                <a href="#">Ketentuan Layanan</a>
            </div>

            <div className="footer-copyright">
                2025 CareerMatch. Semua hak cipta dilindungi.
            </div>

            {/* Resign Modal */}
            {showResignModal && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Konfirmasi Resign</h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => setShowResignModal(false)}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <p>Apakah Anda yakin ingin meresign karyawan ini?</p>
                            </div>
                            <div className="modal-footer">
                                <button 
                                    type="button" 
                                    className="btn btn-secondary"
                                    onClick={() => setShowResignModal(false)}
                                >
                                    Batal
                                </button>
                                <button 
                                    type="button" 
                                    className="btn btn-danger"
                                    onClick={handleResign}
                                >
                                    Ya, Resign
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
            {showResignModal && <div className="modal-backdrop fade show"></div>}
        </div>
    );
};

export default EmployeeDetail;
