  PointElement,
  LineElement,
  Filler,
} from 'chart.js';
import { Bar, Doughnut, Line } from 'react-chartjs-2';
import { useAuth } from '../App';
import { Card, Container, Row, Col, Nav, Tab, Table, Badge, Button, Modal, Alert, Spinner, ProgressBar, Form } from 'react-bootstrap';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  Filler
);

const ManagerDashboard = () => {
  const [analytics, setAnalytics] = useState(null);
  const [educationAnalysis, setEducationAnalysis] = useState(null);
  const [divisionIntelligence, setDivisionIntelligence] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const { user } = useAuth();
  const [showEmployeeModal, setShowEmployeeModal] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [employeeDetail, setEmployeeDetail] = useState(null);
  const [loadingDetail, setLoadingDetail] = useState(false);
  const [showRecommendationModal, setShowRecommendationModal] = useState(false);
  const [recommendationData, setRecommendationData] = useState({
    recommendation_type: 'extend',
    recommended_duration: '',
    reason: ''
  });
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });
  const [processedRecommendations, setProcessedRecommendations] = useState([]);

  useEffect(() => {
    if (user?.role === 'manager') {
      fetchManagerData();
      fetchProcessedRecommendations();

      // Auto refresh processed recommendations every 30 seconds
      const interval = setInterval(() => {
        fetchProcessedRecommendations();
      }, 30000);

      return () => clearInterval(interval);
    } else if (user?.role === 'hr') {
      showAlert('HR tidak dapat membuat rekomendasi. Hanya manager yang dapat membuat rekomendasi karyawan.', 'warning');
    }
  }, [user]);

  const fetchManagerData = async () => {
    try {
      setLoading(true);

      // Use the session-based endpoint that filters by manager's division
      const timestamp = new Date().getTime();
              const analyticsRes = await axios.get(`http://localhost/web_srk_BI/backend/manager_analytics_by_division.php?t=${timestamp}`, {
        withCredentials: true
      });

      console.log('🔥 NEW API Response:', analyticsRes.data);

      if (analyticsRes.data.success) {
        setAnalytics(analyticsRes.data.data);
        console.log('✅ Analytics data set:', analyticsRes.data.data);

        // Use the same analytics data for education analysis
        if (analyticsRes.data.data.employees) {
          const employees = analyticsRes.data.data.employees;
          const matchDistribution = {
            'Match': employees.filter(emp => emp.education_job_match === 'Match').length,
            'Unmatch': employees.filter(emp => emp.education_job_match === 'Unmatch').length
          };

          setEducationAnalysis({
            education_analysis: employees,
            match_distribution: matchDistribution,
            contract_progression: employees.filter(emp => 
              emp.contract_status === 'Ready for Review' || 
              emp.tenure_months >= 3
            ).map(emp => ({
              name: emp.name,
              status: emp.contract_status,
              recommendation: emp.data_mining_recommendation?.reason || 'Standard review'
            }))
          });

          setDivisionIntelligence({
            performance_predictions: employees.map(emp => ({
              name: emp.name,
              success_probability: emp.success_probability,
              risk_level: emp.risk_level,
              contract_recommendation: emp.data_mining_recommendation
            })),
            urgent_contract_decisions: employees.filter(emp => 
              emp.data_mining_recommendation?.risk_level === 'High' ||
              emp.contract_status === 'Ready for Review'
            )
          });
        }
      }

    } catch (error) {
      console.error('Error fetching manager data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchProcessedRecommendations = async () => {
    try {
      const response = await axios.get('http://localhost/web_srk_BI/backend/api/recommendations.php?action=processed', {
        withCredentials: true
      });

      if (response.data.success) {
        setProcessedRecommendations(response.data.data);
        console.log('✅ Processed recommendations loaded:', response.data.data);
      } else {
        console.error('Failed to fetch processed recommendations:', response.data.error);
      }
    } catch (error) {
      console.error('Error fetching processed recommendations:', error);
    }
  };

  const fetchEmployeeDetail = async (eid) => {
    setLoadingDetail(true);
    try {
      console.log('Fetching employee detail for eid:', eid);
      const response = await fetch(`http://localhost/web_srk_BI/backend/api/employees.php?action=employee_data_mining_detail&eid=${eid}`, {
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      console.log('Employee detail API Response:', result);

      if (result.success) {
        setEmployeeDetail(result.data);
        console.log('Employee detail loaded successfully');
      } else {
        console.error('API Error:', result.error);
        setEmployeeDetail(null);
      }
    } catch (error) {
      console.error('Network error:', error);
      setEmployeeDetail(null);
    } finally {
      setLoadingDetail(false);
    }
  };

  const handleEmployeeClick = (employee) => {
    setSelectedEmployee(employee);
    setShowEmployeeModal(true);
    fetchEmployeeDetail(employee.eid);
  };

  const handleCreateRecommendation = (employee) => {
    // Check if employee can be recommended
    if (employee.education_job_match === 'Unmatch') {
      showAlert('Karyawan ini tidak dapat direkomendasikan karena status match adalah "Unmatch". Pendidikan dan role tidak sesuai.', 'error');
      return;
    }

    if (employee.has_pending_recommendation) {
      showAlert('Karyawan ini sudah memiliki rekomendasi yang sedang pending. Tunggu hingga HR memproses rekomendasi sebelumnya.', 'warning');
      return;
    }

    if (employee.current_contract_type === 'Permanent') {
      showAlert('Karyawan ini memiliki kontrak permanent. Tidak dapat membuat rekomendasi baru.', 'info');
      return;
    }

    if (employee.days_to_contract_end > 30) {
      const endDate = employee.contract_end_date ? new Date(employee.contract_end_date).toLocaleDateString('id-ID') : 'N/A';
      showAlert(`Kontrak karyawan ini masih berlaku hingga ${endDate} (${employee.days_to_contract_end} hari lagi). Rekomendasi baru hanya dapat dibuat 30 hari sebelum kontrak berakhir.`, 'warning');
      return;
    }

    setSelectedEmployee(employee);
    setRecommendationData({
      recommendation_type: 'kontrak2',
      recommended_duration: employee.data_mining_recommendation?.recommended_duration || '12',
      reason: `Rekomendasi berdasarkan analisis data mining: Historical performance data, Match status ${employee.education_job_match}`
    });
    setShowRecommendationModal(true);
  };

  const submitRecommendation = async () => {
    try {
      const formData = new FormData();
      formData.append('action', 'create');
      formData.append('eid', selectedEmployee.eid);
      formData.append('recommendation_type', recommendationData.recommendation_type);
      formData.append('recommended_duration', recommendationData.recommended_duration);
      formData.append('reason', recommendationData.reason);
      formData.append('system_recommendation', `Rekomendasi sistem: ${recommendationData.recommendation_type} kontrak ${recommendationData.recommended_duration} bulan berdasarkan analisis data mining`);

      const response = await axios.post('http://localhost/web_srk_BI/backend/api/recommendations.php', formData, {
        withCredentials: true
      });

      if (response.data.success) {
        showAlert('Rekomendasi berhasil dibuat dan dikirim ke HR', 'success');
        setShowRecommendationModal(false);
        // Refresh data after creating recommendation
        fetchManagerData();
        fetchProcessedRecommendations();
      } else {
        showAlert(response.data.error, 'danger');
      }
    } catch (error) {
      console.error('Error creating recommendation:', error);
      showAlert('Gagal membuat rekomendasi', 'danger');
    }
  };

  const showAlert = (message, type) => {
    setAlert({ show: true, message, type });
    setTimeout(() => setAlert({ show: false, message: '', type: '' }), 5000);
  };

  const getMatchDistributionChart = () => {
    if (!educationAnalysis?.match_distribution) {
      return {
        labels: ['Match', 'Unmatch'],
        datasets: [{
          label: 'Distribusi Kecocokan',
          data: [0, 0],
          backgroundColor: ['#28a745', '#dc3545'],
          borderWidth: 1
        }]
      };
    }

    const data = educationAnalysis.match_distribution;
    return {
      labels: ['Match', 'Unmatch'],
      datasets: [{
        label: 'Distribusi Kecocokan',
        data: [data.Match || 0, data.Unmatch || 0],
        backgroundColor: ['#28a745', '#dc3545'],
        borderWidth: 1
      }]
    };
  };

  const getPerformancePredictionChart = () => {
    if (!analytics?.employees || analytics.employees.length === 0) {
      return {
        labels: ['No Data'],
        datasets: [{
          label: 'Performance Score (%)',
          data: [0],
          borderColor: '#0d6efd',
          backgroundColor: 'rgba(13, 110, 253, 0.1)',
          tension: 0.4,
          fill: true
        }]
      };
    }

    const employees = analytics.employees.slice(0, 10);
    return {
      labels: employees.map(emp => emp.name?.split(' ')[0] || 'Unknown'),
      datasets: [{
        label: 'Performance Score (%)',
        data: employees.map(emp => emp.data_mining_recommendation?.success_probability || 50),
        borderColor: '#0d6efd',
        backgroundColor: 'rgba(13, 110, 253, 0.1)',
        tension: 0.4,
        fill: true
      }]
    };
  };

  const getRiskLevelChart = () => {
    if (!analytics?.employees || analytics.employees.length === 0) {
      return {
        labels: ['Low Risk', 'Medium Risk', 'High Risk'],
        datasets: [{
          label: 'Distribusi Risk Level',
          data: [0, 0, 0],
          backgroundColor: ['#28a745', '#ffc107', '#dc3545'],
          borderWidth: 1
        }]
      };
    }

    const employees = analytics.employees;
    const lowRisk = employees.filter(emp => emp.data_mining_recommendation?.risk_level === 'Low').length;
    const mediumRisk = employees.filter(emp => emp.data_mining_recommendation?.risk_level === 'Medium').length;
    const highRisk = employees.filter(emp => emp.data_mining_recommendation?.risk_level === 'High').length;

    return {
      labels: ['Low Risk', 'Medium Risk', 'High Risk'],
      datasets: [{
        label: 'Distribusi Risk Level',
        data: [lowRisk, mediumRisk, highRisk],
        backgroundColor: ['#28a745', '#ffc107', '#dc3545'],
        borderWidth: 1
      }]
    };
  };

  const getContractRecommendationChart = () => {
    if (!analytics?.employees || analytics.employees.length === 0) {
      return {
        labels: ['6 bulan', '12 bulan', '18 bulan', '24 bulan', '36 bulan'],
        datasets: [{
          label: 'Jumlah Rekomendasi',
          data: [0, 0, 0, 0, 0],
          backgroundColor: 'rgba(0, 123, 255, 0.8)',
          borderColor: '#007bff',
          borderWidth: 2
        }]
      };
    }

    const employees = analytics.employees;
    const duration6 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 6).length;
    const duration12 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 12).length;
    const duration18 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 18).length;
    const duration24 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 24).length;
    const duration36 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 36).length;

    return {
      labels: ['6 bulan', '12 bulan', '18 bulan', '24 bulan', '36 bulan'],
      datasets: [{
        label: 'Jumlah Rekomendasi',
        data: [duration6, duration12, duration18, duration24, duration36],
        backgroundColor: 'rgba(0, 123, 255, 0.8)',
        borderColor: '#007bff',
        borderWidth: 2
      }]
    };
  };

  // Fungsi untuk mendapatkan rekomendasi kontrak berdasarkan data mining
  const getDataMiningRecommendation = (employee) => {
    // Gunakan data mining recommendation dari backend
    if (employee.data_mining_recommendation) {
      return {
        type: 'data_mining',
        duration: `${employee.data_mining_recommendation.recommended_duration} bulan`,
        success_probability: employee.data_mining_recommendation.success_probability,
        risk_level: employee.data_mining_recommendation.risk_level,
        confidence: employee.data_mining_recommendation.confidence_level,
        reasoning: employee.data_mining_recommendation.reasoning || []
      };
    }

    // Fallback untuk backward compatibility
    const matchStatus = employee.education_job_match || 'Unmatch';
    const tenure = employee.tenure_months || 0;
    const currentContract = employee.current_contract_type;

    if (currentContract === 'probation' && tenure >= 3) {
      if (matchStatus === 'Match') {
        return {
          type: 'permanent',
          reason: 'Good education-job match',
          duration: 'Permanent',
          confidence: 'High'
        };
      } else {
        return {
          type: 'review',
          reason: 'No education-job match, need performance evaluation',
          duration: 'Review diperlukan',
          confidence: 'Low'
        };
      }
    }

    if (tenure >= 12 && currentContract !== 'permanent') {
      return {
        type: 'extension',
        reason: 'Long tenure with good performance',
        duration: 'Extension recommended',
        confidence: 'High'
      };
    }

    return {
      type: 'monitor',
      reason: 'Continue monitoring',
      duration: 'Monitoring berkelanjutan',
      confidence: 'Medium'
    };
  };

  // Fungsi untuk mendapatkan status match dengan warna
  const getMatchLevelBadge = (matchStatus) => {
    if (matchStatus === 'Match') return <Badge bg="success">Match</Badge>;
    return <Badge bg="danger">Unmatch</Badge>;
  };

  const getRiskLevelBadge = (riskLevel) => {
    if (riskLevel === 'Low') return <Badge bg="success">Risiko Rendah</Badge>;
    if (riskLevel === 'Medium') return <Badge bg="warning">Risiko Sedang</Badge>;
    return <Badge bg="danger">Risiko Tinggi</Badge>;
  };

  const getContractStatusBadge = (contractType, tenure) => {
    const badges = {
      'probation': { variant: 'warning', text: 'Probation' },
      '1': { variant: 'info', text: 'Kontrak 1 Tahun' },
      '2': { variant: 'primary', text: 'Kontrak 2 Tahun' },
      '3': { variant: 'secondary', text: 'Kontrak 3 Tahun' },
      'permanent': { variant: 'success', text: 'Permanent' },
      null: { variant: 'light', text: 'N/A' }
    };

    const badge = badges[contractType] || badges[null];

    if (contractType === 'probation' && tenure >= 3) {
      return <Badge bg="warning" className="border border-danger">Probation (Ready for Review)</Badge>;
    }

    return <Badge bg={badge.variant}>{badge.text}</Badge>;
  };

  const getRecommendationStatusBadge = (employee) => {
    // Use new recommendation_stats if available
    if (employee.recommendation_stats) {
      const stats = employee.recommendation_stats;

      // Show recommendation history info
      const recommendationInfo = (
        <div className="d-flex flex-column align-items-center">
          {stats.display_status === 'pending' && (
            <Badge bg="warning">⏳ Pending</Badge>
          )}
          {stats.display_status === 'processed_approved' && (
            <Badge bg="success">✅ Kontrak Diperpanjang</Badge>
          )}
          {stats.display_status === 'processed_rejected' && (
            <Badge bg="danger">❌ Ditolak</Badge>
          )}
          {stats.display_status === 'none' && (
            <>
              {employee.current_contract_type === 'Permanent' ? (
                <Badge bg="secondary">🔒 Permanent</Badge>
              ) : employee.days_to_contract_end <= 30 ? (
                <Badge bg="success">✅ Dapat Direkomendasikan</Badge>
              ) : employee.days_to_contract_end > 30 ? (
                <div className="text-center">
                  <Badge bg="danger">❌ Belum Bisa</Badge>
                  <br />
                  <small className="text-muted">{employee.days_to_contract_end} hari lagi</small>
                </div>
              ) : (
                <Badge bg="info">📝 Siap Direkomendasikan</Badge>
              )}
            </>
          )}
          {stats.total > 0 && (
            <small className="text-muted mt-1">
              📋 {stats.total}x rekomendasi ({stats.approved} disetujui)
            </small>
          )}
        </div>
      );

      return recommendationInfo;
    }

    // Fallback to old logic
    if (employee.has_pending_recommendation) {
      return <Badge bg="warning">⏳ Pending</Badge>;
    }

    if (employee.current_contract_type === 'Permanent') {
      return <Badge bg="secondary">🔒 Permanent</Badge>;
    }

    if (employee.days_to_contract_end <= 30) {
      return <Badge bg="success">✅ Dapat Direkomendasikan</Badge>;
    }

    if (employee.days_to_contract_end > 30) {
      return (
        <div>
          <Badge bg="danger">❌ Belum Bisa</Badge>
          <br />
          <small className="text-muted">
            {employee.days_to_contract_end} hari lagi
          </small>
        </div>
      );
    }

    return <Badge bg="info">📝 Siap Direkomendasikan</Badge>;
  };

  if (loading) {
    return (
      <div className="container-fluid mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Memuat data intelligence...</p>
        </div>
      </div>
    );
  }

  return (
    <Container fluid className="mt-3">
      {alert.show && (
        <Alert variant={alert.type} dismissible onClose={() => setAlert({ show: false, message: '', type: '' })}>
          {alert.message}
        </Alert>
      )}

      {/* Header dengan tema biru konsisten */}
      <div className="mb-4 p-4 rounded" style={{
        background: 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)',
        color: 'white'
      }}>
        <Row className="align-items-center">
          <Col>
            <h2>📊 Manager Dashboard - Data Intelligence</h2>
            <p className="mb-0">Analisis Kecocokan Pendidikan & Rekomendasi Kontrak untuk Divisi</p>
            <small>{user.division_name} | Business Intelligence System</small>
          </Col>
          <Col xs="auto">
            <div className="text-end">
              <h3 className="mb-0">{analytics?.division_stats?.total_employees || 0}</h3>
              <small>Karyawan Aktif</small>
            </div>
          </Col>
        </Row>
      </div>

      {/* Navigation Tabs dengan tema biru */}
      <Tab.Container activeKey={activeTab} onSelect={setActiveTab}>
        <Nav variant="tabs" className="mb-4">
          <Nav.Item>
            <Nav.Link eventKey="overview" style={{
              color: activeTab === 'overview' ? '#fff' : '#007bff',
              backgroundColor: activeTab === 'overview' ? '#007bff' : 'transparent',
              borderColor: '#007bff'
            }}>
              📈 Overview
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="education" style={{
              color: activeTab === 'education' ? '#fff' : '#007bff',
              backgroundColor: activeTab === 'education' ? '#007bff' : 'transparent',
              borderColor: '#007bff'
            }}>
              🎓 Analisis Pendidikan
            </Nav.Link>
          </Nav.Item>

          <Nav.Item>
            <Nav.Link eventKey="employees" style={{
              color: activeTab === 'employees' ? '#fff' : '#007bff',
              backgroundColor: activeTab === 'employees' ? '#007bff' : 'transparent',
              borderColor: '#007bff'
            }}>
              👥 Detail Karyawan
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="processed" style={{
              color: activeTab === 'processed' ? '#fff' : '#007bff',
              backgroundColor: activeTab === 'processed' ? '#007bff' : 'transparent',
              borderColor: '#007bff'
            }}>
              ✅ Rekomendasi Diproses ({processedRecommendations.length})
            </Nav.Link>
          </Nav.Item>
        </Nav>

        <Tab.Content>
          {/* Overview Tab */}
          <Tab.Pane eventKey="overview">
            <Row className="mb-4">
              {/* Key Metrics Cards dengan gradient biru */}
              <Col md={4} className="mb-3">
                <Card className="h-100" style={{
                  background: 'linear-gradient(135deg, #28a745 0%, #1e7e34 100%)',
                  color: 'white'
                }}>
                  <Card.Body className="text-center">
                    <div className="display-6">👥</div>
                    <h3>{analytics?.division_stats?.data_mining_summary?.match_employees || 0}</h3>
                    <small>Match Employees</small>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={4} className="mb-3">
                <Card className="h-100" style={{
                  background: 'linear-gradient(135deg, #ffc107 0%, #e0a800 100%)',
                  color: 'white'
                }}>
                  <Card.Body className="text-center">
                    <div className="display-6">📋</div>
                    <h3>{analytics?.division_stats?.data_mining_summary?.recommended_extensions || 0}</h3>
                    <small>Rekomendasi Perpanjangan</small>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={4} className="mb-3">
                <Card className="h-100" style={{
                  background: 'linear-gradient(135deg, #dc3545 0%, #c82333 100%)',
                  color: 'white'
                }}>
                  <Card.Body className="text-center">
                    <div className="display-6">⚠️</div>
                    <h3>{analytics?.division_stats?.data_mining_summary?.high_risk_employees || 0}</h3>
                    <small>High Risk Employees</small>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            <Row>
              <Col md={6} className="mb-4">
                <Card>
                  <Card.Header style={{ backgroundColor: '#007bff', color: 'white' }}>
                    <h5 className="mb-0">📊 Distribusi Tingkat Risiko</h5>
                  </Card.Header>
                  <Card.Body>
                    <Doughnut data={getRiskLevelChart()} options={{
                      responsive: true,
                      plugins: {
                        legend: { position: 'bottom' }
                      }
                    }} />
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6} className="mb-4">
                <Card>
                  <Card.Header style={{ backgroundColor: '#007bff', color: 'white' }}>
                    <h5 className="mb-0">📈 Rekomendasi Durasi Kontrak</h5>
                  </Card.Header>
                  <Card.Body>
                    <Bar data={getContractRecommendationChart()} options={{
                      responsive: true,
                      plugins: {
                        legend: { display: false }
                      },
                      scales: {
                        y: { beginAtZero: true }
                      }
                    }} />
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            {/* Contract Intelligence Summary */}
            <Card className="mb-4">
              <Card.Header style={{ backgroundColor: '#007bff', color: 'white' }}>
                <h5 className="mb-0">🧠 Contract Intelligence Summary</h5>
              </Card.Header>
              <Card.Body>
                <Row>
                  <Col md={4}>
                    <div className="text-center p-3 border rounded" style={{ backgroundColor: '#e3f2fd' }}>
                      <h4 className="text-primary">{analytics?.division_stats?.contract_summary?.probation_ready || 0}</h4>
                      <small>Probation Ready for Review</small>
                    </div>
                  </Col>
                  <Col md={4}>
                    <div className="text-center p-3 border rounded" style={{ backgroundColor: '#fff3e0' }}>
                      <h4 className="text-warning">{analytics?.division_stats?.contract_summary?.contracts_ending || 0}</h4>
                      <small>Contracts Ending Soon</small>
                    </div>
                  </Col>
                  <Col md={4}>
                    <div className="text-center p-3 border rounded" style={{ backgroundColor: '#e8f5e8' }}>
                      <h4 className="text-success">{analytics?.division_stats?.contract_summary?.permanent_eligible || 0}</h4>
                      <small>Permanent Eligible</small>
                    </div>
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          </Tab.Pane>

          {/* Education Analysis Tab */}
          <Tab.Pane eventKey="education">
            <Card>
              <Card.Header style={{ backgroundColor: '#007bff', color: 'white' }}>
                <h5 className="mb-0">🎓 Analisis Kecocokan Pendidikan-Pekerjaan (Data Mining)</h5>
              </Card.Header>
              <Card.Body>
                <Table responsive striped>
                  <thead style={{ backgroundColor: '#007bff', color: 'white' }}>
                    <tr>
                      <th>Nama</th>
                      <th>Pendidikan</th>
                      <th>Posisi</th>
                      <th>Status Match</th>
                      <th>Rekomendasi Durasi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {analytics?.employees?.map((emp, index) => (
                      <tr key={index}>
                        <td>
                          <strong>{emp.name}</strong>
                          <br />
                          <small className="text-muted">{emp.tenure_months} bulan</small>
                        </td>
                        <td>
                          <div>{emp.major}</div>
                          <small className="text-muted">{emp.education_level}</small>
                        </td>
                        <td>{emp.role}</td>
                        <td>
                          <div className="d-flex align-items-center">
                            {getMatchLevelBadge(emp.education_job_match)}
                          </div>
                        </td>
                        <td>
                          <Badge bg="primary">
                            {emp.data_mining_recommendation?.recommended_duration || 'N/A'} bulan
                          </Badge>
                        </td>
                      </tr>
                    )) || []}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Tab.Pane>



          {/* Employee Details Tab */}
          <Tab.Pane eventKey="employees">
            <Card>
              <Card.Header style={{ backgroundColor: '#007bff', color: 'white' }}>
                <h5 className="mb-0">👥 Detail Karyawan Divisi</h5>
              </Card.Header>
              <Card.Body>
                <Table responsive hover>
                  <thead style={{ backgroundColor: '#e3f2fd' }}>
                    <tr>
                      <th>Nama & Departemen</th>
                      <th>Pendidikan</th>
                      <th>Join Date</th>
                      <th>Tenure</th>
                      <th>Status Kontrak</th>
                      <th>Status Match</th>
                      <th>Rekomendasi Kontrak</th>
                      <th>Status Rekomendasi</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {analytics?.employees.map((emp, index) => (
                      <tr key={index}>
                        <td>
                          <div>
                            <strong>{emp.name}</strong>
                            <>
                              <br />
                              <small className="text-muted">{emp.role}</small>
                            </>
                          </div>
                        </td>
                        <td>
                          {emp.major}
                          <>
                            <br />
                            <small className="text-muted">{emp.education_level}</small>
                          </>
                        </td>
                        <td>{new Date(emp.join_date).toLocaleDateString('id-ID')}</td>
                        <td>
                          <Badge bg="info">{emp.tenure_months} bulan</Badge>
                        </td>
                        <td>
                          {getContractStatusBadge(emp.current_contract_type, emp.days_to_contract_end)}
                        </td>
                        <td>
                          <div className="d-flex align-items-center">
                            {getMatchLevelBadge(emp.education_job_match)}
                          </div>
                        </td>
                        <td>
                          <Badge bg="success">
                            {emp.data_mining_recommendation?.recommended_duration || 'N/A'} bulan
                          </Badge>
                          <>
                            <br />
                            <small className="text-muted">
                              {emp.data_mining_recommendation?.confidence_level || 'N/A'} confidence
                            </small>
                          </>
                        </td>
                        <td>
                          {getRecommendationStatusBadge(emp)}
                        </td>
                        <td>
                          <div className="d-flex gap-2">
                            <Button 
                              size="sm" 
                              variant="outline-primary"
                              onClick={() => handleEmployeeClick(emp)}
                            >
                              Detail
                            </Button>
                            <Button 
                              size="sm" 
                              variant={emp.education_job_match === 'Unmatch' ? 'secondary' : 'success'}
                              onClick={() => handleCreateRecommendation(emp)}
                              disabled={
                                emp.has_pending_recommendation || 
                                emp.current_contract_type === 'Permanent' || 
                                emp.days_to_contract_end > 30 ||
                                emp.education_job_match === 'Unmatch'
                              }
                              title={
                                emp.education_job_match === 'Unmatch' ? 'Karyawan tidak dapat direkomendasikan - Status Match: Unmatch' :
                                emp.has_pending_recommendation ? 'Sudah ada rekomendasi pending' :
                                emp.current_contract_type === 'Permanent' ? 'Karyawan memiliki kontrak permanent' :
                                emp.days_to_contract_end > 30 ? `Kontrak masih berlaku ${emp.days_to_contract_end} hari lagi` :
                                'Buat rekomendasi untuk karyawan ini'
                              }
                            >
                              {emp.education_job_match === 'Unmatch' ? 'Unmatch' : 'Rekomendasi'}
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Tab.Pane>

          {/* Processed Recommendations Tab */}
          <Tab.Pane eventKey="processed">
            <Card>
              <Card.Header style={{ backgroundColor: '#28a745', color: 'white' }} className="d-flex justify-content-between align-items-center">
                <div>
                  <h5 className="mb-0">✅ Rekomendasi yang Sudah Diproses HR</h5>
                  <small>Lihat status rekomendasi Anda dan kontrak yang telah diperpanjang</small>
                </div>
                <Button 
                  variant="outline-light" 
                  size="sm" 
                  onClick={fetchProcessedRecommendations}
                  className="ms-2"
                >
                  🔄 Refresh
                </Button>
              </Card.Header>
              <Card.Body>
                {processedRecommendations.length > 0 ? (
                  <Table responsive hover>
                    <thead style={{ backgroundColor: '#e8f5e8' }}>
                      <tr>
                        <th>Karyawan</th>
                        <th>Rekomendasi Anda</th>
                        <th>Status HR</th>
                        <th>Kontrak Baru</th>
                        <th>Tanggal Diproses</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {processedRecommendations.map((rec, index) => (
                        <tr key={index}>
                          <td>
                            <div>
                              <strong>{rec.employee.name}</strong>
                              <br />
                              <small className="text-muted">{rec.employee.role}</small>
                            </div>
                          </td>
                          <td>
                            <Badge bg="primary" className="me-1">
                              {rec.recommendation_type === 'kontrak1' ? 'Kontrak 1' :
                               rec.recommendation_type === 'kontrak2' ? 'Kontrak 2' :
                               rec.recommendation_type === 'kontrak3' ? 'Kontrak 3' : rec.recommendation_type}
                            </Badge>
                            <br />
                            <small className="text-muted">{rec.recommended_duration} bulan</small>
                          </td>
                          <td>
                            {rec.status === 'approved' ? (
                              <Badge bg="success">✅ Disetujui</Badge>
                            ) : (
                              <Badge bg="danger">❌ Ditolak</Badge>
                            )}
                          </td>
                                                     <td>
                             {rec.status === 'approved' && rec.new_contract?.is_created ? (
                               <div>
                                 <Badge bg="success" className="mb-1">{rec.new_contract.type}</Badge>
                                 <br />
                                 <small className="text-success">
                                   <strong>✅ Kontrak Aktif:</strong><br />
                                   {rec.new_contract.end_date ? 
                                     `${new Date(rec.new_contract.start_date).toLocaleDateString('id-ID')} - ${new Date(rec.new_contract.end_date).toLocaleDateString('id-ID')}` : 
                                     `Mulai: ${new Date(rec.new_contract.start_date).toLocaleDateString('id-ID')} (Permanent)`
                                   }
                                   <br />
                                   <span className="text-muted">Durasi: {rec.recommended_duration === 'permanent' ? 'Permanent' : `${rec.recommended_duration} bulan`}</span>
                                 </small>
                               </div>
                             ) : rec.status === 'approved' ? (
                               <Badge bg="warning">⏳ Kontrak sedang dibuat</Badge>
                             ) : (
                               <small className="text-muted">Tidak ada kontrak baru</small>
                             )}
                           </td>
                          <td>
                            {new Date(rec.updated_at).toLocaleDateString('id-ID')}
                          </td>
                                                     <td>
                             {rec.status === 'approved' && rec.new_contract?.is_created ? (
                               <Badge bg="success">🎉 Kontrak Berhasil Diperpanjang</Badge>
                             ) : rec.status === 'approved' ? (
                               <Badge bg="warning">⏳ Sedang Memproses Kontrak</Badge>
                             ) : (
                               <Badge bg="danger">❌ Rekomendasi Ditolak</Badge>
                             )}
                           </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                ) : (
                  <div className="text-center py-5">
                    <div className="display-1 mb-3">📋</div>
                    <h5>Belum Ada Rekomendasi yang Diproses</h5>
                    <p className="text-muted">
                      Rekomendasi yang Anda buat akan muncul di sini setelah diproses oleh HR.
                    </p>
                  </div>
                )}
              </Card.Body>
            </Card>
          </Tab.Pane>
        </Tab.Content>
      </Tab.Container>

      {/* Employee Detail Modal dengan Data Mining Analysis */}
      <Modal show={showEmployeeModal} onHide={() => setShowEmployeeModal(false)} size="xl">
        <Modal.Header closeButton style={{ backgroundColor: '#007bff', color: 'white' }}>
          <Modal.Title>🔍 Detail Analisis Karyawan - Data Mining</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {loadingDetail ? (
            <div className="text-center py-4">
              <Spinner animation="border" variant="primary" />
              <p className="mt-2">Menganalisis data karyawan...</p>
            </div>
          ) : employeeDetail ? (
            <div>
              {/* Employee Basic Info */}
              <Row className="mb-4">
                <Col md={6}>
                  <Card className="h-100">
                    <Card.Header style={{ backgroundColor: '#e3f2fd' }}>
                      <h6 className="mb-0">👤 Informasi Dasar</h6>
                    </Card.Header>
                    <Card.Body>
                      <p><strong>Nama:</strong> {employeeDetail.employee.name}</p>
                      <p><strong>Posisi:</strong> {employeeDetail.employee.role}</p>
                      <p><strong>Divisi:</strong> {employeeDetail.employee.division_name}</p>
                      <p><strong>Join Date:</strong> {new Date(employeeDetail.employee.join_date).toLocaleDateString('id-ID')}</p>
                      <p><strong>Tenure:</strong> {employeeDetail.employee.tenure_months} bulan</p>
                      {employeeDetail.employee.recommendation_stats && (
                        <div className="mt-3 p-2 bg-light rounded">
                          <h6 className="mb-2">📋 Riwayat Rekomendasi</h6>
                          <small className="text-muted">
                            Total: <strong>{employeeDetail.employee.recommendation_stats.total}</strong> | 
                            Disetujui: <strong className="text-success">{employeeDetail.employee.recommendation_stats.approved}</strong> | 
                            Pending: <strong className="text-warning">{employeeDetail.employee.recommendation_stats.pending}</strong>
                          </small>
                          {employeeDetail.employee.recommendation_stats.latest_status && (
                            <div className="mt-1">
                              <small>Status Terakhir: 
                                <Badge bg={employeeDetail.employee.recommendation_stats.latest_status === 'approved' ? 'success' : 'danger'} className="ms-1">
                                  {employeeDetail.employee.recommendation_stats.latest_status === 'approved' ? '✅ Disetujui' : '❌ Ditolak'}
                                </Badge>
                              </small>
                            </div>
                          )}
                        </div>
                      )}
                    </Card.Body>
                  </Card>
                </Col>
                <Col md={6}>
                  <Card className="h-100">
                    <Card.Header style={{ backgroundColor: '#e3f2fd' }}>
                      <h6 className="mb-0">🎓 Pendidikan</h6>
                    </Card.Header>
                    <Card.Body>
                      <p><strong>Jurusan:</strong> {employeeDetail.employee.major}</p>
                      <p><strong>Tingkat:</strong> {employeeDetail.employee.education_level}</p>
                      <p><strong>Match Status:</strong> 
                        {employeeDetail.education_match_analysis?.match_status ? 
                          getMatchLevelBadge(employeeDetail.education_match_analysis.match_status) : 
                          <Badge bg="secondary">N/A</Badge>
                        }
                      </p>
                      <p><strong>Kategori:</strong> {employeeDetail.education_match_analysis?.category || 'N/A'}</p>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>

              {/* Data Mining Analysis */}
              <Row className="mb-4">
                <Col md={8}>
                  <Card>
                    <Card.Header style={{ backgroundColor: '#007bff', color: 'white' }}>
                      <h6 className="mb-0">🤖 Analisis Data Mining</h6>
                    </Card.Header>
                    <Card.Body>
                      <Row>
                        <Col md={6}>
                          <h6>Rekomendasi Kontrak:</h6>
                          <div className="p-3 border rounded mb-3" style={{ backgroundColor: '#f8f9fa' }}>
                            <div className="d-flex justify-content-between align-items-center">
                              <span><strong>Durasi:</strong></span>
                              <Badge bg="primary" className="fs-6">
                                {employeeDetail.contract_recommendation?.recommended_duration || 'N/A'} bulan
                              </Badge>
                            </div>

                            <div className="d-flex justify-content-between align-items-center mt-2">
                              <span><strong>Risk Level:</strong></span>
                              {employeeDetail.contract_recommendation?.risk_level ? 
                                getRiskLevelBadge(employeeDetail.contract_recommendation.risk_level) :
                                <Badge bg="secondary">N/A</Badge>
                              }
                            </div>
                            <div className="d-flex justify-content-between align-items-center mt-2">
                              <span><strong>Confidence:</strong></span>
                              <Badge bg="info">{employeeDetail.contract_recommendation?.confidence_level || 'N/A'}</Badge>
                            </div>
                          </div>
                        </Col>
                        <Col md={6}>
                          <h6>Profil Serupa Analysis:</h6>
                          <div className="p-3 border rounded" style={{ backgroundColor: '#f8f9fa' }}>
                            <p><strong>Sample Size:</strong> {employeeDetail.similar_profiles_analysis?.total_count || 0} karyawan</p>

                            <p><strong>Avg Duration:</strong> {employeeDetail.similar_profiles_analysis?.avg_duration?.toFixed(1) || 'N/A'} bulan</p>
                            <p><strong>Resigned:</strong> {employeeDetail.similar_profiles_analysis?.resigned_count || 0}</p>
                          </div>
                        </Col>
                      </Row>
                    </Card.Body>
                  </Card>
                </Col>
                <Col md={4}>
                  <Card className="h-100">
                    <Card.Header style={{ backgroundColor: '#28a745', color: 'white' }}>
                      <h6 className="mb-0">�� Data Confidence</h6>
                    </Card.Header>
                    <Card.Body>
                      <div className="text-center">
                        <div className="display-6 mb-3">
                          {employeeDetail.contract_recommendation?.data_insights?.data_confidence === 'High' ? '🟢' :
                           employeeDetail.contract_recommendation?.data_insights?.data_confidence === 'Medium' ? '🟡' : '🔴'}
                        </div>
                        <h5>{employeeDetail.contract_recommendation?.data_insights?.data_confidence || 'N/A'}</h5>
                        <p className="text-muted">
                          Berdasarkan {employeeDetail.contract_recommendation?.data_insights?.sample_size || 0} data serupa
                        </p>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>

              {/* Reasoning */}
              <Card>
                <Card.Header style={{ backgroundColor: '#ffc107', color: 'black' }}>
                  <h6 className="mb-0">💡 Alasan Rekomendasi</h6>
                </Card.Header>
                <Card.Body>
                  <ul>
                    {employeeDetail.contract_recommendation?.reasoning?.length > 0 ? 
                      employeeDetail.contract_recommendation.reasoning.map((reason, idx) => (
                        <li key={idx}>{reason}</li>
                      )) :
                      <li>Tidak ada alasan spesifik tersedia</li>
                    }
                  </ul>
                </Card.Body>
              </Card>
            </div>
          ) : (
            <Alert variant="danger">
              <Alert.Heading>Gagal memuat detail karyawan</Alert.Heading>
              <p>Terjadi kesalahan saat mengambil data analisis karyawan. Silakan coba lagi atau hubungi administrator sistem.</p>
              <hr />
              <p className="mb-0">
                <strong>Tips:</strong> Pastikan Anda sudah login dan memiliki akses ke data karyawan ini.
              </p>
            </Alert>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEmployeeModal(false)}>
            Tutup
          </Button>
          {selectedEmployee && selectedEmployee.education_job_match !== 'Unmatch' && (
            <Button 
              variant="success" 
              onClick={() => {
                setShowEmployeeModal(false);
                handleCreateRecommendation(selectedEmployee);
              }}
            >
              📋 Buat Rekomendasi
            </Button>
          )}
          {selectedEmployee && selectedEmployee.education_job_match === 'Unmatch' && (
            <Button 
              variant="secondary" 
              disabled
              title="Karyawan tidak dapat direkomendasikan - Status Match: Unmatch"
            >
              ❌ Unmatch
            </Button>
          )}
        </Modal.Footer>
      </Modal>

      {/* Create Recommendation Modal */}
      <Modal show={showRecommendationModal} onHide={() => setShowRecommendationModal(false)} size="lg">
        <Modal.Header closeButton style={{ backgroundColor: '#28a745', color: 'white' }}>
          <Modal.Title>📋 Buat Rekomendasi Kontrak</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedEmployee && (
            <div>
              <div className="mb-4 p-3 bg-light rounded">
                <Row>
                  <Col md={6}>
                    <strong>Karyawan:</strong> {selectedEmployee.name}<br />
                    <strong>Posisi:</strong> {selectedEmployee.role}<br />
                    <strong>Divisi:</strong> {selectedEmployee.division_name}<br />
                    <strong>Pendidikan:</strong> {selectedEmployee.education_level || 'N/A'} - {selectedEmployee.major || 'N/A'}
                  </Col>
                  <Col md={6}>
                    <strong>Tenure:</strong> {selectedEmployee.tenure_months} bulan<br />
                    <strong>Match Status:</strong> 
                    {getMatchLevelBadge(selectedEmployee.education_job_match)}<br />
                    <strong>Status Kontrak:</strong> {selectedEmployee.current_contract_type || 'No Contract'}<br />
                    {selectedEmployee.days_to_contract_end && (
                      <small className="text-warning">
                        ⚠️ Kontrak berakhir dalam {selectedEmployee.days_to_contract_end} hari
                      </small>
                    )}
                  </Col>
                </Row>
              </div>

              {/* Status Info Alert */}
              <Alert variant="success" className="mb-3">
                <Alert.Heading>✅ Karyawan Dapat Direkomendasikan</Alert.Heading>
                <p className="mb-1">
                  {selectedEmployee.days_to_contract_end <= 30 && selectedEmployee.days_to_contract_end > 0 ? 
                    `Kontrak akan berakhir dalam ${selectedEmployee.days_to_contract_end} hari (kurang dari 30 hari).` :
                    selectedEmployee.current_contract_type ? 
                      'Karyawan memenuhi syarat untuk rekomendasi kontrak baru.' :
                      'Karyawan belum memiliki kontrak aktif.'
                  }
                </p>
                <hr />
                <p className="mb-0">
                  <small>
                    💡 <strong>Info:</strong> Rekomendasi hanya dapat dibuat 30 hari sebelum kontrak berakhir atau untuk karyawan tanpa kontrak aktif.
                  </small>
                </p>
              </Alert>

              <Form>
                <Form.Group className="mb-3">
                  <Form.Label><strong>Jenis Rekomendasi *</strong></Form.Label>
                  <Form.Select 
                    value={recommendationData.recommendation_type} 
                    onChange={(e) => setRecommendationData({...recommendationData, recommendation_type: e.target.value})}
                    required
                  >
                    <option value="kontrak2">Kontrak 2</option>
                    <option value="kontrak3">Kontrak 3</option>
                    <option value="permanent">Permanent</option>
                    <option value="terminate">Tidak Direkomendasikan</option>
                  </Form.Select>
                </Form.Group>

                {(recommendationData.recommendation_type === 'kontrak2' || 
                  recommendationData.recommendation_type === 'kontrak3') && (
                  <Form.Group className="mb-3">
                    <Form.Label><strong>Durasi Kontrak (bulan) *</strong></Form.Label>
                    <Form.Select 
                      value={recommendationData.recommended_duration} 
                      onChange={(e) => setRecommendationData({...recommendationData, recommended_duration: e.target.value})}
                      required
                    >
                      <option value="">Pilih durasi...</option>
                      {recommendationData.recommendation_type === 'kontrak2' && (
                        <>
                          <option value="3">3 Bulan</option>
                          <option value="6">6 bulan</option>
                          <option value="12">12 Bulan</option>
                          <option value="permanent">Permanent</option>
                        </>
                      )}
                      {recommendationData.recommendation_type === 'kontrak3' && (
                        <>
                          <option value="3">3 Bulan</option>
                          <option value="12">12 Bulan</option>
                          <option value="24">24 bulan</option>
                          <option value="36">36 bulan</option>
                          <option value="permanent">Permanent</option>
                        </>
                      )}
                    </Form.Select>
                  </Form.Group>
                )}

                <Form.Group className="mb-3">
                  <Form.Label><strong>Alasan Rekomendasi *</strong></Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={4}
                    value={recommendationData.reason}
                    onChange={(e) => setRecommendationData({...recommendationData, reason: e.target.value})}
                    placeholder="Jelaskan alasan rekomendasi berdasarkan analisis kinerja, kecocokan pendidikan, dan data mining..."
                    required
                  />
                  <Form.Text className="text-muted">
                    Berikan justifikasi yang jelas berdasarkan data analisis dan performa karyawan.
                  </Form.Text>
                </Form.Group>

                {selectedEmployee.data_mining_recommendation && (
                  <div className="mb-3">
                    <strong>Rekomendasi Sistem:</strong>
                    <div className="bg-info bg-opacity-10 p-2 rounded mt-1">
                      Durasi: {selectedEmployee.data_mining_recommendation.recommended_duration} bulan<br />
                      Risk Level: {selectedEmployee.data_mining_recommendation.risk_level}
                    </div>
                  </div>
                )}
              </Form>
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowRecommendationModal(false)}>
            Batal
          </Button>
          <Button 
            variant="success" 
            onClick={submitRecommendation}
            disabled={!recommendationData.recommendation_type || !recommendationData.reason || 
                     ((recommendationData.recommendation_type === 'kontrak1' || 
                       recommendationData.recommendation_type === 'kontrak2' || 
                       recommendationData.recommendation_type === 'kontrak3') && !recommendationData.recommended_duration)}
          >
            📤 Kirim Rekomendasi ke HR
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default ManagerDashboard;
