import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../App';

const Employees = () => {
    const { user } = useContext(AuthContext);
    const navigate = useNavigate();
    const [employees, setEmployees] = useState([]);
    const [divisions, setDivisions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAddModal, setShowAddModal] = useState(false);
    const [newEmployee, setNewEmployee] = useState({
        full_name: '',
        email: '',
        phone: '',
        birth_date: '',
        address: '',
        education_level: '',
        major: '',
        position: '',
        division_id: '',
        join_date: ''
    });

    // Separate active and resigned employees
    const activeEmployees = employees.filter(emp => emp.status === 'active' || emp.status === 'probation');
    const resignedEmployees = employees.filter(emp => emp.status === 'resigned' || emp.status === 'terminated');

    useEffect(() => {
        fetchEmployees();
        fetchDivisions();
    }, []);

    const fetchEmployees = async () => {
        try {
            const response = await fetch('http://localhost/web_srk_BI/backend/api/employees.php', {
                credentials: 'include'
            });
            const data = await response.json();
            if (data.success) {
                setEmployees(data.employees || []);
            }
        } catch (error) {
            console.error('Error fetching employees:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchDivisions = async () => {
        try {
            const response = await fetch('http://localhost/web_srk_BI/backend/api/divisions.php', {
                credentials: 'include'
            });
            const data = await response.json();
            if (data.success) {
                setDivisions(data.divisions || []);
            }
        } catch (error) {
            console.error('Error fetching divisions:', error);
        }
    };

    const calculateYearsWorked = (joinDate) => {
        if (!joinDate) return 'N/A';
        const today = new Date();
        const join = new Date(joinDate);
        const diffTime = Math.abs(today - join);
        const diffYears = Math.floor(diffTime / (1000 * 60 * 60 * 24 * 365));
        return diffYears;
    };

    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost/web_srk_BI/backend/api/employees.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
                body: JSON.stringify({
                    action: 'add',
                    ...newEmployee
                })
            });

            const data = await response.json();
            if (data.success) {
                alert('Karyawan berhasil ditambahkan!');
                setShowAddModal(false);
                setNewEmployee({
                    full_name: '',
                    email: '',
                    phone: '',
                    birth_date: '',
                    address: '',
                    education_level: '',
                    major: '',
                    position: '',
                    division_id: '',
                    join_date: ''
                });
                fetchEmployees();
            } else {
                alert('Error: ' + data.message);
            }
        } catch (error) {
            console.error('Error adding employee:', error);
            alert('Error adding employee');
        }
    };

    const getStatusBadge = (status) => {
        switch (status) {
            case 'Active':
                return 'status-active';
            case 'Probation':
                return 'status-probation';
            default:
                return 'status-inactive';
        }
    };

    const getStatusText = (status) => {
        switch (status) {
            case 'Active':
                return 'Aktif';
            case 'Probation':
                return 'Probation';
            case 'Inactive':
                return 'Resign';
            default:
                return 'Tidak Aktif';
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'active':
                return 'status-active-color';
            case 'probation':
                return 'status-probation-color';
            case 'resigned':
            case 'terminated':
                return 'status-inactive-color';
            default:
                return 'status-inactive-color';
        }
    };

    if (loading) {
        return <div className="text-center p-4">Loading...</div>;
    }

    return (
        <div className="employees-page">
            <div className="container-fluid">
                {/* Active Employees Section */}
                <div className="section-header">
                    <h3>Karyawan Profile</h3>
                    <p className="text-muted">Total Talents {activeEmployees.length}</p>
                </div>

                <div className="employees-grid">
                    {/* Add Employee Card */}
                    {(user.role === 'admin' || user.role === 'hr') && (
                        <div className="employee-card add-employee-card" onClick={() => setShowAddModal(true)}>
                            <div className="add-icon">
                                <i className="fas fa-plus"></i>
                            </div>
                        </div>
                    )}

                    {/* Active Employee Cards */}
                    {activeEmployees.map(employee => (
                        <div key={employee.employee_id} className="employee-card">
                            <div className="employee-avatar">
                                <img 
                                    src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=60`} 
                                    alt={employee.name}
                                />
                            </div>

                            <div className="employee-info">
                                <h5 className="employee-name">{employee.name}</h5>
                                <p className="employee-years">{calculateYearsWorked(employee.join_date)} Tahun</p>
                                <p className="employee-division">{employee.division_name}</p>

                                {/* Status Dots */}
                                <div className="status-dots">
                                    <span className={`dot ${employee.status === 'active' ? 'active' : ''}`}></span>
                                    <span className={`dot ${employee.status === 'probation' ? 'probation' : ''}`}></span>
                                    <span className={`dot ${employee.status === 'resigned' || employee.status === 'terminated' ? 'resigned' : ''}`}></span>
                                </div>
                            </div>

                            <div className="employee-actions">
                                <button 
                                    className="btn btn-primary btn-sm"
                                    onClick={() => navigate(`/employee/${employee.employee_id}`)}
                                >
                                    Details
                                </button>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Resigned Employees Section */}
                {resignedEmployees.length > 0 && (
                    <>
                        <div className="section-header mt-5">
                            <h3>Karyawan Resign</h3>
                            <p className="text-muted">Total Karyawan {resignedEmployees.length}</p>
                        </div>

                        <div className="employees-grid">
                            {resignedEmployees.map(employee => (
                                <div key={employee.employee_id} className="employee-card resigned-card">
                                    <div className="employee-avatar">
                                        <img 
                                            src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=60`} 
                                            alt={employee.name}
                                        />
                                    </div>

                                    <div className="employee-info">
                                        <h5 className="employee-name">{employee.name}</h5>
                                        <p className="employee-years">{calculateYearsWorked(employee.join_date)} Tahun</p>
                                        <p className="employee-division">{employee.division_name}</p>

                                        {/* Status Dots */}
                                        <div className="status-dots">
                                            <span className="dot"></span>
                                            <span className="dot"></span>
                                            <span className="dot resigned"></span>
                                        </div>
                                    </div>

                                    <div className="employee-actions">
                                        <button 
                                            className="btn btn-secondary btn-sm"
                                            onClick={() => navigate(`/employee/${employee.employee_id}`)}
                                        >
                                            Update
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </>
                )}
            </div>

            {/* Add Employee Modal */}
            {showAddModal && (
                <div className="modal fade show" style={{ display: 'block' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Tambah Karyawan Baru</h5>
                                <button type="button" className="btn-close" onClick={() => setShowAddModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleAddEmployee}>
                                    <div className="mb-3">
                                        <label className="form-label">Nama Lengkap</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={newEmployee.full_name}
                                            onChange={(e) => setNewEmployee({...newEmployee, full_name: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Email</label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            value={newEmployee.email}
                                            onChange={(e) => setNewEmployee({...newEmployee, email: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Nomor Telepon</label>
                                        <input
                                            type="tel"
                                            className="form-control"
                                            value={newEmployee.phone}
                                            onChange={(e) => setNewEmployee({...newEmployee, phone: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Divisi</label>
                                        <select
                                            className="form-control"
                                            value={newEmployee.division_id}
                                            onChange={(e) => setNewEmployee({...newEmployee, division_id: e.target.value})}
                                            required
                                        >
                                            <option value="">Pilih Divisi</option>
                                            {divisions.map(division => (
                                                <option key={division.division_id} value={division.division_id}>
                                                    {division.division_name}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Posisi</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={newEmployee.position}
                                            onChange={(e) => setNewEmployee({...newEmployee, position: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Pendidikan</label>
                                        <select
                                            className="form-control"
                                            value={newEmployee.education_level}
                                            onChange={(e) => setNewEmployee({...newEmployee, education_level: e.target.value})}
                                            required
                                        >
                                            <option value="">Pilih Pendidikan</option>
                                            <option value="SMA/K">SMA/K</option>
                                            <option value="D3">D3</option>
                                            <option value="S1">S1</option>
                                            <option value="S2">S2</option>
                                            <option value="S3">S3</option>
                                        </select>
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Jurusan</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={newEmployee.major}
                                            onChange={(e) => setNewEmployee({...newEmployee, major: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Tanggal Bergabung</label>
                                        <input
                                            type="date"
                                            className="form-control"
                                            value={newEmployee.join_date}
                                            onChange={(e) => setNewEmployee({...newEmployee, join_date: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="modal-footer">
                                        <button type="button" className="btn btn-secondary" onClick={() => setShowAddModal(false)}>
                                            Batal
                                        </button>
                                        <button type="submit" className="btn btn-primary">
                                            Simpan
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Employees;
