import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const Employees = () => {
    const { user } = useAuth();
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAddModal, setShowAddModal] = useState(false);
    const [newEmployee, setNewEmployee] = useState({
        full_name: '',
        email: '',
        phone: '',
        birth_date: '',
        address: '',
        education_level: '',
        major: '',
        role: '',
        division_id: ''
    });
    const [divisions, setDivisions] = useState([]);

    useEffect(() => {
        fetchEmployees();
        fetchDivisions();
    }, []);

    const fetchEmployees = async () => {
        try {
            const response = await axios.get('/employees.php');
            if (response.data.success) {
                setEmployees(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching employees:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchDivisions = async () => {
        try {
            const response = await axios.get('/divisions.php');
            if (response.data.success) {
                setDivisions(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching divisions:', error);
        }
    };

    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('action', 'create');
            Object.keys(newEmployee).forEach(key => {
                formData.append(key, newEmployee[key]);
            });

            const response = await axios.post('/employees.php', formData);
            if (response.data.success) {
                alert('Karyawan berhasil ditambahkan!');
                setShowAddModal(false);
                setNewEmployee({
                    full_name: '',
                    email: '',
                    phone: '',
                    birth_date: '',
                    address: '',
                    education_level: '',
                    major: '',
                    role: '',
                    division_id: ''
                });
                fetchEmployees();
            } else {
                alert('Error: ' + response.data.error);
            }
        } catch (error) {
            console.error('Error adding employee:', error);
            alert('Terjadi kesalahan saat menambah karyawan');
        }
    };

    const getStatusBadge = (status) => {
        switch (status) {
            case 'Active':
                return 'status-active';
            case 'Probation':
                return 'status-probation';
            default:
                return 'status-inactive';
        }
    };

    const getStatusText = (status) => {
        switch (status) {
            case 'Active':
                return 'Aktif';
            case 'Probation':
                return 'Probation';
            case 'Inactive':
                return 'Resign';
            default:
                return 'Tidak Aktif';
        }
    };

    if (loading) {
        return (
            <div className="loading-spinner">
                <div className="spinner"></div>
            </div>
        );
    }

    return (
        <div className="main-container">
            {/* Header */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <h1>Total Lamaran: {employees.length}</h1>
                </div>
                {(user.role === 'admin' || user.role === 'hr') && (
                    <button 
                        className="btn btn-primary"
                        onClick={() => setShowAddModal(true)}
                    >
                        + Tambah Karyawan
                    </button>
                )}
            </div>

            {/* Employee List */}
            <div className="employee-list">
                {employees.length > 0 ? (
                    employees.map(employee => (
                        <div key={employee.eid} className="employee-card">
                            <div className="employee-card-header">
                                <img 
                                    src={`https://via.placeholder.com/60x60/007bff/white?text=${employee.full_name?.charAt(0) || 'N'}`}
                                    alt={employee.full_name}
                                    className="employee-card-avatar"
                                />
                                <div className="employee-card-info">
                                    <h4>{employee.full_name}</h4>
                                    <p>{employee.age || 'N/A'} Tahun</p>
                                    <p>{employee.email}</p>
                                </div>
                            </div>

                            <div className="employee-card-footer">
                                <div className="d-flex gap-2">
                                    <span className={`status-badge ${getStatusBadge(employee.status)}`}>
                                        {getStatusText(employee.status)}
                                    </span>
                                    {employee.status === 'Inactive' && (
                                        <button className="btn btn-danger btn-sm">
                                            Deactive
                                        </button>
                                    )}
                                </div>
                                <Link 
                                    to={`/employees/${employee.eid}`}
                                    className="btn btn-primary btn-sm"
                                >
                                    Details
                                </Link>
                            </div>
                        </div>
                    ))
                ) : (
                    <div className="text-center py-5">
                        <p className="text-muted">Tidak ada data karyawan</p>
                    </div>
                )}
            </div>

            {/* Add Employee Modal */}
            {showAddModal && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Tambah Karyawan Baru</h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => setShowAddModal(false)}
                                ></button>
                            </div>
                            <form onSubmit={handleAddEmployee}>
                                <div className="modal-body">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Nama Lengkap</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.full_name}
                                                    onChange={(e) => setNewEmployee({...newEmployee, full_name: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Email</label>
                                                <input
                                                    type="email"
                                                    className="form-control"
                                                    value={newEmployee.email}
                                                    onChange={(e) => setNewEmployee({...newEmployee, email: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>No. HP</label>
                                                <input
                                                    type="tel"
                                                    className="form-control"
                                                    value={newEmployee.phone}
                                                    onChange={(e) => setNewEmployee({...newEmployee, phone: e.target.value})}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Tanggal Lahir</label>
                                                <input
                                                    type="date"
                                                    className="form-control"
                                                    value={newEmployee.birth_date}
                                                    onChange={(e) => setNewEmployee({...newEmployee, birth_date: e.target.value})}
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="form-group">
                                        <label>Alamat</label>
                                        <textarea
                                            className="form-control"
                                            rows="3"
                                            value={newEmployee.address}
                                            onChange={(e) => setNewEmployee({...newEmployee, address: e.target.value})}
                                        ></textarea>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Tingkat Pendidikan</label>
                                                <select
                                                    className="form-control"
                                                    value={newEmployee.education_level}
                                                    onChange={(e) => setNewEmployee({...newEmployee, education_level: e.target.value})}
                                                    required
                                                >
                                                    <option value="">Pilih Tingkat Pendidikan</option>
                                                    <option value="D3">D3</option>
                                                    <option value="S1">S1</option>
                                                    <option value="S2">S2</option>
                                                    <option value="S3">S3</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Jurusan</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.major}
                                                    onChange={(e) => setNewEmployee({...newEmployee, major: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Role</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.role}
                                                    onChange={(e) => setNewEmployee({...newEmployee, role: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Divisi</label>
                                                <select
                                                    className="form-control"
                                                    value={newEmployee.division_id}
                                                    onChange={(e) => setNewEmployee({...newEmployee, division_id: e.target.value})}
                                                    required
                                                >
                                                    <option value="">Pilih Divisi</option>
                                                    {divisions.map(division => (
                                                        <option key={division.id} value={division.id}>
                                                            {division.name}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button 
                                        type="button" 
                                        className="btn btn-secondary"
                                        onClick={() => setShowAddModal(false)}
                                    >
                                        Batal
                                    </button>
                                    <button type="submit" className="btn btn-primary">
                                        Tambah Karyawan
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
            {showAddModal && <div className="modal-backdrop fade show"></div>}

            {/* Footer */}
            <div className="footer">
                <div className="footer-links">
                    <a href="#">Tentang Kami</a>
                    <a href="#">Kontak</a>
                    <a href="#">Kebijakan Privasi</a>
                    <a href="#">Ketentuan Layanan</a>
                </div>
                <p>©2025 CareerMatch. Semua hak cipta dilindungi.</p>
            </div>
        </div>
    );
};

export default Employees;
