          contractType = 'Kontrak 2 (Evaluasi)';
          break;
        case '2':
          contractType = 'Kontrak 3 (Evaluasi)';
          break;
        case '3':
          contractType = 'Kontrak 3 (Evaluasi)';
          break;
        default:
