import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../App';

const EmployeeTable = () => {
    const { user } = useAuth();
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAddModal, setShowAddModal] = useState(false);
    const [divisions, setDivisions] = useState([]);
    const [newEmployee, setNewEmployee] = useState({
        name: '',
        email: '',
        phone: '',
        birth_date: '',
        address: '',
        education_level: '',
        major: '',
        last_education_place: '',
        designation: '',
        role: '',
        division_id: ''
    });

    useEffect(() => {
        fetchEmployees();
        fetchDivisions();
    }, []);

    const fetchEmployees = async () => {
        try {
            setLoading(true);
            const response = await axios.get('/employees.php', {
                withCredentials: true
            });
            if (response.data.success) {
                setEmployees(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching employees:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchDivisions = async () => {
        try {
            const response = await axios.get('/divisions.php', {
                withCredentials: true
            });
            if (response.data.success && Array.isArray(response.data.data)) {
                setDivisions(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching divisions:', error);
        }
    };

    const handleAddEmployee = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('action', 'create');
            Object.keys(newEmployee).forEach(key => {
                formData.append(key, newEmployee[key]);
            });

            const response = await axios.post('/employees.php', formData, {
                withCredentials: true
            });

            if (response.data.success) {
                alert('Karyawan berhasil ditambahkan!');
                setShowAddModal(false);
                setNewEmployee({
                    name: '',
                    email: '',
                    phone: '',
                    birth_date: '',
                    address: '',
                    education_level: '',
                    major: '',
                    last_education_place: '',
                    designation: '',
                    role: '',
                    division_id: ''
                });
                fetchEmployees();
            } else {
                alert('Error: ' + response.data.error);
            }
        } catch (error) {
            console.error('Error adding employee:', error);
            alert('Terjadi kesalahan saat menambah karyawan');
        }
    };

    const getStatusBadge = (status) => {
        switch (status) {
            case 'active':
                return <span className="badge bg-success">Active</span>;
            case 'probation':
                return <span className="badge bg-warning">Probation</span>;
            case 'resigned':
                return <span className="badge bg-danger">Resigned</span>;
            case 'terminated':
                return <span className="badge bg-danger">Resigned</span>;
            default:
                return <span className="badge bg-success">Active</span>;
        }
    };

    const formatDate = (dateString) => {
        if (!dateString || dateString === '0000-00-00') return '-';
        // Format: YYYY-MM-DD
        const date = new Date(dateString);
        const year = date.getFullYear();
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const day = String(date.getDate()).padStart(2, '0');
        return `${year}-${month}-${day}`;
    };

    const getEID = (id) => {
        return `EID${id.toString().padStart(4, '0')}`;
    };

    const getDivisionName = (divisionId) => {
        const division = divisions.find(d => d.division_id === divisionId);
        return division ? division.division_name : 'IT';
    };

    if (loading) {
        return (
            <div className="loading-spinner">
                <div className="spinner"></div>
            </div>
        );
    }

    return (
        <div className="main-container">
            {/* Header */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <h2 className="mb-0">Data Karyawan</h2>
                    <p className="text-muted mb-0">Total Talents {employees.length}</p>
                </div>
                {(user.role === 'admin' || user.role === 'hr') && (
                    <button 
                        className="btn btn-primary"
                        onClick={() => setShowAddModal(true)}
                    >
                        <i className="fas fa-plus"></i> Tambah Karyawan
                    </button>
                )}
            </div>

            {/* Employee Table */}
            <div className="card">
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table table-hover">
                            <thead className="table-light">
                                <tr>
                                    <th>No</th>
                                    <th>Name</th>
                                    <th>EID</th>
                                    <th>Status</th>
                                    <th>Department</th>
                                    <th>Role</th>
                                    <th>Education</th>
                                    <th>Major</th>
                                    <th>Join Date</th>
                                    <th>Resign Date</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {employees.map((employee, index) => (
                                    <tr key={employee.eid}>
                                        <td>{index + 1}</td>
                                        <td>
                                            <div className="d-flex align-items-center">
                                                <img 
                                                    src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=32`}
                                                    alt={employee.name}
                                                    className="rounded-circle me-2"
                                                    width="32"
                                                    height="32"
                                                />
                                                {employee.name}
                                            </div>
                                        </td>
                                        <td>
                                            <span className="text-primary">{getEID(employee.eid)}</span>
                                        </td>
                                        <td>{getStatusBadge(employee.status)}</td>
                                        <td>{getDivisionName(employee.division_id)}</td>
                                        <td>{employee.role || 'Programmer'}</td>
                                        <td>{employee.education_level || 'S1'}</td>
                                        <td>{employee.major || 'IT'}</td>
                                        <td>{formatDate(employee.join_date)}</td>
                                        <td>{formatDate(employee.resign_date)}</td>
                                        <td>
                                            <Link 
                                                to={`/employees/${employee.eid}`}
                                                className="btn btn-sm btn-outline-primary"
                                            >
                                                View Contract
                                            </Link>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* Add Employee Modal */}
            {showAddModal && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Tambah Karyawan Baru</h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => setShowAddModal(false)}
                                ></button>
                            </div>
                            <form onSubmit={handleAddEmployee}>
                                <div className="modal-body">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>Nama Lengkap</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.name}
                                                    onChange={(e) => setNewEmployee({...newEmployee, name: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>Email</label>
                                                <input
                                                    type="email"
                                                    className="form-control"
                                                    value={newEmployee.email}
                                                    onChange={(e) => setNewEmployee({...newEmployee, email: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>
                                    
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>No. HP</label>
                                                <input
                                                    type="tel"
                                                    className="form-control"
                                                    value={newEmployee.phone}
                                                    onChange={(e) => setNewEmployee({...newEmployee, phone: e.target.value})}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>Tanggal Lahir</label>
                                                <input
                                                    type="date"
                                                    className="form-control"
                                                    value={newEmployee.birth_date}
                                                    onChange={(e) => setNewEmployee({...newEmployee, birth_date: e.target.value})}
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="form-group mb-3">
                                        <label>Alamat</label>
                                        <textarea
                                            className="form-control"
                                            rows="3"
                                            value={newEmployee.address}
                                            onChange={(e) => setNewEmployee({...newEmployee, address: e.target.value})}
                                        ></textarea>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>Tingkat Pendidikan</label>
                                                <select
                                                    className="form-control"
                                                    value={newEmployee.education_level}
                                                    onChange={(e) => setNewEmployee({...newEmployee, education_level: e.target.value})}
                                                    required
                                                >
                                                    <option value="">Pilih Tingkat Pendidikan</option>
                                                    <option value="SMA/K">SMA/K</option>
                                                    <option value="D3">D3</option>
                                                    <option value="S1">S1</option>
                                                    <option value="S2">S2</option>
                                                    <option value="S3">S3</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>Jurusan</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.major}
                                                    onChange={(e) => setNewEmployee({...newEmployee, major: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>Role</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    value={newEmployee.role}
                                                    onChange={(e) => setNewEmployee({...newEmployee, role: e.target.value})}
                                                    required
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group mb-3">
                                                <label>Divisi</label>
                                                <select
                                                    className="form-control"
                                                    value={newEmployee.division_id}
                                                    onChange={(e) => setNewEmployee({...newEmployee, division_id: e.target.value})}
                                                    required
                                                >
                                                    <option value="">Pilih Divisi</option>
                                                    {divisions.map(division => (
                                                        <option key={division.division_id} value={division.division_id}>
                                                            {division.division_name}
                                                        </option>
                                                    ))}
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button 
                                        type="button" 
                                        className="btn btn-secondary"
                                        onClick={() => setShowAddModal(false)}
                                    >
                                        Batal
                                    </button>
                                    <button type="submit" className="btn btn-primary">
                                        Tambah Karyawan
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default EmployeeTable;
