                              <label className="form-label small">Alasan Rekomendasi</label>
                              <textarea
                                className="form-control form-control-sm"
                                rows="3"
                                value={recommendationData.reason || ''}
                                onChange={(e) => setRecommendationData(prev => ({
                                  ...prev,
                                  reason: e.target.value
                                }))}
                                placeholder="Jelaskan alasan rekomendasi berdasarkan analisis..."
                              />
                            </div>
                            {/* Dynamic Intelligence Display */}
                            {employeeDetail.dynamic_intelligence && (
                              <div className="alert alert-info p-2 small">
                                <strong>ðŸ§  Dynamic Intelligence Suggestion:</strong><br/>
                                <strong>Status:</strong> {employeeDetail.dynamic_intelligence.match_category}<br/>
                                <strong>Durasi Optimal:</strong> {employeeDetail.dynamic_intelligence.optimal_duration === 0 ? 'Permanent' : `${employeeDetail.dynamic_intelligence.optimal_duration} bulan`}<br/>
                                <strong>Sample Size:</strong> {employeeDetail.dynamic_intelligence.sample_size} profil serupa
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
              <div className="modal-footer">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowRecommendationModal(false)}
                >
                  Batal
                </button>
                <button 
                  type="button"
                  className="btn btn-primary"
                  onClick={submitRecommendation}
                  disabled={!recommendationData.recommendation_type || !recommendationData.reason || 
                                 (recommendationData.recommendation_type !== 'permanent' && 
                                  recommendationData.recommendation_type !== 'terminate' && 
                                  !recommendationData.recommended_duration)}
                >
                  Kirim Rekomendasi ke HR
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Container>
  );
};

export default ManagerDashboard;
