import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../App';
import axios from 'axios';

const Divisions = () => {
    const { user } = useContext(AuthContext);
    const [divisions, setDivisions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showModal, setShowModal] = useState(false);
    const [editingDivision, setEditingDivision] = useState(null);
    const [formData, setFormData] = useState({
        name: '',
        code: '',
        description: ''
    });
    const [error, setError] = useState('');

    useEffect(() => {
        fetchDivisions();
    }, []);

    const fetchDivisions = async () => {
        try {
            const response = await axios.get('http://localhost/web_srk_BI/backend/api/divisions.php', {
                withCredentials: true
            });
            if (response.data.success) {
                setDivisions(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching divisions:', error);
            setError('Gagal memuat data divisi');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const url = 'http://localhost/web_srk_BI/backend/api/divisions.php';
            const method = editingDivision ? 'PUT' : 'POST';
            const data = editingDivision ? { ...formData, id: editingDivision.id } : formData;

            const response = await axios({
                method: method,
                url: url,
                data: data,
                withCredentials: true
            });

            if (response.data.success) {
                setShowModal(false);
                setEditingDivision(null);
                setFormData({ name: '', code: '', description: '' });
                fetchDivisions();
            }
        } catch (error) {
            console.error('Error saving division:', error);
            setError(error.response?.data?.error || 'Gagal menyimpan data divisi');
        }
    };

    const handleEdit = (division) => {
        setEditingDivision(division);
        setFormData({
            name: division.name,
            code: division.code || '',
            description: division.description || ''
        });
        setShowModal(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Apakah Anda yakin ingin menghapus divisi ini?')) {
            try {
                const response = await axios.delete(
                    `http://localhost/web_srk_BI/backend/api/divisions.php?id=${id}`,
                    { withCredentials: true }
                );

                if (response.data.success) {
                    fetchDivisions();
                }
            } catch (error) {
                console.error('Error deleting division:', error);
                setError(error.response?.data?.error || 'Gagal menghapus divisi');
            }
        }
    };

    const resetForm = () => {
        setFormData({ name: '', code: '', description: '' });
        setEditingDivision(null);
        setError('');
    };

    if (user?.role !== 'admin') {
        return (
            <div className="container mt-5">
                <div className="alert alert-danger">
                    Akses ditolak. Hanya admin yang dapat mengelola divisi.
                </div>
            </div>
        );
    }

    if (loading) {
        return (
            <div className="container mt-5">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-12">
                    <div className="d-flex justify-content-between align-items-center mb-4">
                        <h2 className="dashboard-title">Manajemen Divisi</h2>
                        <button 
                            className="btn btn-primary btn-modern"
                            onClick={() => setShowModal(true)}
                        >
                            <i className="fas fa-plus me-2"></i>
                            Tambah Divisi
                        </button>
                    </div>

                    {error && (
                        <div className="alert alert-danger alert-dismissible fade show" role="alert">
                            {error}
                            <button type="button" className="btn-close" onClick={() => setError('')}></button>
                        </div>
                    )}

                    <div className="row">
                        {divisions.map(division => (
                            <div key={division.id} className="col-md-6 col-lg-4 mb-4">
                                <div className="card modern-card">
                                    <div className="card-header bg-primary text-white">
                                        <h5 className="card-title mb-0">{division.name}</h5>
                                        <small className="text-light">Kode: {division.code}</small>
                                    </div>
                                    <div className="card-body">
                                        <p className="card-text">
                                            {division.description || 'Tidak ada deskripsi'}
                                        </p>
                                        <div className="row text-center">
                                            <div className="col-6">
                                                <div className="stat-box">
                                                    <h4 className="text-primary">{division.employee_count}</h4>
                                                    <small className="text-muted">Total Karyawan</small>
                                                </div>
                                            </div>
                                            <div className="col-6">
                                                <div className="stat-box">
                                                    <h4 className="text-success">{division.active_employees}</h4>
                                                    <small className="text-muted">Aktif</small>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="card-footer bg-transparent">
                                        <div className="btn-group w-100" role="group">
                                            <button 
                                                className="btn btn-outline-primary"
                                                onClick={() => handleEdit(division)}
                                            >
                                                <i className="fas fa-edit"></i> Edit
                                            </button>
                                            <button 
                                                className="btn btn-outline-danger"
                                                onClick={() => handleDelete(division.id)}
                                                disabled={division.employee_count > 0}
                                            >
                                                <i className="fas fa-trash"></i> Hapus
                                            </button>
                                        </div>
                                        {division.employee_count > 0 && (
                                            <small className="text-muted mt-2 d-block">
                                                Tidak dapat dihapus karena masih ada karyawan
                                            </small>
                                        )}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>

                    {divisions.length === 0 && (
                        <div className="text-center py-5">
                            <i className="fas fa-building fa-3x text-muted mb-3"></i>
                            <h4 className="text-muted">Belum ada divisi</h4>
                            <p className="text-muted">Tambahkan divisi pertama untuk memulai</p>
                        </div>
                    )}
                </div>
            </div>

            {/* Modal Form */}
            {showModal && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    {editingDivision ? 'Edit Divisi' : 'Tambah Divisi'}
                                </h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => {
                                        setShowModal(false);
                                        resetForm();
                                    }}
                                ></button>
                            </div>
                            <form onSubmit={handleSubmit}>
                                <div className="modal-body">
                                    <div className="mb-3">
                                        <label htmlFor="name" className="form-label">Nama Divisi *</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="name"
                                            value={formData.name}
                                            onChange={(e) => setFormData({...formData, name: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="code" className="form-label">Kode Divisi</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="code"
                                            value={formData.code}
                                            onChange={(e) => setFormData({...formData, code: e.target.value})}
                                            placeholder="Otomatis jika kosong"
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="description" className="form-label">Deskripsi</label>
                                        <textarea
                                            className="form-control"
                                            id="description"
                                            rows="3"
                                            value={formData.description}
                                            onChange={(e) => setFormData({...formData, description: e.target.value})}
                                            placeholder="Deskripsi divisi (opsional)"
                                        ></textarea>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button 
                                        type="button" 
                                        className="btn btn-secondary"
                                        onClick={() => {
                                            setShowModal(false);
                                            resetForm();
                                        }}
                                    >
                                        Batal
                                    </button>
                                    <button type="submit" className="btn btn-primary">
                                        {editingDivision ? 'Update' : 'Simpan'}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
            {showModal && <div className="modal-backdrop fade show"></div>}
        </div>
    );
};

export default Divisions;
