import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Table, Button, Badge, Nav, Tab, Alert } from 'react-bootstrap';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const ManagerDashboard = () => {
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [showEmployeeModal, setShowEmployeeModal] = useState(false);
  const [showRecommendationModal, setShowRecommendationModal] = useState(false);
  const [employeeDetail, setEmployeeDetail] = useState(null);
  const [recommendationData, setRecommendationData] = useState({
    recommendation_type: '',
    recommended_duration: '',
    reason: ''
  });
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });
  const [processedRecommendations, setProcessedRecommendations] = useState([]);

  const user = JSON.parse(localStorage.getItem('user') || '{}');

  useEffect(() => {
    fetchManagerData();
    fetchProcessedRecommendations();
  }, []);

  const fetchManagerData = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/employees.php?action=manager_analytics', {
        withCredentials: true,
        timeout: 15000
      });

      if (response.data.success) {
        setAnalytics(response.data.data);
      } else {
        setError(response.data.error || 'Failed to fetch data');
      }
    } catch (err) {
      console.error('Analytics error:', err);
      setError('Network error or session expired');
    } finally {
      setLoading(false);
    }
  };

  const fetchProcessedRecommendations = async () => {
    try {
      const response = await axios.get('/recommendations.php', {
        withCredentials: true,
        timeout: 10000
      });

      if (response.data.success) {
        const processed = response.data.data.filter(rec => 
          rec.status === 'approved' || rec.status === 'rejected'
        );
        setProcessedRecommendations(processed);
      }
    } catch (err) {
      console.error('Processed recommendations error:', err);
    }
  };

  const fetchEmployeeDetail = async (eid) => {
    try {
      console.log(`🔍 Fetching employee detail for EID: ${eid}`);
      const response = await axios.get(`/employees.php?action=employee_data_mining_detail&eid=${eid}`, {
        withCredentials: true,
        timeout: 15000
      });

      console.log('✅ Employee detail response:', response.data);

      if (response.data.success) {
        setEmployeeDetail(response.data.data);
        return response.data.data;
      } else {
        console.error('❌ Employee detail API error:', response.data.error);
        throw new Error(response.data.error || 'Failed to fetch employee detail');
      }
    } catch (error) {
      console.error('❌ Network/Parse error:', error);
      setEmployeeDetail({
        error: true,
        errorMessage: error.message,
        employee: { eid, name: 'Unknown' }
      });
      throw new Error('Server returned invalid JSON. Check browser console for details.');
    }
  };

  const handleEmployeeClick = async (employee) => {
    try {
      setShowEmployeeModal(true);
      setEmployeeDetail(null);

      console.log(`🔍 Loading detail for employee:`, employee);

      const detail = await fetchEmployeeDetail(employee.eid);
      console.log('✅ Employee detail loaded successfully');

    } catch (error) {
      console.error('❌ Failed to fetch employee detail:', error);
      showAlert(`Gagal memuat detail karyawan: ${error.message}`, 'danger');
    }
  };

  const handleCreateRecommendation = async (employee) => {
    try {
      setShowRecommendationModal(true);
      setEmployeeDetail(null);

      const detail = await fetchEmployeeDetail(employee.eid);

      const category = detail.dynamic_intelligence?.match_category;
      const duration = detail.dynamic_intelligence?.optimal_duration;

      let defaultType = '';
      let defaultDuration = '';
      let defaultReason = `Berdasarkan dynamic intelligence analysis: ${category}. `;

      if (category === 'Siap Permanent' || (employee.current_contract_type === '3' && duration === 0)) {
        defaultType = 'permanent';
        defaultDuration = 0;
        defaultReason += 'Karyawan layak untuk kontrak permanent.';
      } else if (duration > 0) {
        defaultType = employee.current_contract_type === '2' ? 'kontrak3' : 'kontrak2';
        defaultDuration = duration;
        defaultReason += `Rekomendasi durasi ${duration} bulan berdasarkan analisis historical patterns.`;
      }

      setRecommendationData({
        eid: employee.eid,
        employee_name: employee.name,
        recommendation_type: defaultType,
        recommended_duration: defaultDuration,
        reason: defaultReason + (detail.dynamic_intelligence?.reasoning?.[0] || ' Analisis data mining menunjukkan tingkat kecocokan yang baik.')
      });

    } catch (error) {
      console.error('Failed to load employee for recommendation:', error);
      showAlert(`Gagal memuat data karyawan: ${error.message}`, 'danger');
    }
  };

  const submitRecommendation = async () => {
    try {
      const response = await axios.post('/employees.php', {
        action: 'recommend',
        ...recommendationData
      }, {
        withCredentials: true,
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (response.data.success) {
        showAlert('Rekomendasi berhasil dikirim ke HR!', 'success');
        setShowRecommendationModal(false);
        fetchManagerData();
      } else {
        showAlert(`Gagal mengirim rekomendasi: ${response.data.error}`, 'danger');
      }
    } catch (error) {
      console.error('Submit recommendation error:', error);
      showAlert('Error mengirim rekomendasi. Silakan coba lagi.', 'danger');
    }
  };

  const showAlert = (message, type) => {
    setAlert({ show: true, message, type });
    setTimeout(() => setAlert({ show: false, message: '', type: '' }), 5000);
  };

  const getMatchLevelBadge = (matchStatus) => {
    if (matchStatus === 'Match') return <Badge bg="success">Match</Badge>;
    if (matchStatus === 'Unmatch') return <Badge bg="danger">Unmatch</Badge>;
    return <Badge bg="secondary">N/A</Badge>;
  };

  const getContractStatusBadge = (contractType, tenure) => {
    if (contractType === 'permanent') return <Badge bg="success">Permanent</Badge>;
    if (contractType === 'probation') return <Badge bg="warning">Probation</Badge>;
    if (contractType === '3' || contractType === 'kontrak3') return <Badge bg="info">Kontrak 3</Badge>;
    if (contractType === '2' || contractType === 'kontrak2') return <Badge bg="primary">Kontrak 2</Badge>;
    return <Badge bg="secondary">N/A</Badge>;
  };

  const getRecommendationStatusBadge = (employee) => {
    if (employee.current_contract_type === 'permanent') {
      return <Badge bg="secondary">-</Badge>;
    } else if (employee.education_job_match === 'Unmatch') {
      return <Badge bg="danger">TIDAK DIREKOMENDASIKAN</Badge>;
    } else {
      const existingRecommendation = processedRecommendations.find(rec => rec.employee_id === employee.eid);
      if (existingRecommendation && existingRecommendation.recommended_duration) {
        return <Badge bg="success">{existingRecommendation.recommended_duration} bulan</Badge>;
      } else {
        const duration = employee.data_mining_recommendation?.recommended_duration;

        if ((employee.current_contract_type === '3' || employee.current_contract_type === 'kontrak3') && duration === 0) {
          return <Badge bg="success">PERMANENT</Badge>;
        } else if (duration && duration > 0) {
          return <Badge bg="success">{duration} bulan</Badge>;
        } else {
          if (employee.current_contract_type === '3') {
            return <Badge bg="info">PERMANENT</Badge>;
          } else if (employee.current_contract_type === '2') {
            return <Badge bg="warning">6 bulan (Evaluasi)</Badge>;
          } else {
            return <Badge bg="secondary">Tidak Ada Data</Badge>;
          }
        }
      }
    }
  };

  if (loading) {
    return (
      <div className="container-fluid mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Memuat data intelligence...</p>
        </div>
      </div>
    );
  }

  return (
    <Container fluid className="mt-3">
      {alert.show && (
        <Alert variant={alert.type} dismissible onClose={() => setAlert({ show: false, message: '', type: '' })}>
          {alert.message}
        </Alert>
      )}

      <div className="mb-4 p-4 rounded" style={{
        background: 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)',
        color: 'white'
      }}>
        <Row className="align-items-center">
          <Col>
            <h2>Manager Dashboard - Data Intelligence</h2>
            <p className="mb-0">Analisis Kecocokan Pendidikan & Rekomendasi Kontrak untuk Divisi</p>
            <small>{user.division_name} | Business Intelligence System</small>
          </Col>
          <Col xs="auto">
            <div className="text-end">
              <h3 className="mb-0">{analytics?.division_stats?.total_employees || 0}</h3>
              <small>Karyawan Aktif</small>
            </div>
          </Col>
        </Row>
      </div>

      <Tab.Container activeKey={activeTab} onSelect={setActiveTab}>
        <Nav variant="tabs" className="mb-4">
          <Nav.Item>
            <Nav.Link eventKey="employees" style={{
              color: activeTab === 'employees' ? '#fff' : '#007bff',
              backgroundColor: activeTab === 'employees' ? '#007bff' : 'transparent',
              borderColor: '#007bff'
            }}>
              Detail Karyawan
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="processed" style={{
              color: activeTab === 'processed' ? '#fff' : '#007bff',
              backgroundColor: activeTab === 'processed' ? '#007bff' : 'transparent',
              borderColor: '#007bff'
            }}>
              Rekomendasi Diproses ({processedRecommendations.length})
            </Nav.Link>
          </Nav.Item>
        </Nav>

        <Tab.Content>
          <Tab.Pane eventKey="employees">
            <Card>
              <Card.Header style={{ backgroundColor: '#007bff', color: 'white' }}>
                <h5 className="mb-0">👥 Detail Karyawan Divisi</h5>
              </Card.Header>
              <Card.Body>
                <Table responsive hover>
                  <thead style={{ backgroundColor: '#e3f2fd' }}>
                    <tr>
                      <th>Nama & Departemen</th>
                      <th>Pendidikan</th>
                      <th>Join Date</th>
                      <th>Years Of Service</th>
                      <th>Status Kontrak</th>
                      <th>Status Match</th>
                      <th>Rekomendasi Kontrak</th>
                      <th>Status Rekomendasi</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {analytics?.employees.map((emp, index) => (
                      <tr key={index}>
                        <td>
                          <div>
                            <strong>{emp.name}</strong>
                            <br />
                            <small className="text-muted">{emp.role}</small>
                          </div>
                        </td>
                        <td>
                          {emp.major}
                          <br />
                          <small className="text-muted">{emp.education_level}</small>
                        </td>
                        <td>{new Date(emp.join_date).toLocaleDateString('id-ID')}</td>
                        <td>
                          <Badge bg="info">{emp.tenure_months} bulan</Badge>
                        </td>
                        <td>
                          {getContractStatusBadge(emp.current_contract_type, emp.days_to_contract_end)}
                        </td>
                        <td>
                          <div className="d-flex align-items-center">
                            {getMatchLevelBadge(emp.education_job_match)}
                          </div>
                        </td>
                        <td>
                          {emp.current_contract_type === 'permanent' ? (
                            <span>-</span>
                          ) : emp.education_job_match === 'Unmatch' ? (
                            <Badge bg="warning">
                              {(() => {
                                const duration = emp.data_mining_recommendation?.recommended_duration;
                                if (duration && duration > 0) {
                                  return `${duration} bulan (Evaluasi)`;
                                } else {
                                  return '6 bulan (Evaluasi)';
                                }
                              })()}
                            </Badge>
                          ) : (
                          <Badge bg="success">
                            {(() => {
                              const duration = emp.data_mining_recommendation?.recommended_duration;
                              if ((emp.current_contract_type === '3' || emp.current_contract_type === 'kontrak3') && 
                                  (duration === 0 || duration === null)) {
                                return 'Permanent';
                              } else if (duration && duration > 0) {
                                return `${duration} bulan`;
                              } else {
                                return '6 bulan (Default)';
                              }
                            })()}
                          </Badge>
                          )}
                        </td>
                        <td>
                          {getRecommendationStatusBadge(emp)}
                        </td>
                        <td>
                          <div className="d-flex gap-2">
                            <Button 
                              size="sm" 
                              variant="outline-primary"
                              onClick={() => handleEmployeeClick(emp)}
                            >
                              Detail
                            </Button>
                            {emp.current_contract_type === 'permanent' ? (
                              <Button 
                                size="sm" 
                                variant="outline-danger"
                                disabled
                                title="Karyawan memiliki kontrak permanent"
                                style={{ 
                                  backgroundColor: '#f8f9fa',
                                  borderColor: '#dc3545',
                                  color: '#dc3545',
                                  cursor: 'not-allowed',
                                  opacity: 0.7,
                                  fontSize: '0.7rem',
                                  padding: '0.25rem 0.4rem',
                                  lineHeight: '1.2'
                                }}
                              >
                                ❌ Tidak Dapat<br/>Direkomendasikan
                              </Button>
                            ) : (
                              <Button 
                                size="sm" 
                                variant="success"
                                onClick={() => handleCreateRecommendation(emp)}
                                disabled={
                                  emp.has_pending_recommendation || 
                                  emp.days_to_contract_end > 30
                                }
                                title={
                                  emp.has_pending_recommendation ? 'Sudah ada rekomendasi pending' :
                                  emp.days_to_contract_end > 30 ? `Kontrak masih berlaku ${emp.days_to_contract_end} hari lagi` :
                                  'Buat rekomendasi untuk karyawan ini'
                                }
                              >
                                Rekomendasi
                              </Button>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Tab.Pane>

          <Tab.Pane eventKey="processed">
            <Card>
              <Card.Header style={{ backgroundColor: '#28a745', color: 'white' }} className="d-flex justify-content-between align-items-center">
                <div>
                  <h5 className="mb-0">✅ Rekomendasi yang Sudah Diproses HR</h5>
                  <small>Lihat status rekomendasi Anda dan kontrak yang telah diperpanjang</small>
                </div>
                <Button 
                  variant="outline-light" 
                  size="sm" 
                  onClick={fetchProcessedRecommendations}
                  className="ms-2"
                >
                  🔄 Refresh
                </Button>
              </Card.Header>
              <Card.Body>
                {processedRecommendations.length > 0 ? (
                  <Table responsive hover>
                    <thead style={{ backgroundColor: '#e8f5e8' }}>
                      <tr>
                        <th>Karyawan</th>
                        <th>Rekomendasi Anda</th>
                        <th>Status HR</th>
                        <th>Kontrak Baru</th>
                        <th>Tanggal Diproses</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {processedRecommendations.map((rec, index) => (
                        <tr key={index}>
                          <td>
                            <div>
                              <strong>{rec.employee.name}</strong>
                              <br />
                              <small className="text-muted">{rec.employee.role}</small>
                            </div>
                          </td>
                          <td>
                            <Badge bg="primary" className="me-1">
                              {rec.recommendation_type === 'kontrak1' ? 'Kontrak 1' :
                               rec.recommendation_type === 'kontrak2' ? 'Kontrak 2' :
                               rec.recommendation_type === 'kontrak3' ? 'Kontrak 3' : rec.recommendation_type}
                            </Badge>
                            <br />
                            <small className="text-muted">{rec.recommended_duration} bulan</small>
                          </td>
                          <td>
                            {rec.status === 'approved' ? (
                              <Badge bg="success">✅ Disetujui</Badge>
                            ) : (
                              <Badge bg="danger">❌ Ditolak</Badge>
                            )}
                          </td>
                          <td>
                             {rec.status === 'approved' && rec.new_contract?.is_created ? (
                               <Badge bg="success">🎉 Kontrak Berhasil Dibuat</Badge>
                             ) : rec.status === 'approved' ? (
                               <Badge bg="warning">⏳ Kontrak sedang dibuat</Badge>
                             ) : (
                               <small className="text-muted">Tidak ada kontrak baru</small>
                             )}
                           </td>
                          <td>
                            {new Date(rec.updated_at).toLocaleDateString('id-ID')}
                          </td>
                          <td>
                             {rec.status === 'approved' && rec.new_contract?.is_created ? (
                               <Badge bg="success">🎉 Kontrak Berhasil Diperpanjang</Badge>
                             ) : rec.status === 'approved' ? (
                               <Badge bg="warning">⏳ Sedang Memproses Kontrak</Badge>
                             ) : (
                               <Badge bg="danger">❌ Rekomendasi Ditolak</Badge>
                             )}
                           </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                ) : (
                  <div className="text-center py-5">
                    <div className="display-1 mb-3">📋</div>
                    <h5>Belum Ada Rekomendasi yang Diproses</h5>
                    <p className="text-muted">
                      Rekomendasi yang Anda buat akan muncul di sini setelah diproses oleh HR.
                    </p>
                  </div>
                )}
              </Card.Body>
            </Card>
          </Tab.Pane>
        </Tab.Content>
      </Tab.Container>

      {/* Employee Detail Modal - COMPACT NO SCROLL */}
      {showEmployeeModal && (
        <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-xl">
            <div className="modal-content">
              <div className="modal-header py-3" style={{ background: 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)', color: 'white' }}>
                <h5 className="modal-title">
                  <i className="fas fa-user-chart me-2"></i>
                  {employeeDetail?.employee?.name} - Data Intelligence
                </h5>
                <button 
                  type="button" 
                  className="btn-close btn-close-white"
                  onClick={() => setShowEmployeeModal(false)}
                ></button>
              </div>

              <div className="modal-body p-4">
                {employeeDetail && !employeeDetail?.error ? (
                  <div className="row g-4">
                    <div className="col-lg-6">
                      <div className="card border-0 shadow-sm h-100">
                        <div className="card-header py-3 bg-primary text-white">
                          <h6 className="mb-0">
                            <i className="fas fa-user me-2"></i>
                            Informasi Karyawan
                          </h6>
                        </div>
                        <div className="card-body">
                          <div className="mb-3">
                            <small className="text-muted d-block">Nama</small>
                            <div className="fw-bold text-dark fs-5">{employeeDetail.employee.name}</div>
                          </div>
                          <div className="row">
                            <div className="col-6">
                              <div className="mb-3">
                                <small className="text-muted d-block">Posisi</small>
                                <div className="fw-bold text-dark">{employeeDetail.employee.role}</div>
                              </div>
                              <div className="mb-3">
                                <small className="text-muted d-block">Tenure</small>
                                <div className="fw-bold text-primary">{Math.round(employeeDetail.employee.tenure_months)} bulan</div>
                              </div>
                            </div>
                            <div className="col-6">
                              <div className="mb-3">
                                <small className="text-muted d-block">Divisi</small>
                                <div className="fw-bold text-dark">{employeeDetail.employee.division_name}</div>
                              </div>
                              <div className="mb-3">
                                <small className="text-muted d-block">Pendidikan</small>
                                <div className="fw-bold text-dark">{employeeDetail.employee.education_level} - {employeeDetail.employee.major}</div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="col-lg-6">
                      <div className="card border-0 shadow-sm h-100">
                        <div className="card-header py-3 bg-success text-white">
                          <h6 className="mb-0">
                            <i className="fas fa-brain me-2"></i>
                            Data Intelligence
                          </h6>
                        </div>
                        <div className="card-body">
                          <div className="row">
                            <div className="col-6">
                              <div className="mb-3">
                                <small className="text-muted d-block">Status</small>
                                <div className="fw-bold text-success">{employeeDetail.dynamic_intelligence?.match_category || 'Tidak Ada Data'}</div>
                              </div>
                              <div className="mb-3">
                                <small className="text-muted d-block">Sample Size</small>
                                <div className="fw-bold text-dark">{employeeDetail.dynamic_intelligence?.sample_size || 0} profil</div>
                              </div>
                            </div>
                            <div className="col-6">
                              <div className="mb-3">
                                <small className="text-muted d-block">Rekomendasi</small>
                                <div className="fw-bold text-primary fs-5">
                                  {(() => {
                                    const duration = employeeDetail.dynamic_intelligence?.optimal_duration;
                                    const category = employeeDetail.dynamic_intelligence?.match_category;

                                    if (category === 'Sudah Permanent') {
                                      return 'Sudah Permanent';
                                    } else if (employeeDetail.employee.current_contract_type === '3' && 
                                        (category === 'Siap Permanent' || duration === 0)) {
                                      return 'Permanent';
                                    } else if (duration === 0) {
                                      return 'Permanent';
                                    } else if (duration && duration > 0) {
                                      return `${duration} bulan`;
                                    } else {
                                      return 'Perlu Evaluasi';
                                    }
                                  })()}
                                </div>
                              </div>
                            </div>
                          </div>

                          {employeeDetail.employee.current_contract_type !== 'permanent' && employeeDetail.dynamic_intelligence?.historical_patterns && (
                            <div className="mt-4">
                              <div className="row text-center">
                                <div className="col-4">
                                  <div className="p-2 bg-light rounded">
                                    <small className="text-muted d-block">Masih Aktif</small>
                                    <div className="fw-bold text-success">{employeeDetail.dynamic_intelligence.historical_patterns.success_rate || 0}%</div>
                                  </div>
                                </div>
                                <div className="col-4">
                                  <div className="p-2 bg-light rounded">
                                    <small className="text-muted d-block">Sudah Resign</small>
                                    <div className="fw-bold text-danger">{employeeDetail.dynamic_intelligence.historical_patterns.resign_rate || 0}%</div>
                                  </div>
                                </div>
                                <div className="col-4">
                                  <div className="p-2 bg-light rounded">
                                    <small className="text-muted d-block">Rata-rata Tenure</small>
                                    <div className="fw-bold text-primary">
                                      {employeeDetail.dynamic_intelligence.historical_patterns.avg_duration ? 
                                       employeeDetail.dynamic_intelligence.historical_patterns.avg_duration.toFixed(1) : '0'} bulan
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          )}

                          {employeeDetail.dynamic_intelligence?.historical_patterns?.profiles?.length > 0 && (
                            <div className="mt-4">
                              <div className="d-flex justify-content-between align-items-center mb-3">
                                <h6 className="mb-0 text-muted">
                                  <i className="fas fa-users me-2"></i>
                                  Profil Serupa ({employeeDetail.dynamic_intelligence.historical_patterns.total_similar || 0} total)
                                </h6>
                              </div>
                              <div className="row g-2">
                                {employeeDetail.dynamic_intelligence.historical_patterns.profiles.slice(0, 4).map((profile, idx) => (
                                  <div key={profile.eid} className="col-6">
                                    <div className="card border-0 bg-light" style={{ fontSize: '0.8rem' }}>
                                      <div className="card-body p-2">
                                        <div className="fw-bold text-primary mb-1 text-truncate">{profile.name}</div>
                                        <div className="text-muted small">{profile.major} → {profile.role}</div>
                                        <div className={`fw-bold ${profile.status_summary === 'Aktif' ? 'text-success' : 'text-danger'} small`}>
                                          {profile.status_summary} ({Math.round(profile.total_tenure_months)} bulan)
                                        </div>
                                      </div>
                                    </div>
                                  </div>
                                ))}
                              </div>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="alert alert-danger border-0 shadow-sm">
                    <h4 className="alert-heading">
                      <i className="fas fa-exclamation-triangle me-2"></i>
                      Gagal memuat detail karyawan
                    </h4>
                    <p>Terjadi kesalahan saat mengambil data analisis karyawan. Silakan coba lagi atau hubungi administrator sistem.</p>
                  </div>
                )}
              </div>
              <div className="modal-footer py-3 bg-light">
                <button 
                  type="button" 
                  className="btn btn-secondary px-4"
                  onClick={() => setShowEmployeeModal(false)}
                >
                  <i className="fas fa-times me-2"></i>
                  Tutup
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Recommendation Modal - COMPACT */}
      {showRecommendationModal && (
        <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-xl">
            <div className="modal-content">
              <div className="modal-header py-3" style={{ background: 'linear-gradient(135deg, #28a745 0%, #20c997 100%)', color: 'white' }}>
                <h5 className="modal-title">
                  <i className="fas fa-file-contract me-2"></i>
                  {employeeDetail?.employee?.name} - Buat Rekomendasi Kontrak
                </h5>
                <button 
                  type="button" 
                  className="btn-close btn-close-white"
                  onClick={() => setShowRecommendationModal(false)}
                ></button>
              </div>

              <div className="modal-body p-4">
                {employeeDetail && (
                  <div className="row g-4">
                    <div className="col-lg-6">
                      <div className="card border-0 shadow-sm h-100">
                        <div className="card-header py-3 bg-primary text-white">
                          <h6 className="mb-0">
                            <i className="fas fa-user me-2"></i>
                            Informasi Karyawan
                          </h6>
                        </div>
                        <div className="card-body">
                          <div className="mb-3">
                            <small className="text-muted d-block">Nama</small>
                            <div className="fw-bold text-dark fs-5">{employeeDetail.employee.name}</div>
                          </div>
                          <div className="row">
                            <div className="col-6">
                              <div className="mb-3">
                                <small className="text-muted d-block">Posisi</small>
                                <div className="fw-bold text-dark">{employeeDetail.employee.role}</div>
                              </div>
                              <div className="mb-3">
                                <small className="text-muted d-block">Tenure</small>
                                <div className="fw-bold text-primary">{Math.round(employeeDetail.employee.tenure_months)} bulan</div>
                              </div>
                            </div>
                            <div className="col-6">
                              <div className="mb-3">
                                <small className="text-muted d-block">Divisi</small>
                                <div className="fw-bold text-dark">{employeeDetail.employee.division_name}</div>
                              </div>
                              <div className="mb-3">
                                <small className="text-muted d-block">Pendidikan</small>
                                <div className="fw-bold text-dark">{employeeDetail.employee.education_level} - {employeeDetail.employee.major}</div>
                              </div>
                            </div>
                          </div>

                          {employeeDetail.dynamic_intelligence && (
                            <div className="card bg-light mt-3">
                              <div className="card-body p-3">
                                <h6 className="card-title text-success mb-2">
                                  <i className="fas fa-brain me-1"></i>
                                  Dynamic Intelligence
                                </h6>
                                <div className="small">
                                  <div><strong>Status:</strong> {employeeDetail.dynamic_intelligence.match_category}</div>
                                  <div><strong>Rekomendasi:</strong> 
                                    {(() => {
                                      const duration = employeeDetail.dynamic_intelligence?.optimal_duration;
                                      const category = employeeDetail.dynamic_intelligence?.match_category;

                                      if (category === 'Sudah Permanent') {
                                        return ' Sudah Permanent';
                                      } else if (employeeDetail.employee.current_contract_type === '3' && 
                                          (category === 'Siap Permanent' || duration === 0)) {
                                        return ' Permanent';
                                      } else if (duration === 0) {
                                        return ' Permanent';
                                      } else if (duration && duration > 0) {
                                        return ` ${duration} bulan`;
                                      } else {
                                        return ' Perlu Evaluasi';
                                      }
                                    })()}
                                  </div>
                                  <div><strong>Sample Size:</strong> {employeeDetail.dynamic_intelligence.sample_size || 0} profil serupa</div>
                                </div>
                              </div>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>

                    <div className="col-lg-6">
                      <div className="card border-0 shadow-sm h-100">
                        <div className="card-header py-3 bg-success text-white">
                          <h6 className="mb-0">
                            <i className="fas fa-file-contract me-2"></i>
                            Form Rekomendasi
                          </h6>
                        </div>
                        <div className="card-body">
                          <div className="mb-3">
                            <label className="form-label fw-bold">
                              <i className="fas fa-contract me-2"></i>
                              Jenis Kontrak
                            </label>
                            <select
                              className="form-select"
                              value={recommendationData.recommendation_type || ''}
                              onChange={(e) => {
                                const newType = e.target.value;
                                setRecommendationData(prev => ({
                                  ...prev,
                                  recommendation_type: newType,
                                  recommended_duration: (newType === 'permanent' || newType === 'terminate') ? 0 : prev.recommended_duration
                                }));
                              }}
                            >
                              <option value="">Pilih jenis kontrak...</option>
                              <option value="kontrak2">Kontrak 2</option>
                              <option value="kontrak3">Kontrak 3</option>
                              <option value="permanent">Permanent</option>
                              <option value="terminate">Tidak Diperpanjang</option>
                            </select>
                          </div>

                          <div className="mb-3">
                            <label className="form-label fw-bold">
                              <i className="fas fa-calendar-alt me-2"></i>
                              Durasi (bulan)
                            </label>
                            <input
                              type="number"
                              className="form-control"
                              min="1"
                              max="36"
                              value={recommendationData.recommended_duration || ''}
                              onChange={(e) => setRecommendationData(prev => ({
                                ...prev,
                                recommended_duration: parseInt(e.target.value) || 0
                              }))}
                              disabled={recommendationData.recommendation_type === 'permanent' || recommendationData.recommendation_type === 'terminate'}
                              placeholder={
                                recommendationData.recommendation_type === 'permanent' ? 'Permanent - tidak perlu durasi' :
                                recommendationData.recommendation_type === 'terminate' ? 'Kontrak berakhir' :
                                'Masukkan durasi dalam bulan (1-36)'
                              }
                            />
                            {recommendationData.recommendation_type === 'permanent' && (
                              <div className="mt-2">
                                <small className="text-success">
                                  <i className="fas fa-info-circle me-1"></i>
                                  Permanent - tidak memerlukan durasi
                                </small>
                              </div>
                            )}
                            {recommendationData.recommendation_type === 'terminate' && (
                              <div className="mt-2">
                                <small className="text-danger">
                                  <i className="fas fa-exclamation-triangle me-1"></i>
                                  Tidak diperpanjang - kontrak berakhir
                                </small>
                              </div>
                            )}
                          </div>

                          <div className="mb-4">
                            <label className="form-label fw-bold">
                              <i className="fas fa-comment-alt me-2"></i>
                              Alasan Rekomendasi
                            </label>
                            <textarea
                              className="form-control"
                              rows="4"
                              value={recommendationData.reason || ''}
                              onChange={(e) => setRecommendationData(prev => ({
                                ...prev,
                                reason: e.target.value
                              }))}
                              placeholder="Jelaskan alasan rekomendasi berdasarkan analisis data, performa karyawan, dan kebutuhan perusahaan..."
                              style={{ resize: 'vertical' }}
                            />
                            <div className="form-text">
                              <i className="fas fa-lightbulb me-1"></i>
                              Tip: Sertakan justifikasi berdasarkan match status, tenure, dan analisis data mining
                            </div>
                          </div>

                          <div className="text-center">
                            <button 
                              type="button" 
                              className="btn btn-success btn-lg px-5"
                              onClick={submitRecommendation}
                              disabled={!recommendationData.recommendation_type || !recommendationData.reason}
                            >
                              <i className="fas fa-paper-plane me-2"></i>
                              Kirim Rekomendasi ke HR
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>

              <div className="modal-footer py-3 bg-light">
                <button 
                  type="button" 
                  className="btn btn-secondary px-4"
                  onClick={() => setShowRecommendationModal(false)}
                >
                  <i className="fas fa-times me-2"></i>
                  Tutup
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Container>
  );
};

export default ManagerDashboard;
