                                          <div className={`fw-bold ${profile.status_summary === 'Aktif' ? 'text-success' : 'text-danger'}`} style={{ fontSize: '0.8rem', lineHeight: '1.2' }}>
                                            <i className={`fas ${profile.status_summary === 'Aktif' ? 'fa-check-circle' : 'fa-times-circle'} me-1`}></i>
                                            {profile.status_summary} ({Math.round(profile.total_tenure_months)} bulan)
                                          </div>
                                        </div>
                                      </div>
                                    </div>
                                  </div>
                                ))}
                              </div>
                            ) : (
                              <div className="text-center py-4">
                                <i className="fas fa-users fa-3x text-muted mb-3"></i>
                                <div className="text-muted">Tidak ada profil serupa untuk referensi</div>
                                <small className="text-muted">Belum ada data karyawan dengan background yang sama</small>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* FORM REKOMENDASI KONTRAK - IMPROVED UI */}
                    <div className="row">
                      <div className="col-12">
                        <div className="card border-0 shadow-sm">
                          <div className="card-header py-3" style={{ background: 'linear-gradient(135deg, #fd7e14 0%, #ffc107 100%)', color: 'white' }}>
                            <h6 className="mb-0">
                              <i className="fas fa-file-signature me-2"></i>
                              Form Rekomendasi Kontrak
                            </h6>
                            <small className="opacity-75">Pilih tipe kontrak dan durasi yang sesuai berdasarkan analisis data</small>
                          </div>
                          <div className="card-body p-4">
                            {/* Dynamic Intelligence Suggestion */}
                            {employeeDetail.dynamic_intelligence && (
                              <div className="alert alert-info border-0 shadow-sm mb-4">
                                <div className="d-flex align-items-center">
                                  <i className="fas fa-brain fa-2x text-info me-3"></i>
                                  <div>
                                    <h6 className="alert-heading mb-2">
                                      <i className="fas fa-lightbulb me-1"></i>
                                      Dynamic Intelligence Suggestion
                                    </h6>
                                    <div className="row">
                                      <div className="col-md-4">
                                        <small className="text-muted d-block">Status Kategori</small>
                                        <strong>{employeeDetail.dynamic_intelligence?.match_category}</strong>
                                      </div>
                                      <div className="col-md-4">
                                        <small className="text-muted d-block">Durasi Optimal</small>
                                        <strong className="text-primary">
                                          {employeeDetail.dynamic_intelligence?.optimal_duration === 0 ? 'Permanent' : `${employeeDetail.dynamic_intelligence?.optimal_duration} bulan`}
                                        </strong>
                                      </div>
                                      <div className="col-md-4">
                                        <small className="text-muted d-block">Sample Size</small>
                                        <strong>{employeeDetail.dynamic_intelligence?.sample_size} profil serupa</strong>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            )}

                            <div className="row">
                              <div className="col-md-6">
                                <div className="mb-4">
                                  <label className="form-label fw-bold">
                                    <i className="fas fa-clipboard-list me-2"></i>
                                    Jenis Rekomendasi
                                  </label>
                                  <select 
                                    className="form-select form-select-lg"
                                    value={recommendationData.recommendation_type || ''}
                                    onChange={(e) => {
                                      const newType = e.target.value;
                                      setRecommendationData(prev => ({
                                        ...prev,
                                        recommendation_type: newType,
                                        recommended_duration: newType === 'permanent' ? 0 : (newType === 'terminate' ? 0 : prev.recommended_duration)
                                      }));
                                    }}
                                  >
                                    <option value="">Pilih Jenis Kontrak</option>
                                    <option value="kontrak2">Kontrak 2</option>
                                    <option value="kontrak3">Kontrak 3</option>
                                    <option value="permanent">Permanent</option>
                                    <option value="terminate">Tidak Diperpanjang</option>
                                  </select>
                                </div>
                              </div>
                              <div className="col-md-6">
                                <div className="mb-4">
                                  <label className="form-label fw-bold">
                                    <i className="fas fa-calendar-alt me-2"></i>
                                    Durasi Optimal (bulan)
                                  </label>
                                  <input
                                    type="number"
                                    className="form-control form-control-lg"
                                    value={recommendationData.recommended_duration || ''}
                                    onChange={(e) => setRecommendationData(prev => ({
                                      ...prev,
                                      recommended_duration: e.target.value
                                    }))}
                                    disabled={recommendationData.recommendation_type === 'permanent' || recommendationData.recommendation_type === 'terminate'}
                                    placeholder="Masukkan durasi dalam bulan"
                                    min="1"
                                    max="36"
                                  />
                                  {recommendationData.recommendation_type === 'permanent' && (
                                    <div className="mt-2">
                                      <small className="text-success">
                                        <i className="fas fa-info-circle me-1"></i>
                                        Permanent - tidak memerlukan durasi
                                      </small>
                                    </div>
                                  )}
                                  {recommendationData.recommendation_type === 'terminate' && (
                                    <div className="mt-2">
                                      <small className="text-danger">
                                        <i className="fas fa-exclamation-triangle me-1"></i>
                                        Tidak diperpanjang - kontrak berakhir
                                      </small>
                                    </div>
                                  )}
                                </div>
                              </div>
                            </div>

                            <div className="mb-4">
                              <label className="form-label fw-bold">
                                <i className="fas fa-comment-alt me-2"></i>
                                Alasan Rekomendasi
                              </label>
                              <textarea
                                className="form-control"
                                rows="4"
                                value={recommendationData.reason || ''}
                                onChange={(e) => setRecommendationData(prev => ({
                                  ...prev,
                                  reason: e.target.value
                                }))}
                                placeholder="Jelaskan alasan rekomendasi berdasarkan analisis data, performa karyawan, dan kebutuhan perusahaan..."
                                style={{ resize: 'vertical' }}
                              />
                              <div className="form-text">
                                <i className="fas fa-lightbulb me-1"></i>
                                Tip: Sertakan justifikasi berdasarkan match status, tenure, dan analisis data mining
                              </div>
                            </div>

                            <div className="text-center">
                              <button 
                                type="button" 
                                className="btn btn-success btn-lg px-5"
                                onClick={submitRecommendation}
                                disabled={!recommendationData.recommendation_type || !recommendationData.reason}
                              >
                                <i className="fas fa-paper-plane me-2"></i>
                                Kirim Rekomendasi ke HR
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
              <div className="modal-footer py-3 bg-light">
                <button 
                  type="button" 
                  className="btn btn-secondary px-4"
                  onClick={() => setShowRecommendationModal(false)}
                >
                  <i className="fas fa-times me-2"></i>
                  Tutup
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Container>
  );
};

export default ManagerDashboard;
