import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
  Filler,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import { useAuth } from '../App';
import { Card, Container, Row, Col, Nav, Tab, Table, Badge, Button, Alert, Modal, Spinner, Form } from 'react-bootstrap';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  Filler
);

const ManagerDashboard = () => {
  const [analytics, setAnalytics] = useState(null);
  const [educationAnalysis, setEducationAnalysis] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const { user } = useAuth();
  const [showEmployeeModal, setShowEmployeeModal] = useState(false);
  const [employeeDetail, setEmployeeDetail] = useState(null);
  const [loadingDetail, setLoadingDetail] = useState(false);
  const [showRecommendationModal, setShowRecommendationModal] = useState(false);
  const [recommendationData, setRecommendationData] = useState({
    recommendation_type: 'extend',
    recommended_duration: '',
    reason: ''
  });
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });
  const [processedRecommendations, setProcessedRecommendations] = useState([]);

  useEffect(() => {
    if (user?.role === 'manager') {
      fetchManagerData();
      fetchProcessedRecommendations();

      // Auto refresh processed recommendations every 30 seconds
      const interval = setInterval(() => {
        fetchProcessedRecommendations();
      }, 30000);

      return () => clearInterval(interval);
    } else if (user?.role === 'hr') {
      showAlert('HR tidak dapat membuat rekomendasi. Hanya manager yang dapat membuat rekomendasi karyawan.', 'warning');
    }
  }, [user]);

  const fetchManagerData = async () => {
    try {
      setLoading(true);

      // Use the session-based endpoint that filters by manager's division
      const timestamp = new Date().getTime();
              const analyticsRes = await axios.get(`http://localhost/web_srk_BI/backend/manager_analytics_by_division.php?t=${timestamp}`, {
        withCredentials: true
      });

      console.log('ðŸ”¥ NEW API Response:', analyticsRes.data);

      if (analyticsRes.data.success) {
        setAnalytics(analyticsRes.data.data);
        console.log('âœ… Analytics data set:', analyticsRes.data.data);

        // Use the same analytics data for education analysis
        if (analyticsRes.data.data.employees) {
          const employees = analyticsRes.data.data.employees;
          const matchDistribution = {
            'Match': employees.filter(emp => emp.education_job_match === 'Match').length,
            'Unmatch': employees.filter(emp => emp.education_job_match === 'Unmatch').length
          };

          setEducationAnalysis({
            education_analysis: employees,
            match_distribution: matchDistribution,
            contract_progression: employees.filter(emp => 
              emp.contract_status === 'Ready for Review' || 
              emp.tenure_months >= 3
            ).map(emp => ({
              name: emp.name,
              status: emp.contract_status,
              recommendation: emp.data_mining_recommendation?.reason || 'Standard review'
            }))
          });

          // setDivisionIntelligence({
          //   performance_predictions: employees.map(emp => ({
          //     name: emp.name,
          //     success_probability: emp.success_probability,
          //     risk_level: emp.risk_level,
          //     contract_recommendation: emp.data_mining_recommendation
          //   })),
          //   urgent_contract_decisions: employees.filter(emp => 
          //     emp.data_mining_recommendation?.risk_level === 'High' ||
          //     emp.contract_status === 'Ready for Review'
          //   )
          // });
        }
      }

    } catch (error) {
      console.error('Error fetching manager data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchProcessedRecommendations = async () => {
    try {
      const response = await axios.get('http://localhost/web_srk_BI/backend/api/recommendations.php?action=processed', {
        withCredentials: true
      });

      if (response.data.success) {
        setProcessedRecommendations(response.data.data);
        console.log('âœ… Processed recommendations loaded:', response.data.data);
      } else {
        console.error('Failed to fetch processed recommendations:', response.data.error);
      }
    } catch (error) {
      console.error('Error fetching processed recommendations:', error);
    }
  };

  const fetchEmployeeDetail = async (eid) => {
    try {
      console.log('Fetching employee detail for eid:', eid);
      const response = await fetch(`http://localhost/web_srk_BI/backend/api/employees.php?action=employee_data_mining_detail&eid=${eid}`, {
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      console.log('Response status:', response.status);
      console.log('Response OK:', response.ok);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const responseText = await response.text();
      console.log('Raw response text (first 200 chars):', responseText.substring(0, 200));

      let result;
      try {
        result = JSON.parse(responseText);
      } catch (jsonError) {
        console.error('JSON Parse Error:', jsonError);
        console.log('Full response text:', responseText);
        throw new Error('Server returned invalid JSON. Check browser console for details.');
      }

      console.log('Employee detail API Response:', result);

      if (result.success) {
        console.log('Employee detail loaded successfully');
        return result.data; // Return the data for await
      } else {
        console.error('API Error:', result.error);
        throw new Error(result.error || 'Failed to load employee data');
      }
    } catch (error) {
      console.error('Network/Parse error:', error);
      throw error; // Re-throw for handleEmployeeClick to catch
    }
  };

  const handleEmployeeClick = async (employee) => {
    console.log('ðŸ” handleEmployeeClick called for:', employee.name, 'EID:', employee.eid);

    // Reset state and show loading
    setEmployeeDetail(null);
    setLoadingDetail(true);
    setShowEmployeeModal(true);

    try {
      console.log('ðŸ“ž Calling fetchEmployeeDetail...');
      const employeeData = await fetchEmployeeDetail(employee.eid);
      console.log('âœ… Employee data received:', employeeData);
      console.log('   - Dynamic Intelligence:', employeeData?.dynamic_intelligence);
      console.log('   - Match Category:', employeeData?.dynamic_intelligence?.match_category);
      console.log('   - Optimal Duration:', employeeData?.dynamic_intelligence?.optimal_duration);
      setEmployeeDetail(employeeData);
    } catch (error) {
      console.error('âŒ Failed to fetch employee detail:', error);
      setEmployeeDetail({
        error: true,
        errorMessage: error.message,
        employee: {
          name: employee.name,
          eid: employee.eid,
          role: employee.role || 'N/A',
          current_contract_type: employee.current_contract_type || 'Unknown'
        }
      });
    } finally {
      setLoadingDetail(false);
    }
  };

  const handleCreateRecommendation = async (employee) => {
    console.log('ðŸ”§ handleCreateRecommendation called for:', employee.name, 'Contract:', employee.current_contract_type);

    setLoadingDetail(true);
    let freshDetail = null;

    try {
      const employeeData = await fetchEmployeeDetail(employee.eid);
      setEmployeeDetail(employeeData);
      freshDetail = employeeData; // Use the fresh data directly
    } catch (error) {
      console.error('Failed to fetch employee data:', error);
      const errorData = {
        error: true,
        errorMessage: error.message,
        employee: { name: employee.name, eid: employee.eid }
      };
      setEmployeeDetail(errorData);
      freshDetail = errorData;
    } finally {
      setLoadingDetail(false);
    }

    // DYNAMIC INTELLIGENCE DEFAULTS - using fresh data
    if (freshDetail && freshDetail.dynamic_intelligence) {
      const intelligence = freshDetail.dynamic_intelligence;
      const duration = intelligence.optimal_duration;
      const category = intelligence.match_category;

      // PROPER FORM DEFAULTS based on dynamic intelligence
      let defaultType = 'kontrak2';
      let defaultDuration = duration || 6;

      if (employee.current_contract_type === '3' || employee.current_contract_type === 'kontrak3') {
        // Kontrak 3 only to Permanent
        defaultType = 'permanent';
        defaultDuration = 0;
      } else if (category === 'Siap Permanent' || duration === 0) {
        defaultType = 'permanent';
        defaultDuration = 0;
      } else if (category === 'Berpotensi Kontrak 3' || duration >= 18) {
        defaultType = 'kontrak3';
        defaultDuration = duration || 18;
      } else if (category === 'Berpotensi Kontrak 2' || duration >= 6) {
        defaultType = 'kontrak2';
        defaultDuration = duration || 12;
      } else if (category === 'Tidak Direkomendasikan') {
        // UNMATCH but still give 6 months based on tenure
        defaultType = 'kontrak2';
        defaultDuration = 6;
      }

      // Special handling for UNMATCH with recommendation
      const reasonPrefix = category === 'Tidak Sesuai' ? 
        `Status Unmatch, namun berdasarkan perjalanan kontrak dan data mining, direkomendasikan ${defaultDuration} bulan untuk evaluasi. ` :
        `Berdasarkan dynamic intelligence analysis: ${category}. `;

      setRecommendationData({
        eid: employee.eid,
        employee_name: employee.name,
        recommendation_type: defaultType,
        recommended_duration: defaultDuration,
        reason: reasonPrefix + (intelligence.reasoning?.[0] || 'Analisis data mining menunjukkan pola yang sesuai untuk rekomendasi ini.')
      });
    } else {
      // Fallback jika tidak ada data intelligence
      setRecommendationData({
        eid: employee.eid,
        employee_name: employee.name,
        recommendation_type: 'kontrak2',
        recommended_duration: 6,
        reason: 'Rekomendasi berdasarkan evaluasi manual - belum ada data intelligence.'
      });
    }

    setShowRecommendationModal(true);
  };

  const submitRecommendation = async () => {
    try {
      const formData = new FormData();
      formData.append('action', 'create');
      formData.append('eid', employeeDetail.employee?.eid);
      formData.append('recommendation_type', recommendationData.recommendation_type);
      formData.append('recommended_duration', recommendationData.recommended_duration);
      formData.append('reason', recommendationData.reason);

      // Enhanced system recommendation with dynamic intelligence
      const systemRec = employeeDetail?.dynamic_intelligence ? 
        `Rekomendasi sistem: ${employeeDetail.dynamic_intelligence?.recommendation_type} (${employeeDetail.dynamic_intelligence?.optimal_duration} bulan), Risk Level: ${employeeDetail.dynamic_intelligence?.risk_level}, Success Probability: ${employeeDetail.dynamic_intelligence?.success_probability}%` :
        `Rekomendasi sistem: ${recommendationData.recommendation_type} kontrak ${recommendationData.recommended_duration} bulan berdasarkan analisis data mining`;

      formData.append('system_recommendation', systemRec);

      const response = await axios.post('http://localhost/web_srk_BI/backend/api/recommendations.php', formData, {
        withCredentials: true
      });

      if (response.data.success) {
        showAlert('Rekomendasi berhasil dibuat dan dikirim ke HR', 'success');
        setShowEmployeeModal(false);
        // Refresh data after creating recommendation
        fetchManagerData();
        fetchProcessedRecommendations();
      } else {
        showAlert(response.data.error, 'danger');
