import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Badge, Button, Table, Modal, Alert, Spinner } from 'react-bootstrap';
import { useAuth } from '../App';
import axios from 'axios';

const HRRecommendations = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [statistics, setStatistics] = useState(null);
  const [division, setDivision] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRecommendation, setSelectedRecommendation] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [actionLoading, setActionLoading] = useState(false);
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterType, setFilterType] = useState('all');
  const { user } = useAuth();

  useEffect(() => {
    if (user?.role === 'manager') {
      fetchRecommendations();
    }
  }, [user]);

  const fetchRecommendations = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await axios.get(
        'http://localhost/web_srk_BI/backend/api/hr_recommendations.php?action=manager_recommendations',
        { withCredentials: true }
      );

      if (response.data.success) {
        setRecommendations(response.data.data.recommendations);
        setStatistics(response.data.data.statistics);
        setDivision(response.data.data.division);
      } else {
        setError(response.data.error || 'Gagal memuat data rekomendasi');
      }
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      setError('Gagal memuat data rekomendasi. Silakan coba lagi.');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (recommendationId, newStatus) => {
    try {
      setActionLoading(true);
      
      const response = await axios.put(
        'http://localhost/web_srk_BI/backend/api/hr_recommendations.php',
        {
          action: 'update_status',
          recommendation_id: recommendationId,
          status: newStatus
        },
        { withCredentials: true }
      );

      if (response.data.success) {
        // Update local state
        setRecommendations(prev => 
          prev.map(rec => 
            rec.recommendation_id === recommendationId 
              ? { ...rec, status: newStatus }
              : rec
          )
        );
        
        // Update statistics
        setStatistics(prev => ({
          ...prev,
          pending: prev.pending - 1,
          [newStatus]: prev[newStatus] + 1
        }));

        // Close modal
        setShowDetailModal(false);
        setSelectedRecommendation(null);
      } else {
        setError(response.data.error || 'Gagal mengupdate status');
      }
    } catch (error) {
      console.error('Error updating status:', error);
      setError('Gagal mengupdate status. Silakan coba lagi.');
    } finally {
      setActionLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { variant: 'warning', text: 'Menunggu' },
      approved: { variant: 'success', text: 'Disetujui' },
      rejected: { variant: 'danger', text: 'Ditolak' }
    };
    
    const config = statusConfig[status] || { variant: 'secondary', text: status };
    return <Badge bg={config.variant}>{config.text}</Badge>;
  };

  const getTypeBadge = (type) => {
    const typeConfig = {
      extend: { variant: 'primary', text: 'Perpanjang Kontrak' },
      permanent: { variant: 'success', text: 'Kontrak Permanen' },
      terminate: { variant: 'danger', text: 'Terminasi' },
      review: { variant: 'info', text: 'Review' }
    };
    
    const config = typeConfig[type] || { variant: 'secondary', text: type };
    return <Badge bg={config.variant}>{config.text}</Badge>;
  };

  const getPriorityBadge = (priority) => {
    const priorityConfig = {
      high: { variant: 'danger', text: 'Tinggi' },
      medium: { variant: 'warning', text: 'Sedang' },
      low: { variant: 'secondary', text: 'Rendah' }
    };
    
    const config = priorityConfig[priority] || { variant: 'secondary', text: priority };
    return <Badge bg={config.variant}>{config.text}</Badge>;
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('id-ID', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatTenure = (months) => {
    if (!months) return '-';
    const years = Math.floor(months / 12);
    const remainingMonths = months % 12;
    
    if (years > 0) {
      return `${years} tahun ${remainingMonths} bulan`;
    }
    return `${months} bulan`;
  };

  const filteredRecommendations = recommendations.filter(rec => {
    const statusMatch = filterStatus === 'all' || rec.status === filterStatus;
    const typeMatch = filterType === 'all' || rec.recommendation_type === filterType;
    return statusMatch && typeMatch;
  });

  if (loading) {
    return (
      <Container className="mt-4">
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
          <p className="mt-2">Memuat data rekomendasi...</p>
        </div>
      </Container>
    );
  }

  return (
    <Container fluid className="mt-4">
      <Row>
        <Col>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <div>
              <h2>📋 Rekomendasi HR</h2>
              <p className="text-muted">
                Rekomendasi kontrak karyawan dari HR untuk divisi {division?.division_name}
              </p>
            </div>
            <Button variant="outline-primary" onClick={fetchRecommendations}>
              🔄 Refresh
            </Button>
          </div>

          {error && (
            <Alert variant="danger" dismissible onClose={() => setError(null)}>
              {error}
            </Alert>
          )}

          {/* Statistics Cards */}
          {statistics && (
            <Row className="mb-4">
              <Col md={3}>
                <Card className="text-center border-primary">
                  <Card.Body>
                    <h3 className="text-primary">{statistics.total}</h3>
                    <small className="text-muted">Total Rekomendasi</small>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="text-center border-warning">
                  <Card.Body>
                    <h3 className="text-warning">{statistics.pending}</h3>
                    <small className="text-muted">Menunggu Keputusan</small>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="text-center border-success">
                  <Card.Body>
                    <h3 className="text-success">{statistics.approved}</h3>
                    <small className="text-muted">Disetujui</small>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="text-center border-danger">
                  <Card.Body>
                    <h3 className="text-danger">{statistics.rejected}</h3>
                    <small className="text-muted">Ditolak</small>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          )}

          {/* Filters */}
          <Card className="mb-4">
            <Card.Body>
              <Row>
                <Col md={6}>
                  <label className="form-label">Filter Status:</label>
                  <select 
                    className="form-select"
                    value={filterStatus}
                    onChange={(e) => setFilterStatus(e.target.value)}
                  >
                    <option value="all">Semua Status</option>
                    <option value="pending">Menunggu</option>
                    <option value="approved">Disetujui</option>
                    <option value="rejected">Ditolak</option>
                  </select>
                </Col>
                <Col md={6}>
                  <label className="form-label">Filter Tipe:</label>
                  <select 
                    className="form-select"
                    value={filterType}
                    onChange={(e) => setFilterType(e.target.value)}
                  >
                    <option value="all">Semua Tipe</option>
                    <option value="extend">Perpanjang Kontrak</option>
                    <option value="permanent">Kontrak Permanen</option>
                    <option value="terminate">Terminasi</option>
                    <option value="review">Review</option>
                  </select>
                </Col>
              </Row>
            </Card.Body>
          </Card>

          {/* Recommendations Table */}
          <Card>
            <Card.Header>
              <h5>📊 Daftar Rekomendasi ({filteredRecommendations.length})</h5>
            </Card.Header>
            <Card.Body>
              {filteredRecommendations.length === 0 ? (
                <div className="text-center py-4">
                  <p className="text-muted">Tidak ada rekomendasi yang sesuai dengan filter</p>
                </div>
              ) : (
                <Table responsive hover>
                  <thead>
                    <tr>
                      <th>Karyawan</th>
                      <th>Tipe Rekomendasi</th>
                      <th>Durasi</th>
                      <th>Status</th>
                      <th>Prioritas</th>
                      <th>Direkomendasikan Oleh</th>
                      <th>Tanggal</th>
                      <th>Aksi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredRecommendations.map((rec) => (
                      <tr key={rec.recommendation_id}>
                        <td>
                          <div>
                            <strong>{rec.employee.name}</strong>
                            <br />
                            <small className="text-muted">
                              {rec.employee.role} • {formatTenure(rec.employee.tenure_months)}
                            </small>
                          </div>
                        </td>
                        <td>{getTypeBadge(rec.recommendation_type)}</td>
                        <td>
                          {rec.recommended_duration ? `${rec.recommended_duration} bulan` : '-'}
                        </td>
                        <td>{getStatusBadge(rec.status)}</td>
                        <td>{getPriorityBadge(rec.priority)}</td>
                        <td>
                          <div>
                            <strong>{rec.recommended_by.name}</strong>
                            <br />
                            <small className="text-muted">{rec.recommended_by.role}</small>
                          </div>
                        </td>
                        <td>{formatDate(rec.created_at)}</td>
                        <td>
                          <Button
                            variant="outline-primary"
                            size="sm"
                            onClick={() => {
                              setSelectedRecommendation(rec);
                              setShowDetailModal(true);
                            }}
                          >
                            Detail
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Detail Modal */}
      <Modal show={showDetailModal} onHide={() => setShowDetailModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Detail Rekomendasi</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedRecommendation && (
            <div>
              <Row className="mb-3">
                <Col md={6}>
                  <h5>👤 Informasi Karyawan</h5>
                  <p><strong>Nama:</strong> {selectedRecommendation.employee.name}</p>
                  <p><strong>Email:</strong> {selectedRecommendation.employee.email}</p>
                  <p><strong>Posisi:</strong> {selectedRecommendation.employee.role}</p>
                  <p><strong>Pendidikan:</strong> {selectedRecommendation.employee.education_level} - {selectedRecommendation.employee.major}</p>
                  <p><strong>Tanggal Bergabung:</strong> {formatDate(selectedRecommendation.employee.join_date)}</p>
                  <p><strong>Masa Kerja:</strong> {formatTenure(selectedRecommendation.employee.tenure_months)}</p>
                </Col>
                <Col md={6}>
                  <h5>📄 Kontrak Saat Ini</h5>
                  <p><strong>Tipe:</strong> {selectedRecommendation.current_contract.type || 'Tidak ada'}</p>
                  <p><strong>Mulai:</strong> {formatDate(selectedRecommendation.current_contract.start_date)}</p>
                  <p><strong>Berakhir:</strong> {formatDate(selectedRecommendation.current_contract.end_date)}</p>
                  {selectedRecommendation.current_contract.days_to_end !== null && (
                    <p><strong>Sisa Hari:</strong> {selectedRecommendation.current_contract.days_to_end} hari</p>
                  )}
                </Col>
              </Row>

              <hr />

              <h5>📋 Detail Rekomendasi</h5>
              <Row className="mb-3">
                <Col md={6}>
                  <p><strong>Tipe:</strong> {getTypeBadge(selectedRecommendation.recommendation_type)}</p>
                  <p><strong>Durasi:</strong> {selectedRecommendation.recommended_duration ? `${selectedRecommendation.recommended_duration} bulan` : 'Tidak ditentukan'}</p>
                  <p><strong>Status:</strong> {getStatusBadge(selectedRecommendation.status)}</p>
                  <p><strong>Prioritas:</strong> {getPriorityBadge(selectedRecommendation.priority)}</p>
                </Col>
                <Col md={6}>
                  <p><strong>Direkomendasikan oleh:</strong> {selectedRecommendation.recommended_by.name} ({selectedRecommendation.recommended_by.role})</p>
                  <p><strong>Tanggal:</strong> {formatDate(selectedRecommendation.created_at)}</p>
                  {selectedRecommendation.updated_at !== selectedRecommendation.created_at && (
                    <p><strong>Diupdate:</strong> {formatDate(selectedRecommendation.updated_at)}</p>
                  )}
                </Col>
              </Row>

              {selectedRecommendation.reason && (
                <div className="mb-3">
                  <h6>💬 Alasan Rekomendasi:</h6>
                  <p className="text-muted">{selectedRecommendation.reason}</p>
                </div>
              )}

              {selectedRecommendation.system_recommendation && (
                <div className="mb-3">
                  <h6>🤖 Rekomendasi Sistem:</h6>
                  <p className="text-muted">{selectedRecommendation.system_recommendation}</p>
                </div>
              )}
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          {selectedRecommendation?.status === 'pending' && (
            <>
              <Button
                variant="success"
                onClick={() => handleStatusUpdate(selectedRecommendation.recommendation_id, 'approved')}
                disabled={actionLoading}
              >
                {actionLoading ? <Spinner animation="border" size="sm" /> : '✅ Setujui'}
              </Button>
              <Button
                variant="danger"
                onClick={() => handleStatusUpdate(selectedRecommendation.recommendation_id, 'rejected')}
                disabled={actionLoading}
              >
                {actionLoading ? <Spinner animation="border" size="sm" /> : '❌ Tolak'}
              </Button>
            </>
          )}
          <Button variant="secondary" onClick={() => setShowDetailModal(false)}>
            Tutup
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default HRRecommendations;
