                    onChange={(e) => setRecommendationData({...recommendationData, reason: e.target.value})}
                    placeholder="Jelaskan alasan rekomendasi berdasarkan analisis kinerja, kecocokan pendidikan, dan data mining..."
                    required
                  />
                          <small className="text-muted">
                    Berikan justifikasi yang jelas berdasarkan data analisis dan performa karyawan.
                          </small>
                        </div>

                {selectedEmployee.data_mining_recommendation && (
                          <div className="recommendation-card">
                            <div className="card-header">
                              <h3>ðŸ¤– Rekomendasi Sistem</h3>
                            </div>
                            <div className="probation-info">
                              <div className="info-item">
                                <strong>Durasi:</strong>
                                <div className="info-value">{selectedEmployee.data_mining_recommendation.recommended_duration} bulan</div>
                              </div>
                              <div className="info-item">
                                <strong>Risk Level:</strong>
                                <div className="info-value">{selectedEmployee.data_mining_recommendation.risk_level}</div>
                              </div>
                    </div>
                  </div>
                )}
                      </div>
                    </div>
            </div>
          )}
              </div>
              <div className="modal-footer">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowRecommendationModal(false)}
                >
            Batal
                </button>
                <button 
                  className="btn-action btn-aktif"
            onClick={submitRecommendation}
            disabled={!recommendationData.recommendation_type || !recommendationData.reason || 
                     ((recommendationData.recommendation_type === 'kontrak2' || 
                       recommendationData.recommendation_type === 'kontrak3') && !recommendationData.recommended_duration)}
          >
            ðŸ“¤ Kirim Rekomendasi ke HR
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Container>
  );
};

export default ManagerDashboard;
