          borderWidth: 2
        }]
      };
    }

    const employees = analytics.employees;
    const duration6 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 6).length;
    const duration12 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 12).length;
    const duration18 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 18).length;
    const duration24 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 24).length;
    const duration36 = employees.filter(emp => emp.data_mining_recommendation?.recommended_duration === 36).length;

    return {
      labels: ['6 bulan', '12 bulan', '18 bulan', '24 bulan', '36 bulan'],
