          {stats.display_status === 'none' && (
            <>
              {employee.current_contract_type === 'permanent' ? (
                <Badge bg="secondary">🔒 Permanent</Badge>
              ) : employee.days_to_contract_end <= 30 ? (
                <Badge bg="success">✅ Dapat Direkomendasikan</Badge>
              ) : employee.days_to_contract_end > 30 ? (
                <div className="text-center">
                  <Badge bg="danger">❌ Belum Bisa</Badge>
                  <br />
                  <small className="text-muted">{employee.days_to_contract_end} hari lagi</small>
