import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../App';
import axios from 'axios';

const EmployeeDetail = () => {
    const { eid } = useParams();
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const [employee, setEmployee] = useState(null);
    const [recommendation, setRecommendation] = useState(null);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState('profile');
    const [showResignModal, setShowResignModal] = useState(false);
    const [resignReason, setResignReason] = useState('');

    useEffect(() => {
        fetchEmployeeData();
        fetchRecommendation();
    }, [eid]);

    const fetchEmployeeData = async () => {
        try {
            const response = await axios.get(`http://localhost/web_srk_BI/backend/api/employees.php?action=detail&eid=${eid}`, {
                withCredentials: true
            });
            if (response.data.success) {
                setEmployee(response.data.data);
            }
        } catch (error) {
            console.error('Error fetching employee:', error);
        }
    };

    const fetchRecommendation = async () => {
        try {
            const response = await axios.get(`http://localhost/web_srk_BI/backend/api/employees.php?action=recommendation&eid=${eid}`, {
                withCredentials: true
            });
            if (response.data.success) {
                setRecommendation(response.data.recommendation);
            }
        } catch (error) {
            console.error('Error fetching recommendation:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleResign = async () => {
        if (!resignReason.trim()) {
            alert('Alasan resign harus diisi');
            return;
        }

        try {
            const response = await axios.post('http://localhost/web_srk_BI/backend/api/employees.php', {
                action: 'resign',
                eid: eid,
                reason: resignReason
            }, { withCredentials: true });

            if (response.data.success) {
                setShowResignModal(false);
                fetchEmployeeData();
                alert('Karyawan berhasil di-resign');
            }
        } catch (error) {
            console.error('Error resigning employee:', error);
            alert('Gagal melakukan resign');
        }
    };

    const getConfidenceBadge = (confidence) => {
        const badges = {
            'very_high': 'bg-success',
            'high': 'bg-info',
            'medium': 'bg-warning',
            'low': 'bg-danger'
        };
        return badges[confidence] || 'bg-secondary';
    };

    const getRecommendationTypeDisplay = (type) => {
        const types = {
            'probation': 'Probation',
            '1': 'Kontrak 1',
            '2': 'Kontrak 2',
            '3': 'Kontrak 3',
            'permanent': 'Permanent'
        };
        return types[type] || type;
    };

    if (loading) {
        return (
            <div className="container mt-5">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>
            </div>
        );
    }

    if (!employee) {
        return (
            <div className="container mt-5">
                <div className="alert alert-danger">
                    Karyawan tidak ditemukan
                </div>
            </div>
        );
    }

    return (
        <div className="container-fluid py-4">
            <div className="row">
                {/* Left Panel - Profile Card */}
                <div className="col-md-4">
                    <div className="card modern-card mb-4">
                        <div className="card-body text-center">
                            <div className="employee-avatar mb-3">
                                <img 
                                    src={employee.profile_picture || '/api/placeholder/150/150'} 
                                    alt="Profile" 
                                    className="rounded-circle"
                                    style={{ width: '120px', height: '120px', objectFit: 'cover' }}
                                />
                            </div>
                            <h4 className="mb-1">{employee.full_name}</h4>
                            <p className="text-muted mb-2">{employee.role}</p>
                            <p className="text-muted mb-3">{employee.division_name}</p>
                            
                            <div className="employee-info">
                                <div className="info-item">
                                    <small className="text-muted">Tanggal Lahir</small>
                                    <div>{new Date(employee.birthdate).toLocaleDateString('id-ID')}</div>
                                </div>
                                <div className="info-item">
                                    <small className="text-muted">EID</small>
                                    <div>{employee.eid}</div>
                                </div>
                                <div className="info-item">
                                    <small className="text-muted">No HP</small>
                                    <div>{employee.phone || '-'}</div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* CV Download Card */}
                    <div className="card modern-card">
                        <div className="card-body text-center">
                            <h6 className="card-title">CV</h6>
                            <button className="btn btn-outline-primary btn-sm">
                                <i className="fas fa-download me-2"></i>
                                Download CV
                            </button>
                        </div>
                    </div>
                </div>

                {/* Right Panel - Details and Recommendation */}
                <div className="col-md-8">
                    {/* Tab Navigation */}
                    <div className="card modern-card">
                        <div className="card-header">
                            <ul className="nav nav-tabs card-header-tabs">
                                <li className="nav-item">
                                    <button 
                                        className={`nav-link ${activeTab === 'profile' ? 'active' : ''}`}
                                        onClick={() => setActiveTab('profile')}
                                    >
                                        Data Karyawan
                                    </button>
                                </li>
                                <li className="nav-item">
                                    <button 
                                        className={`nav-link ${activeTab === 'recommendation' ? 'active' : ''}`}
                                        onClick={() => setActiveTab('recommendation')}
                                    >
                                        Kontrak Recommendation
                                    </button>
                                </li>
                                <li className="nav-item">
                                    <button 
                                        className={`nav-link ${activeTab === 'history' ? 'active' : ''}`}
                                        onClick={() => setActiveTab('history')}
                                    >
                                        Riwayat Kontrak
                                    </button>
                                </li>
                            </ul>
                        </div>

                        <div className="card-body">
                            {/* Profile Tab */}
                            {activeTab === 'profile' && (
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="detail-item">
                                            <label>Nama Departement</label>
                                            <div className="form-control-plaintext">{employee.division_name}</div>
                                        </div>
                                        <div className="detail-item">
                                            <label>Role</label>
                                            <div className="form-control-plaintext">{employee.role}</div>
                                        </div>
                                        <div className="detail-item">
                                            <label>Jurusan</label>
                                            <div className="form-control-plaintext">{employee.major}</div>
                                        </div>
                                        <div className="detail-item">
                                            <label>Tingkat Pendidikan</label>
                                            <div className="form-control-plaintext">{employee.education_level}</div>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="detail-item">
                                            <label>Tanggal Join</label>
                                            <div className="form-control-plaintext">
                                                {new Date(employee.join_date).toLocaleDateString('id-ID')}
                                            </div>
                                        </div>
                                        <div className="detail-item">
                                            <label>Tanggal Mulai Probation</label>
                                            <div className="form-control-plaintext">
                                                {employee.probation_start ? 
                                                    new Date(employee.probation_start).toLocaleDateString('id-ID') : '-'}
                                            </div>
                                        </div>
                                        <div className="detail-item">
                                            <label>Tanggal Selesai Probation</label>
                                            <div className="form-control-plaintext">
                                                {employee.probation_end ? 
                                                    new Date(employee.probation_end).toLocaleDateString('id-ID') : '-'}
                                            </div>
                                        </div>
                                        <div className="detail-item">
                                            <label>Status</label>
                                            <div className="form-control-plaintext">
                                                <span className={`badge ${employee.status === 'active' ? 'bg-success' : 'bg-danger'}`}>
                                                    {employee.status === 'active' ? 'Aktif' : 'Tidak Aktif'}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {/* Recommendation Tab */}
                            {activeTab === 'recommendation' && recommendation && (
                                <div className="recommendation-section">
                                    <div className="row">
                                        <div className="col-12">
                                            <div className="alert alert-info border-0">
                                                <h5 className="alert-heading">
                                                    <i className="fas fa-brain me-2"></i>
                                                    Rekomendasi Kontrak Berdasarkan AI Intelligence
                                                </h5>
                                                <hr />
                                                <div className="row">
                                                    <div className="col-md-8">
                                                        <h6>Rekomendasi Utama:</h6>
                                                        <div className="d-flex align-items-center mb-3">
                                                            <div className="recommendation-badge">
                                                                {getRecommendationTypeDisplay(recommendation.recommended_type)}
                                                                {recommendation.recommended_duration && (
                                                                    <span className="ms-2 text-muted">
                                                                        ({recommendation.recommended_duration} bulan)
                                                                    </span>
                                                                )}
                                                            </div>
                                                            <span className={`badge ms-2 ${getConfidenceBadge(recommendation.confidence)}`}>
                                                                {recommendation.confidence} confidence
                                                            </span>
                                                        </div>
                                                        
                                                        <div className="reasons-list">
                                                            <h6>Alasan:</h6>
                                                            <ul className="list-unstyled">
                                                                {recommendation.reasons.map((reason, index) => (
                                                                    <li key={index} className="mb-1">
                                                                        <i className="fas fa-check-circle text-success me-2"></i>
                                                                        {reason}
                                                                    </li>
                                                                ))}
                                                            </ul>
                                                        </div>
                                                    </div>
                                                    <div className="col-md-4">
                                                        <div className="success-probability">
                                                            <div className="text-center">
                                                                <div className="progress-circle">
                                                                    <div className="circle-content">
                                                                        <span className="percentage">
                                                                            {recommendation.success_probability}%
                                                                        </span>
                                                                        <small>Success Rate</small>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>

                                    {/* Detailed Analysis */}
                                    {recommendation.detailed_analysis && (
                                        <div className="row mt-4">
                                            <div className="col-12">
                                                <h6>Analisis Detail:</h6>
                                                <div className="row">
                                                    <div className="col-md-6">
                                                        <div className="analysis-card">
                                                            <h6>Kecocokan Pendidikan-Role</h6>
                                                            <div className="progress mb-2">
                                                                <div 
                                                                    className="progress-bar bg-info" 
                                                                    style={{ width: `${recommendation.detailed_analysis.education_role_match.score * 100}%` }}
                                                                ></div>
                                                            </div>
                                                            <small className="text-muted">
                                                                {recommendation.detailed_analysis.education_role_match.description}
                                                            </small>
                                                        </div>
                                                    </div>
                                                    <div className="col-md-6">
                                                        <div className="analysis-card">
                                                            <h6>Risiko Resign</h6>
                                                            <div className="progress mb-2">
                                                                <div 
                                                                    className="progress-bar bg-warning" 
                                                                    style={{ width: `${recommendation.detailed_analysis.resignation_risk.score * 100}%` }}
                                                                ></div>
                                                            </div>
                                                            <small className="text-muted">
                                                                Level: {recommendation.detailed_analysis.resignation_risk.level}
                                                            </small>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    )}

                                    {/* Insights */}
                                    {recommendation.insights && recommendation.insights.length > 0 && (
                                        <div className="row mt-4">
                                            <div className="col-12">
                                                <h6>Insights:</h6>
                                                <div className="insights-list">
                                                    {recommendation.insights.map((insight, index) => (
                                                        <div key={index} className="alert alert-light border-start border-primary border-3">
                                                            <i className="fas fa-lightbulb text-warning me-2"></i>
                                                            {insight}
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>
                                        </div>
                                    )}

                                    {/* Risk Factors */}
                                    {recommendation.risk_factors && recommendation.risk_factors.length > 0 && (
                                        <div className="row mt-4">
                                            <div className="col-12">
                                                <h6>Faktor Risiko:</h6>
                                                <div className="risk-factors">
                                                    {recommendation.risk_factors.map((factor, index) => (
                                                        <div key={index} className="alert alert-warning border-start border-warning border-3">
                                                            <i className="fas fa-exclamation-triangle text-warning me-2"></i>
                                                            {factor}
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            )}

                            {/* History Tab */}
                            {activeTab === 'history' && (
                                <div className="history-section">
                                    <h6>Riwayat Kontrak</h6>
                                    {employee.contracts && employee.contracts.length > 0 ? (
                                        <div className="timeline">
                                            {employee.contracts.map((contract, index) => (
                                                <div key={index} className="timeline-item">
                                                    <div className="timeline-marker"></div>
                                                    <div className="timeline-content">
                                                        <div className="d-flex justify-content-between">
                                                            <div>
                                                                <h6>{getRecommendationTypeDisplay(contract.type)}</h6>
                                                                <p className="text-muted mb-1">
                                                                    {new Date(contract.start_date).toLocaleDateString('id-ID')} - 
                                                                    {contract.end_date ? new Date(contract.end_date).toLocaleDateString('id-ID') : 'Ongoing'}
                                                                </p>
                                                                <p className="text-muted mb-0">
                                                                    Durasi: {contract.duration_months} bulan
                                                                </p>
                                                            </div>
                                                            <div>
                                                                <span className={`badge ${contract.status === 'active' ? 'bg-success' : 'bg-secondary'}`}>
                                                                    {contract.status}
                                                                </span>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    ) : (
                                        <div className="text-center py-4">
                                            <i className="fas fa-file-contract fa-3x text-muted mb-3"></i>
                                            <p className="text-muted">Belum ada riwayat kontrak</p>
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="row mt-4">
                        <div className="col-12">
                            <div className="d-flex justify-content-end gap-2">
                                {employee.status === 'active' && (
                                    <>
                                        <button 
                                            className="btn btn-success"
                                            onClick={() => setActiveTab('recommendation')}
                                        >
                                            <i className="fas fa-thumbs-up me-2"></i>
                                            Probation
                                        </button>
                                        <button 
                                            className="btn btn-danger"
                                            onClick={() => setShowResignModal(true)}
                                        >
                                            <i className="fas fa-user-times me-2"></i>
                                            Resign
                                        </button>
                                    </>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Resign Modal */}
            {showResignModal && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Alasan Resign</h5>
                                <button 
                                    type="button" 
                                    className="btn-close"
                                    onClick={() => setShowResignModal(false)}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <div className="mb-3">
                                    <label className="form-label">Detail Alasan Resign</label>
                                    <textarea
                                        className="form-control"
                                        rows="4"
                                        value={resignReason}
                                        onChange={(e) => setResignReason(e.target.value)}
                                        placeholder="Masukkan alasan resign..."
                                        required
                                    ></textarea>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button 
                                    type="button" 
                                    className="btn btn-secondary"
                                    onClick={() => setShowResignModal(false)}
                                >
                                    Batal
                                </button>
                                <button 
                                    type="button" 
                                    className="btn btn-primary"
                                    onClick={handleResign}
                                >
                                    <i className="fas fa-paper-plane me-2"></i>
                                    Send
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
            {showResignModal && <div className="modal-backdrop fade show"></div>}
        </div>
    );
};

export default EmployeeDetail;
