                                    <div className="p-2 bg-light rounded">
                                      <div className="fw-bold text-danger small">{employeeDetail.dynamic_intelligence?.historical_patterns?.resign_rate || 0}%</div>
                                      <small className="text-muted" style={{ fontSize: '0.7rem' }}>Resign</small>
                                    </div>
                                  </div>
                                  <div className="col-4">
                                    <div className="p-2 bg-light rounded">
                                      <div className="fw-bold text-primary small">
                                        {employeeDetail.dynamic_intelligence?.historical_patterns?.avg_duration ? 
                                         employeeDetail.dynamic_intelligence?.historical_patterns?.avg_duration?.toFixed(1) : '0'}
                                      </div>
                                      <small className="text-muted" style={{ fontSize: '0.7rem' }}>Avg Tenure</small>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Right Column - Sample Profiles */}
                      <div className="col-lg-4">
                        <div className="card h-100 border-0 shadow-sm">
                          <div className="card-header py-2" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', color: 'white' }}>
                            <h6 className="mb-0 small">
                              <i className="fas fa-users me-2"></i>
                              Profil Serupa ({employeeDetail?.dynamic_intelligence?.historical_patterns?.total_similar || 0})
                            </h6>
                          </div>
                          <div className="card-body p-2" style={{ maxHeight: '320px', overflowY: 'auto', backgroundColor: '#f8f9fa' }}>
                            {employeeDetail?.dynamic_intelligence?.historical_patterns?.profiles?.length > 0 ? (
                              <div className="d-flex flex-column gap-2">
                                {employeeDetail?.dynamic_intelligence?.historical_patterns?.profiles?.slice(0, 6).map((profile, idx) => (
                                  <div key={profile.eid} className="card border-0 shadow-sm" style={{ backgroundColor: 'white', borderRadius: '6px' }}>
                                    <div className="card-body p-2">
                                      <div className="small">
                                        <div className="fw-bold text-primary mb-1 text-truncate" title={profile.name} style={{ fontSize: '0.8rem' }}>
                                          <i className="fas fa-user-circle me-1"></i>
                                          {profile.name}
                                        </div>
                                        <div className="text-muted mb-1" style={{ fontSize: '0.7rem', lineHeight: '1.2' }}>
                                          <i className="fas fa-graduation-cap me-1"></i>
                                          {profile.major} â†’ {profile.role}
                                        </div>
                                        <div className="d-flex justify-content-between align-items-center">
                                          <div className="badge bg-info text-dark" style={{ fontSize: '0.6rem', padding: '0.2rem 0.4rem' }}>
                                            {profile.current_contract_type || 'N/A'}
                                          </div>
                                          <div className={`fw-bold ${profile.status_summary === 'Aktif' ? 'text-success' : 'text-danger'}`} style={{ fontSize: '0.7rem' }}>
                                            {profile.status_summary} ({Math.round(profile.total_tenure_months)}m)
                                          </div>
                                        </div>
                                      </div>
                                    </div>
                                  </div>
                                ))}
                              </div>
                            ) : (
                              <div className="text-center py-3">
                                <i className="fas fa-users fa-2x text-muted mb-2"></i>
                                <div className="text-muted small">Tidak ada profil serupa</div>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div>
                    {employeeDetail?.error ? (
                      <div>
                        <p><strong>Error:</strong> {employeeDetail.errorMessage}</p>
                        <details>
                          <summary>Debug Information</summary>
                          <div className="mt-2">
                            <p><strong>Employee:</strong> {employeeDetail.employee?.name} (EID: {employeeDetail.employee?.eid})</p>
                            <p><strong>Contract:</strong> {employeeDetail.employee?.current_contract_type}</p>
                            <p><strong>Possible causes:</strong></p>
                            <ul>
                              <li>Session expired - try refreshing the page</li>
                              <li>Network connection issues</li>
                              <li>Server configuration problems</li>
                            </ul>
                            <p className="small text-muted">Check browser console for more details (F12 â†’ Console)</p>
                          </div>
                        </details>
                      </div>
                    ) : (
                      <p>Terjadi kesalahan saat mengambil data analisis karyawan. Silakan coba lagi atau hubungi administrator sistem.</p>
                    )}
                  </div>
                )}


              </div>
            </div>
            <div className="modal-footer py-3 bg-light">
              <button 
                type="button" 
                className="btn btn-secondary px-4"
                onClick={() => {
                  setShowEmployeeModal(false);
                  setEmployeeDetail(null);
                  setLoadingDetail(false);
                }}
              >
                <i className="fas fa-times me-2"></i>
                Tutup
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Recommendation Modal - COMPACT NO SCROLL LAYOUT */}
      {showRecommendationModal && (
        <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-fullscreen" style={{ margin: 0 }}>
            <div className="modal-content" style={{ height: '100vh', border: 'none', borderRadius: 0 }}>
              <div className="modal-header py-2" style={{ background: 'linear-gradient(135deg, #28a745 0%, #20c997 100%)', color: 'white', minHeight: '60px' }}>
                <h5 className="modal-title mb-0">
                  <i className="fas fa-file-contract me-2"></i>
                  {employeeDetail?.employee?.name} - Buat Rekomendasi Kontrak
                </h5>
                <button 
                  type="button" 
                  className="btn-close btn-close-white"
                  onClick={() => {
                    setShowRecommendationModal(false);
                    setEmployeeDetail(null);
                    setLoadingDetail(false);
                  }}
                ></button>
              </div>

              <div className="modal-body p-2" style={{ height: 'calc(100vh - 120px)', overflow: 'hidden' }}>
                {loadingDetail ? (
                  <div className="text-center py-5">
                    <Spinner animation="border" variant="success" />
                    <div className="mt-3">
                      <h5>Memuat data untuk rekomendasi...</h5>
                      <p className="text-muted">Menganalisis profil dan riwayat karyawan</p>
                    </div>
                  </div>
                ) : employeeDetail && !employeeDetail?.error && employeeDetail?.employee ? (
                  <div className="container-fluid p-0" style={{ height: '100%' }}>
                    <div className="row g-2" style={{ height: '100%' }}>
                      {/* LEFT COLUMN - Employee Info + AI Analysis (30%) */}
                      <div className="col-lg-4" style={{ height: '100%' }}>
                        <div className="d-flex flex-column h-100 gap-2">
                          {/* Basic Info - Compact */}
                          <div className="card border-0 shadow-sm" style={{ flex: '0 0 auto' }}>
                            <div className="card-header py-2 bg-primary text-white">
                              <h6 className="mb-0 small">
                                <i className="fas fa-user me-2"></i>
                                Info Karyawan
                              </h6>
                            </div>
                            <div className="card-body p-2">
                              <div className="row">
                                <div className="col-6">
                                  <div className="small mb-1">
                                    <strong>{employeeDetail.employee?.name}</strong>
                                  </div>
                                  <div className="text-muted" style={{ fontSize: '0.8rem' }}>
                                    {employeeDetail.employee?.role}
                                  </div>
                                  <div className="text-muted" style={{ fontSize: '0.8rem' }}>
                                    {employeeDetail.employee?.division_name}
                                  </div>
                                </div>
                                <div className="col-6">
                                  <div className="small mb-1">
                                    <strong>{Math.round(employeeDetail.employee?.tenure_months || 0)} bulan</strong>
                                  </div>
                                  <div className="text-muted" style={{ fontSize: '0.8rem' }}>
                                    {employeeDetail.employee?.education_level}
                                  </div>
                                  <div className="text-muted" style={{ fontSize: '0.8rem' }}>
                                    {employeeDetail.employee?.major}
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>

                          {/* AI Intelligence - Compact */}
                          <div className="card border-0 shadow-sm" style={{ flex: '1 1 auto', minHeight: 0 }}>
                            <div className="card-header py-2 bg-success text-white">
                              <h6 className="mb-0 small">
                                <i className="fas fa-brain me-2"></i>
                                AI Analysis
                              </h6>
                            </div>
                            <div className="card-body p-2" style={{ height: 'calc(100% - 48px)', overflowY: 'auto' }}>
                              <div className="mb-2">
                                <small className="text-muted d-block">Status AI</small>
                                <div className="fw-bold text-success small">{employeeDetail.dynamic_intelligence?.match_category || 'N/A'}</div>
                              </div>
                              <div className="mb-2">
                                <small className="text-muted d-block">Rekomendasi</small>
                                <div className="fw-bold text-primary small">
                                  {(() => {
                                    const duration = employeeDetail.dynamic_intelligence?.optimal_duration;
                                    const category = employeeDetail.dynamic_intelligence?.match_category;

                                    if (category === 'Sudah Permanent') {
                                      return 'Sudah Permanent';
                                    } else if (employeeDetail.employee?.current_contract_type === '3' && 
                                        (category === 'Siap Permanent' || duration === 0)) {
                                      return 'Permanent';
                                    } else if (duration === 0) {
                                      return 'Permanent';
                                    } else if (duration && duration > 0) {
                                      return `${duration} bulan`;
                                    } else {
                                      return 'Perlu Evaluasi';
                                    }
                                  })()}
                                </div>
                              </div>
                              <div className="mb-2">
                                <small className="text-muted d-block">Sample Size</small>
                                <div className="fw-bold text-dark small">{employeeDetail.dynamic_intelligence?.sample_size || 0} profil</div>
                              </div>

                              {/* Statistics Row */}
                              {employeeDetail.dynamic_intelligence?.historical_patterns && (
                                <div className="mt-2">
                                  <small className="text-muted d-block mb-1">Pattern Stats</small>
                                  <div className="row text-center">
                                    <div className="col-4">
                                      <div className="p-1 bg-light rounded">
                                        <div className="fw-bold text-success" style={{ fontSize: '0.8rem' }}>{employeeDetail.dynamic_intelligence?.historical_patterns?.success_rate || 0}%</div>
                                        <small className="text-muted" style={{ fontSize: '0.6rem' }}>Aktif</small>
                                      </div>
                                    </div>
                                    <div className="col-4">
                                      <div className="p-1 bg-light rounded">
                                        <div className="fw-bold text-danger" style={{ fontSize: '0.8rem' }}>{employeeDetail.dynamic_intelligence?.historical_patterns?.resign_rate || 0}%</div>
                                        <small className="text-muted" style={{ fontSize: '0.6rem' }}>Resign</small>
                                      </div>
                                    </div>
                                    <div className="col-4">
                                      <div className="p-1 bg-light rounded">
                                        <div className="fw-bold text-primary" style={{ fontSize: '0.8rem' }}>
                                          {employeeDetail.dynamic_intelligence?.historical_patterns?.avg_duration ? 
                                           employeeDetail.dynamic_intelligence?.historical_patterns?.avg_duration?.toFixed(1) : '0'}
                                        </div>
                                        <small className="text-muted" style={{ fontSize: '0.6rem' }}>Avg</small>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              )}

                              {/* Compact Similar Profiles */}
                              {employeeDetail?.dynamic_intelligence?.historical_patterns?.profiles?.length > 0 && (
                                <div className="mt-3">
                                  <small className="text-muted d-block mb-1">Profil Serupa ({employeeDetail?.dynamic_intelligence?.historical_patterns?.total_similar || 0})</small>
                                  <div className="d-flex flex-column gap-1" style={{ maxHeight: '200px', overflowY: 'auto' }}>
                                    {employeeDetail?.dynamic_intelligence?.historical_patterns?.profiles?.slice(0, 4).map((profile, idx) => (
                                      <div key={profile.eid} className="card border-0 shadow-sm" style={{ backgroundColor: 'white', borderRadius: '4px' }}>
                                        <div className="card-body p-1">
                                          <div style={{ fontSize: '0.7rem' }}>
                                            <div className="fw-bold text-primary mb-1 text-truncate" title={profile.name}>
                                              {profile.name}
                                            </div>
                                            <div className="text-muted" style={{ fontSize: '0.65rem' }}>
                                              {profile.major} â†’ {profile.role}
                                            </div>
                                            <div className="d-flex justify-content-between align-items-center mt-1">
                                              <span className="badge bg-info text-dark" style={{ fontSize: '0.55rem' }}>
                                                {profile.current_contract_type || 'N/A'}
                                              </span>
                                              <span className={`fw-bold ${profile.status_summary === 'Aktif' ? 'text-success' : 'text-danger'}`} style={{ fontSize: '0.6rem' }}>
                                                {profile.status_summary} ({Math.round(profile.total_tenure_months)}m)
                                              </span>
                                            </div>
                                          </div>
                                        </div>
                                      </div>
                                    ))}
                                  </div>
                                </div>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* RIGHT COLUMN - Contract Recommendation Form (70%) */}
                      <div className="col-lg-8" style={{ height: '100%' }}>
                        <div className="card border-0 shadow-sm h-100">
                          <div className="card-header py-2" style={{ background: 'linear-gradient(135deg, #fd7e14 0%, #ffc107 100%)', color: 'white' }}>
                            <h6 className="mb-0">
                              <i className="fas fa-file-signature me-2"></i>
                              Form Rekomendasi Kontrak
                            </h6>
                          </div>
                          <div className="card-body p-3" style={{ height: 'calc(100% - 48px)', overflowY: 'auto' }}>
                            {/* AI Suggestion Alert */}
                            {employeeDetail?.dynamic_intelligence && (
                              <div className="alert alert-info border-0 shadow-sm mb-3" style={{ padding: '0.75rem' }}>
                                <div className="d-flex align-items-center">
                                  <i className="fas fa-brain fa-lg text-info me-2"></i>
                                  <div className="flex-grow-1">
                                    <h6 className="alert-heading mb-1" style={{ fontSize: '0.9rem' }}>
                                      <i className="fas fa-lightbulb me-1"></i>
                                      AI Suggestion
                                    </h6>
                                    <div className="row">
                                      <div className="col-md-4">
                                        <small className="text-muted d-block">Kategori</small>
                                        <strong style={{ fontSize: '0.85rem' }}>{employeeDetail.dynamic_intelligence?.match_category}</strong>
                                      </div>
                                      <div className="col-md-4">
                                        <small className="text-muted d-block">Durasi Optimal</small>
                                        <strong className="text-primary" style={{ fontSize: '0.85rem' }}>
                                          {employeeDetail.dynamic_intelligence?.optimal_duration === 0 ? 'Permanent' : `${employeeDetail.dynamic_intelligence?.optimal_duration} bulan`}
                                        </strong>
                                      </div>
                                      <div className="col-md-4">
                                        <small className="text-muted d-block">Data Points</small>
                                        <strong style={{ fontSize: '0.85rem' }}>{employeeDetail.dynamic_intelligence?.sample_size} profil</strong>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            )}

                            {/* Form Fields */}
                            <div className="row">
                              <div className="col-md-6">
                                <div className="mb-3">
                                  <label className="form-label fw-bold">
                                    <i className="fas fa-clipboard-list me-2"></i>
                                    Jenis Rekomendasi
                                  </label>
                                  <select 
                                    className="form-select"
                                    value={recommendationData.recommendation_type || ''}
                                    onChange={(e) => {
                                      const newType = e.target.value;
                                      setRecommendationData(prev => ({
                                        ...prev,
                                        recommendation_type: newType,
                                        recommended_duration: newType === 'permanent' ? 0 : (newType === 'terminate' ? 0 : prev.recommended_duration)
                                      }));
                                    }}
                                  >
                                    <option value="">Pilih Jenis Kontrak</option>
                                    <option value="kontrak2">Kontrak 2</option>
                                    <option value="kontrak3">Kontrak 3</option>
                                    <option value="permanent">Permanent</option>
                                    <option value="terminate">Tidak Diperpanjang</option>
                                  </select>
                                </div>
                              </div>
                              <div className="col-md-6">
                                <div className="mb-3">
                                  <label className="form-label fw-bold">
                                    <i className="fas fa-calendar-alt me-2"></i>
                                    Durasi (bulan)
                                  </label>
                                  <input
                                    type="number"
                                    className="form-control"
                                    value={recommendationData.recommended_duration || ''}
                                    onChange={(e) => setRecommendationData(prev => ({
                                      ...prev,
                                      recommended_duration: e.target.value
                                    }))}
                                    disabled={recommendationData.recommendation_type === 'permanent' || recommendationData.recommendation_type === 'terminate'}
                                    placeholder="Masukkan durasi"
                                    min="1"
                                    max="36"
                                  />
                                  {recommendationData.recommendation_type === 'permanent' && (
                                    <small className="text-success">
                                      <i className="fas fa-info-circle me-1"></i>
                                      Permanent - tidak memerlukan durasi
                                    </small>
                                  )}
                                  {recommendationData.recommendation_type === 'terminate' && (
                                    <small className="text-danger">
                                      <i className="fas fa-exclamation-triangle me-1"></i>
                                      Tidak diperpanjang
                                    </small>
                                  )}
                                </div>
                              </div>
                            </div>

                            <div className="mb-3">
                              <label className="form-label fw-bold">
                                <i className="fas fa-comment-alt me-2"></i>
                                Alasan Rekomendasi
                              </label>
                              <textarea
                                className="form-control"
                                rows="6"
                                value={recommendationData.reason || ''}
                                onChange={(e) => setRecommendationData(prev => ({
                                  ...prev,
                                  reason: e.target.value
                                }))}
                                placeholder="Jelaskan alasan rekomendasi berdasarkan analisis data, performa karyawan, dan kebutuhan perusahaan..."
                                style={{ resize: 'vertical' }}
                              />
                              <div className="form-text">
                                <i className="fas fa-lightbulb me-1"></i>
                                Tip: Sertakan justifikasi berdasarkan match status, tenure, dan analisis AI
                              </div>
                            </div>

                            <div className="text-center mt-4">
                              <button 
                                type="button" 
                                className="btn btn-success btn-lg px-5"
                                onClick={submitRecommendation}
                                disabled={!recommendationData.recommendation_type || !recommendationData.reason}
                              >
                                <i className="fas fa-paper-plane me-2"></i>
                                Kirim Rekomendasi ke HR
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : employeeDetail?.error ? (
                  <div className="text-center py-5">
                    <div className="alert alert-danger">
                      <h5>âŒ Error Loading Data</h5>
                      <p>{employeeDetail.errorMessage}</p>
                      <p><strong>Employee:</strong> {employeeDetail.employee?.name}</p>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-5">
                    <div className="alert alert-warning">
                      <h5>âš ï¸ Data tidak tersedia</h5>
                      <p>Gagal memuat data karyawan untuk membuat rekomendasi.</p>
                      <p>Silakan tutup modal dan coba lagi.</p>
                    </div>
                  </div>
                )}
              </div>
              <div className="modal-footer py-2 bg-light" style={{ minHeight: '60px' }}>
                <button 
                  type="button" 
                  className="btn btn-secondary px-4"
                  onClick={() => {
                    setShowRecommendationModal(false);
                    setEmployeeDetail(null);
                    setLoadingDetail(false);
                  }}
                >
                  <i className="fas fa-times me-2"></i>
                  Tutup
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Container>
  );
};

export default ManagerDashboard;
