import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import { useAuth } from '../App';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    if (user.role === 'hr') {
      fetchEmployees();
    } else {
      fetchDashboardData();
    }
  }, [user.role]);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/employees.php', {
        withCredentials: true
      });
      if (response.data.success) {
        setEmployees(response.data.data);
      } else {
        setError('Gagal memuat data karyawan');
      }
    } catch (error) {
      setError('Terjadi kesalahan saat memuat data');
      console.error('Employees error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/employees.php?action=dashboard');
      
      if (response.data.success) {
        setDashboardData(response.data.data);
      } else {
        setError('Gagal memuat data dashboard');
      }
    } catch (error) {
      setError('Terjadi kesalahan saat memuat data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  const getContractChartData = () => {
    if (!dashboardData?.contract_distribution) return null;

    const labels = dashboardData.contract_distribution.map(item => {
      switch (item.type) {
        case 'probation': return 'Probation';
        case '1': return 'Kontrak 1';
        case '2': return 'Kontrak 2';
        case '3': return 'Kontrak 3';
        case 'permanent': return 'Permanent';
        default: return item.type;
      }
    });

    const data = dashboardData.contract_distribution.map(item => item.count);

    return {
      labels,
      datasets: [
        {
          label: 'Jumlah Karyawan',
          data,
          backgroundColor: [
            '#ffc107',  // probation - warning
            '#007bff',  // contract 1 - primary
            '#28a745',  // contract 2 - success
            '#6c757d',  // contract 3 - secondary
            '#17a2b8',  // permanent - info
          ],
          borderColor: [
            '#ffc107',
            '#007bff',
            '#28a745',
            '#6c757d',
            '#17a2b8',
          ],
          borderWidth: 1,
        },
      ],
    };
  };

  const getEducationChartData = () => {
    if (!dashboardData?.education_distribution) return null;

    const labels = dashboardData.education_distribution.map(item => item.education_level);
    const data = dashboardData.education_distribution.map(item => item.count);

    return {
      labels,
      datasets: [
        {
          data,
          backgroundColor: [
            '#FF6384',
            '#36A2EB',
            '#FFCE56',
            '#4BC0C0',
          ],
          hoverBackgroundColor: [
            '#FF6384',
            '#36A2EB',
            '#FFCE56',
            '#4BC0C0',
          ],
        },
      ],
    };
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Distribusi Kontrak Karyawan',
      },
    },
  };

  const doughnutOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
      },
      title: {
        display: true,
        text: 'Distribusi Pendidikan',
      },
    },
  };

  // Helper functions for employee table
  const getStatusBadge = (status) => {
    switch (status) {
      case 'active':
        return <span className="badge bg-success">Active</span>;
      case 'probation':
        return <span className="badge bg-warning">Probation</span>;
      case 'resigned':
        return <span className="badge bg-danger">Resigned</span>;
      case 'terminated':
        return <span className="badge bg-danger">Resigned</span>;
      default:
        return <span className="badge bg-success">Active</span>;
    }
  };

  const formatDate = (dateString) => {
    if (!dateString || dateString === '0000-00-00') return '-';
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const getEID = (id) => {
    return `EID${id.toString().padStart(4, '0')}`;
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
          <div className="text-center">
            <div className="spinner-border text-primary mb-3" style={{ width: '3rem', height: '3rem' }} role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="text-muted">Memuat dashboard...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container-fluid py-4">
        <div className="alert alert-danger" role="alert">
          <i className="fas fa-exclamation-triangle me-2"></i>
          {error}
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Welcome Section */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="dashboard-card">
            <div className="d-flex align-items-center">
              <div className="me-3">
                <i className="fas fa-chart-line fa-3x text-primary"></i>
              </div>
              <div>
                <h2 className="mb-1">Selamat Datang, {user.role.toUpperCase()}!</h2>
                <p className="text-muted mb-0">
                  Dashboard Sistem Rekomendasi Kontrak Karyawan
                </p>
                <small className="text-muted">
                  Role: <span className="badge bg-primary">{user.role.toUpperCase()}</span>
                  {user.division_name && (
                    <> | Divisi: <span className="badge bg-secondary ms-1">{user.division_name}</span></>
                  )}
                </small>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* HR: Employee Table */}
      {user.role === 'hr' ? (
        <div className="row">
          <div className="col-12">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <div>
                <h2 className="mb-0">Data Karyawan</h2>
                <p className="text-muted mb-0">Total Talents {employees.length}</p>
              </div>
              <button className="btn btn-primary">
                <i className="fas fa-plus"></i> Tambah Karyawan
              </button>
            </div>

            {/* Employee Table */}
            <div className="card">
              <div className="card-body">
                <div className="table-responsive">
                  <table className="table table-hover">
                    <thead className="table-light">
                      <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>EID</th>
                        <th>Status</th>
                        <th>Department</th>
                        <th>Role</th>
                        <th>Education</th>
                        <th>Major</th>
                        <th>Join Date</th>
                        <th>Resign Date</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {employees.map((employee, index) => (
                        <tr key={employee.eid}>
                          <td>{index + 1}</td>
                          <td>
                            <div className="d-flex align-items-center">
                              <img 
                                src={`https://ui-avatars.com/api/?name=${employee.name}&background=random&size=32`}
                                alt={employee.name}
                                className="rounded-circle me-2"
                                width="32"
                                height="32"
                              />
                              {employee.name}
                            </div>
                          </td>
                          <td>
                            <span className="text-primary">{getEID(employee.eid)}</span>
                          </td>
                          <td>{getStatusBadge(employee.status)}</td>
                          <td>{employee.division_name || 'IT'}</td>
                          <td>{employee.role || 'Programmer'}</td>
                          <td>{employee.education_level || 'S1'}</td>
                          <td>{employee.major || 'IT'}</td>
                          <td>{formatDate(employee.join_date) || '2020-05-20'}</td>
                          <td>{employee.status === 'resigned' ? formatDate(employee.resign_date) || '2023-07-31' : '-'}</td>
                          <td>
                            <Link 
                              to={`/employees/${employee.eid}`}
                              className="btn btn-sm btn-outline-primary"
                            >
                              View Contract
                            </Link>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <>
          {/* Statistics Cards */}
          <div className="row mb-4">
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card primary">
                  <div className="stat-number">{dashboardData?.total_employees || 0}</div>
                  <div className="stat-label">Total Karyawan</div>
                </div>
              </div>
            </div>
            
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card success">
                  <div className="stat-number">{dashboardData?.active_employees || 0}</div>
                  <div className="stat-label">Karyawan Aktif</div>
                </div>
              </div>
            </div>
            
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card danger">
                  <div className="stat-number">{dashboardData?.resigned_employees || 0}</div>
                  <div className="stat-label">Karyawan Resign</div>
                </div>
              </div>
            </div>
            
            <div className="col-xl-3 col-md-6 mb-3">
              <div className="dashboard-card">
                <div className="stat-card warning">
                  <div className="stat-number">
                    {dashboardData?.total_employees > 0 
                      ? Math.round((dashboardData.active_employees / dashboardData.total_employees) * 100)
                      : 0}%
                  </div>
                  <div className="stat-label">Tingkat Retensi</div>
                </div>
              </div>
            </div>
          </div>

          {/* Charts Row */}
          <div className="row mb-4">
            <div className="col-lg-8 mb-4">
              <div className="dashboard-card">
                <div className="chart-container">
                  {getContractChartData() ? (
                    <Bar data={getContractChartData()} options={chartOptions} />
                  ) : (
                    <div className="d-flex justify-content-center align-items-center h-100">
                      <p className="text-muted">Tidak ada data kontrak</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
            
            <div className="col-lg-4 mb-4">
              <div className="dashboard-card">
                <div className="chart-container">
                  {getEducationChartData() ? (
                    <Doughnut data={getEducationChartData()} options={doughnutOptions} />
                  ) : (
                    <div className="d-flex justify-content-center align-items-center h-100">
                      <p className="text-muted">Tidak ada data pendidikan</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Division Distribution (Only for Admin/HR) */}
          {(user.role === 'admin' || user.role === 'hr') && dashboardData?.division_distribution && (
            <div className="row mb-4">
              <div className="col-12">
                <div className="dashboard-card">
                  <h4 className="mb-3">
                    <i className="fas fa-building me-2"></i>
                    Distribusi Divisi
                  </h4>
                  <div className="row">
                    {dashboardData.division_distribution.map((division, index) => (
                      <div key={index} className="col-md-3 mb-3">
                        <div className="card border-0 bg-light">
                          <div className="card-body text-center">
                            <h5 className="card-title text-primary">{division.count}</h5>
                            <p className="card-text text-muted small">{division.division_name}</p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Quick Actions */}
          <div className="row">
            <div className="col-12">
              <div className="dashboard-card">
                <h4 className="mb-3">
                  <i className="fas fa-bolt me-2"></i>
                  Aksi Cepat
                </h4>
                <div className="row">
                  {(user.role === 'hr' || user.role === 'admin') && (
                    <div className="col-md-4 mb-3">
                      <div className="card border-primary">
                        <div className="card-body text-center">
                          <i className="fas fa-user-plus fa-2x text-primary mb-3"></i>
                          <h6 className="card-title">Tambah Karyawan</h6>
                          <p className="card-text small text-muted">
                            Daftarkan karyawan baru ke sistem
                          </p>
                          <a href="/employees" className="btn btn-primary btn-sm">
                            <i className="fas fa-plus me-1"></i>
                            Tambah
                          </a>
                        </div>
                      </div>
                    </div>
                  )}
                  
                  <div className="col-md-4 mb-3">
                    <div className="card border-success">
                      <div className="card-body text-center">
                        <i className="fas fa-chart-bar fa-2x text-success mb-3"></i>
                        <h6 className="card-title">Lihat Rekomendasi</h6>
                        <p className="card-text small text-muted">
                          Cek rekomendasi kontrak sistem
                        </p>
                        <a href="/employees" className="btn btn-success btn-sm">
                          <i className="fas fa-eye me-1"></i>
                          Lihat
                        </a>
                      </div>
                    </div>
                  </div>
                  
                  <div className="col-md-4 mb-3">
                    <div className="card border-info">
                      <div className="card-body text-center">
                        <i className="fas fa-file-contract fa-2x text-info mb-3"></i>
                        <h6 className="card-title">Kelola Kontrak</h6>
                        <p className="card-text small text-muted">
                          Atur kontrak karyawan
                        </p>
                        <a href="/contracts" className="btn btn-info btn-sm">
                          <i className="fas fa-cog me-1"></i>
                          Kelola
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
