import React, { useState } from 'react';
import { useAuth } from '../App';

const Login = () => {
    const { login } = useAuth();
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [activeTab, setActiveTab] = useState('HR');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        const result = await login(formData.username, formData.password);
        
        if (!result.success) {
            setError(result.error);
        }
        
        setLoading(false);
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    return (
        <div className="login-container">
            <div className="container-fluid vh-100">
                <div className="row h-100">
                    <div className="col-12 d-flex justify-content-center align-items-center">
                        <div className="login-card-new">
                            <div className="text-center mb-4">
                                <h2 className="login-title">Masuk Akun</h2>
                            </div>

                            {/* Role Tabs */}
                            <div className="role-tabs mb-4">
                                <div className="tab-buttons">
                                    {['HR', 'Management', 'Admin'].map((role) => (
                                        <button
                                            key={role}
                                            className={`tab-button ${activeTab === role ? 'active' : ''}`}
                                            onClick={() => setActiveTab(role)}
                                        >
                                            {role}
                                        </button>
                                    ))}
                                </div>
                            </div>

                            {error && (
                                <div className="alert alert-danger" role="alert">
                                    {error}
                                </div>
                            )}

                            <form onSubmit={handleSubmit}>
                                <div className="form-group-new mb-3">
                                    <input
                                        type="text"
                                        className="form-control-new"
                                        name="username"
                                        placeholder={`Alamat Email ${activeTab}`}
                                        value={formData.username}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>

                                <div className="form-group-new mb-4">
                                    <input
                                        type="password"
                                        className="form-control-new"
                                        name="password"
                                        placeholder="Kata Sandi"
                                        value={formData.password}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>

                                <button
                                    type="submit"
                                    className="btn-login-new w-100"
                                    disabled={loading}
                                >
                                    {loading ? (
                                        <span>
                                            <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                                            Masuk...
                                        </span>
                                    ) : (
                                        'Masuk'
                                    )}
                                </button>
                            </form>

                            <div className="text-center mt-4">
                                <p className="login-footer">
                                    Â©2025 CareerMatch. Semua hak cipta dilindungi.
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
